package software.uncharted.elasticsearch;

import static software.uncharted.elasticsearch.TemplateVersion.CURRENT_TEMPLATE_VERSION;

import co.elastic.clients.elasticsearch._types.ElasticsearchException;
import co.elastic.clients.elasticsearch._types.query_dsl.Query;
import co.elastic.clients.elasticsearch._types.query_dsl.QueryBuilders;
import co.elastic.clients.elasticsearch.core.SearchRequest;
import co.elastic.clients.elasticsearch.core.SearchResponse;
import co.elastic.clients.elasticsearch.core.search.Hit;
import co.elastic.clients.elasticsearch.indices.GetIndexResponse;
import co.elastic.clients.elasticsearch.indices.stats.IndexMetadataState;
import com.fasterxml.jackson.core.JsonProcessingException;
import com.fasterxml.jackson.databind.JsonNode;
import com.fasterxml.jackson.databind.ObjectMapper;
import com.fasterxml.jackson.databind.node.ArrayNode;
import com.fasterxml.jackson.databind.node.ObjectNode;
import jakarta.annotation.PostConstruct;
import jakarta.annotation.PreDestroy;
import java.io.ByteArrayInputStream;
import java.io.IOException;
import java.util.ArrayList;
import java.util.List;
import java.util.Set;
import java.util.UUID;
import org.junit.jupiter.api.AfterEach;
import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.BeforeEach;
import org.junit.jupiter.api.Test;
import org.springframework.beans.factory.annotation.Autowired;

public class ElasticsearchServiceTests extends ElasticsearchApplicationTests {

  @Autowired
  private TemplateInitializationService templateInitializationService;

  @Autowired
  private ElasticsearchPipelineInitializationService pipelineInitializationService;

  @Autowired
  private GeoShapeService geoShapeService;

  @Autowired
  private GeocodingService geocodingService;

  @Autowired
  private ManagedElasticsearchService elasticsearchService;

  @Autowired
  private ObjectMapper objectMapper;

  private final String INDEX_SUFFIX = "-atlantis-" + CURRENT_TEMPLATE_VERSION;

  @PostConstruct
  void init() throws IOException {
    elasticsearchService.addIndexToIgnore(geoShapeService.getIndex());
    elasticsearchService.addIndexToIgnore(geocodingService.getIndex());
    setup();
    geoShapeService.init();
    geocodingService.init();
  }

  @PreDestroy
  void destroy() {
    elasticsearchService.removeIndexFromIgnore(geoShapeService.getIndex());
    elasticsearchService.removeIndexFromIgnore(geocodingService.getIndex());
    elasticsearchService.cleanup();
  }

  @BeforeEach
  void setup() throws IOException {
    templateInitializationService.init();
    pipelineInitializationService.init();
  }

  @AfterEach
  void teardown() {
    elasticsearchService.cleanup();
  }

  String randomIndexName() {
    return UUID.randomUUID() + INDEX_SUFFIX;
  }

  @Test
  void testItCanCreateAnIndex() {
    final String name = randomIndexName();
    elasticsearchService.createIndex(name);
    Assertions.assertTrue(elasticsearchService.hasIndex(name));
  }

  @Test
  void testItCanCreateAnIndexWithReplicasAndShards() throws IOException {
    final String name = randomIndexName();
    final int replicas = 2;
    final int shards = 3;
    elasticsearchService.createIndex(name, replicas, shards);
    Assertions.assertTrue(elasticsearchService.hasIndex(name));

    final GetIndexResponse response = elasticsearchService
      .getClient()
      .indices()
      .get(b -> b.index(name));
    Assertions.assertEquals(
      shards + "",
      response.get(name).settings().index().numberOfShards()
    );
    Assertions.assertEquals(
      replicas + "",
      response.get(name).settings().index().numberOfReplicas()
    );
  }

  @Test
  void testItCanDeleteAnIndex() {
    final String name = randomIndexName();
    elasticsearchService.createIndex(name);
    Assertions.assertTrue(elasticsearchService.hasIndex(name));
    elasticsearchService.deleteIndex(name);
    Assertions.assertFalse(elasticsearchService.hasIndex(name));
  }

  @Test
  void testItCanCloseAnIndex() {
    final String name = randomIndexName();
    elasticsearchService.createIndex(name);
    Assertions.assertSame(
      elasticsearchService.getIndicesStats(name).indices().get(name).status(),
      IndexMetadataState.Open
    );
    elasticsearchService.closeIndex(name);
    Assertions.assertSame(
      elasticsearchService.getIndicesStats(name).indices().get(name).status(),
      IndexMetadataState.Close
    );
  }

  @Test
  void testItCanOpenAnIndex() {
    final String name = randomIndexName();
    elasticsearchService.createIndex(name);
    elasticsearchService.closeIndex(name);
    Assertions.assertSame(
      elasticsearchService.getIndicesStats(name).indices().get(name).status(),
      IndexMetadataState.Close
    );
    elasticsearchService.openIndex(name);
    Assertions.assertSame(
      elasticsearchService.getIndicesStats(name).indices().get(name).status(),
      IndexMetadataState.Open
    );
  }

  @Test
  void testItCanInsertData() throws IOException {
    final String name = randomIndexName();
    elasticsearchService.createIndex(name);
    Assertions.assertTrue(elasticsearchService.hasIndex(name));

    final List<JsonNode> data = List.of(
      objectMapper.readTree("{\"testField\": \"object1\"}"),
      objectMapper.readTree("{\"testField\": \"object2\"}")
    );
    elasticsearchService.insert(name, data);
    elasticsearchService.refresh(name);
    Assertions.assertEquals(2, (long) elasticsearchService.count(name));
  }

  @Test
  void testItCanInsertDataWithIds() throws IOException {
    final String name = randomIndexName();
    elasticsearchService.createIndex(name);
    Assertions.assertTrue(elasticsearchService.hasIndex(name));

    final List<JsonNode> data = List.of(
      objectMapper.readTree("{\"testField\": \"object1\"}"),
      objectMapper.readTree("{\"testField\": \"object2\"}")
    );
    final List<String> ids = List.of("1", "2");
    elasticsearchService.insert(name, data, ids);
    elasticsearchService.refresh(name);

    final SearchResponse<JsonNode> response = elasticsearchService.search(
      name,
      Query.of(b -> b.matchAll(m -> m)),
      JsonNode.class
    );
    Assertions.assertEquals(2L, response.hits().total().value());
    Assertions.assertTrue(
      response.hits().hits().stream().allMatch(hit -> ids.contains(hit.id()))
    );
  }

  @Test
  void testItOverwritesDataWithProvidedIds() throws IOException {
    final String name = randomIndexName();
    elasticsearchService.createIndex(name);
    Assertions.assertTrue(elasticsearchService.hasIndex(name));

    final List<JsonNode> data = List.of(
      objectMapper.readTree("{\"testField\": \"object1\"}"),
      objectMapper.readTree("{\"testField\": \"object2\"}")
    );
    final List<String> ids = List.of("1", "2");
    elasticsearchService.insert(name, data, ids);
    elasticsearchService.refresh(name);

    // Reinsert the data and ensure the documents are overwritten, implying that the ids are used
    elasticsearchService.insert(name, data, ids);
    elasticsearchService.refresh(name);

    final SearchResponse<JsonNode> response = elasticsearchService.search(
      name,
      Query.of(b -> b.matchAll(m -> m)),
      JsonNode.class
    );
    Assertions.assertEquals(2L, response.hits().total().value());
    Assertions.assertTrue(
      response.hits().hits().stream().allMatch(hit -> ids.contains(hit.id()))
    );
  }

  @Test
  void testItCanInsertDataAndPipelineAddsProperty() throws IOException {
    final String name = randomIndexName();
    elasticsearchService.createIndex(name);
    Assertions.assertTrue(elasticsearchService.hasIndex(name));

    final List<JsonNode> data = List.of(
      objectMapper.createObjectNode().put("testField", "object1"),
      objectMapper.createObjectNode().put("testField", "object2")
    );
    elasticsearchService.insert(name, data);
    elasticsearchService.refresh(name);
    final SearchResponse<JsonNode> response = elasticsearchService.search(
      name,
      Query.of(b -> b.matchAll(m -> m)),
      JsonNode.class
    );
    Assertions.assertEquals(2L, response.hits().total().value());

    Assertions.assertTrue(
      response
        .hits()
        .hits()
        .stream()
        .allMatch(hit ->
          hit.source().at("/analyzed_feature/full_text_search_data").isArray()
        )
    );
  }

  @Test
  void testItThrowsWhenIdsLengthAndDataLengthDoNotMatch()
    throws JsonProcessingException {
    final String name = randomIndexName();
    elasticsearchService.createIndex(name);
    Assertions.assertTrue(elasticsearchService.hasIndex(name));

    final List<JsonNode> data = List.of(
      objectMapper.readTree("{\"testField\": \"object1\"}"),
      objectMapper.readTree("{\"testField\": \"object2\"}")
    );
    final List<String> ids = List.of("1");
    Assertions.assertThrows(IllegalArgumentException.class, () ->
      elasticsearchService.insert(name, data, ids)
    );
  }

  private String buildScrollTestIndex() throws IOException {
    final String name = randomIndexName();
    elasticsearchService.createIndex(name);
    Assertions.assertTrue(elasticsearchService.hasIndex(name));

    final List<JsonNode> data = new ArrayList<>();
    for (int i = 0; i < 100; i++) {
      data.add(
        objectMapper.readTree(
          """
          {
            "keyword_feature": {
              "foo": "bar%d"
            }
          }
          """.formatted(i)
        )
      );
    }

    elasticsearchService.insert(name, data);
    elasticsearchService.refresh(name);
    return name;
  }

  private String buildScrollTestIndex2() throws IOException {
    final String name = randomIndexName();
    elasticsearchService.createIndex(name);
    Assertions.assertTrue(elasticsearchService.hasIndex(name));

    final List<JsonNode> data = new ArrayList<>();
    for (int i = 0; i < 100; i++) {
      data.add(
        objectMapper.readTree(
          """
          {
            "keyword_feature": {
              "foo": "bar%d"
            },
            "double_feature": {
              "foo": %f
            }
          }
          """.formatted(i, (float) (i % 2))
        )
      );
    }

    elasticsearchService.insert(name, data);
    elasticsearchService.refresh(name);
    return name;
  }

  private String buildScrollTestIndex3() throws IOException {
    final String name = randomIndexName();
    elasticsearchService.createIndex(name);
    Assertions.assertTrue(elasticsearchService.hasIndex(name));

    final List<JsonNode> data = new ArrayList<>();
    for (int i = 0; i < 100; i++) {
      data.add(
        objectMapper.readTree(
          """
          {
            "keyword_feature": {
              "foo": "bar%d"
            }
          }
          """.formatted(i % 2)
        )
      );
    }

    elasticsearchService.insert(name, data);
    elasticsearchService.refresh(name);
    return name;
  }

  @Test
  void testItThrowsIllegalArgumentWhenScrollRequestDoesntProvideASort() {
    final SearchRequest request = SearchRequest.of(b ->
      b.index("test").size(10).query(QueryBuilders.matchAll(m -> m))
    );
    Assertions.assertThrows(IllegalArgumentException.class, () ->
      elasticsearchService.scroll(request, JsonNode.class, data -> {})
    );
  }

  @Test
  void testItCanScrollDocumentsInPages() throws IOException {
    final String name = buildScrollTestIndex();

    final SearchRequest request = SearchRequest.of(b ->
      b
        .index(name)
        .size(10)
        .sort(s -> s.field(f -> f.field("keyword_feature.foo")))
        .query(QueryBuilders.matchAll(m -> m))
    );
    final List<Hit<JsonNode>> scrolledData = new ArrayList<>();
    elasticsearchService.scroll(request, JsonNode.class, scrolledData::addAll);

    Assertions.assertEquals(100, scrolledData.size());
  }

  @Test
  void testItCanGetDocumentsInASinglePage() throws IOException {
    final String name = buildScrollTestIndex();

    final SearchRequest request = SearchRequest.of(b ->
      b
        .index(name)
        .size(200)
        .sort(s -> s.field(f -> f.field("keyword_feature.foo")))
        .query(QueryBuilders.matchAll(m -> m))
    );
    final List<Hit<JsonNode>> scrolledData = new ArrayList<>();
    elasticsearchService.scroll(request, JsonNode.class, scrolledData::addAll);

    Assertions.assertEquals(100, scrolledData.size());
  }

  @Test
  void testItThrowsWhenSortFieldIsInvalid() throws IOException {
    final String name = buildScrollTestIndex();

    final SearchRequest request = SearchRequest.of(b ->
      b
        .index(name)
        .size(10)
        .sort(s -> s.field(f -> f.field("keyword_feature.bar")))
        .query(QueryBuilders.matchAll(m -> m))
    );
    Assertions.assertThrows(ElasticsearchException.class, () ->
      elasticsearchService.scroll(request, JsonNode.class, data -> {})
    );
  }

  @Test
  void testItScrollsWhenNoSizeIsProvided() throws IOException {
    final String name = buildScrollTestIndex();

    final SearchRequest request = SearchRequest.of(b ->
      b
        .index(name)
        .sort(s -> s.field(f -> f.field("keyword_feature.foo")))
        .query(QueryBuilders.matchAll(m -> m))
    );
    final List<Hit<JsonNode>> scrolledData = new ArrayList<>();
    elasticsearchService.scroll(request, JsonNode.class, scrolledData::addAll);

    Assertions.assertEquals(100, scrolledData.size());
  }

  @Test
  void testItCanScrollWhenSortingOnANonUniqueNumericalField()
    throws IOException {
    final String name = buildScrollTestIndex2();

    final SearchRequest request = SearchRequest.of(b ->
      b
        .index(name)
        .size(10)
        .sort(s -> s.field(f -> f.field("double_feature.foo")))
        .query(QueryBuilders.matchAll(m -> m))
    );
    final List<Hit<JsonNode>> scrolledData = new ArrayList<>();
    elasticsearchService.scroll(request, JsonNode.class, scrolledData::addAll);

    Assertions.assertEquals(100, scrolledData.size());
  }

  @Test
  void testItCanScrollWhenSortingOnANonUniqueStringField() throws IOException {
    final String name = buildScrollTestIndex3();

    final SearchRequest request = SearchRequest.of(b ->
      b
        .index(name)
        .size(10)
        .sort(s -> s.field(f -> f.field("keyword_feature.foo")))
        .query(QueryBuilders.matchAll(m -> m))
    );
    final List<Hit<JsonNode>> scrolledData = new ArrayList<>();
    elasticsearchService.scroll(request, JsonNode.class, scrolledData::addAll);

    Assertions.assertEquals(100, scrolledData.size());
  }

  @Test
  void testItCanCreateAnEnrichPolicy() throws IOException {
    final String indexName = randomIndexName();
    elasticsearchService.createIndex(indexName);
    final List<JsonNode> data = List.of(
      objectMapper.readTree(
        "{\"keyword_feature\": {\"id\": \"1\", \"name\": \"object1\", \"description\": \"description1\"}}"
      ),
      objectMapper.readTree(
        "{\"keyword_feature\": {\"id\": \"2\", \"name\": \"object2\", \"description\": \"description2\"}}"
      )
    );
    elasticsearchService.insert(indexName, data);
    elasticsearchService.refresh(indexName);

    final String name = elasticsearchService.createEnrichPolicy(
      indexName,
      "keyword_feature.id",
      List.of("keyword_feature.name", "keyword_feature.description")
    );
    Assertions.assertNotNull(name);
    Assertions.assertTrue(elasticsearchService.enrichPolicyExists(name));
  }

  @Test
  void testItFailsWhenSourceIndexDoesNotExist() throws IOException {
    final String indexName = randomIndexName();
    elasticsearchService.createIndex(indexName);
    final List<JsonNode> data = List.of(
      objectMapper.readTree(
        "{\"keyword_feature\": {\"id\": \"1\", \"name\": \"object1\", \"description\": \"description1\"}}"
      ),
      objectMapper.readTree(
        "{\"keyword_feature\": {\"id\": \"2\", \"name\": \"object2\", \"description\": \"description2\"}}"
      )
    );
    elasticsearchService.insert(indexName, data);
    elasticsearchService.refresh(indexName);

    Assertions.assertNull(
      elasticsearchService.createEnrichPolicy(
        indexName,
        "non_existent_field",
        List.of("keyword_feature.name", "keyword_feature.description")
      )
    );
  }

  @Test
  void testItThrowsCreatingEnrichPolicyWhenIndexNotFound() {
    final String indexName = randomIndexName();
    Assertions.assertFalse(elasticsearchService.hasIndex(indexName));
    Assertions.assertThrows(ElasticsearchException.class, () ->
      elasticsearchService.createEnrichPolicy(
        indexName,
        "source-field",
        List.of("included-source-field1", "included-source-field2")
      )
    );
  }

  @Test
  void testItThrowsCreatingEnrichPolicyWhenNoMapping() {
    final String indexName = UUID.randomUUID().toString(); // intentionally not using randomIndexName to ensure a template is not applied
    elasticsearchService.createIndex(indexName);
    Assertions.assertThrows(ElasticsearchException.class, () ->
      elasticsearchService.createEnrichPolicy(
        indexName,
        "source-field",
        List.of("included-source-field1", "included-source-field2")
      )
    );
  }

  @Test
  void testItDoesntRecreateTheSamePolicy() throws IOException {
    final String indexName = randomIndexName();
    elasticsearchService.createIndex(indexName);
    final List<JsonNode> data = List.of(
      objectMapper.readTree(
        "{\"keyword_feature\": {\"id\": \"1\", \"name\": \"object1\", \"description\": \"description1\"}}"
      ),
      objectMapper.readTree(
        "{\"keyword_feature\": {\"id\": \"2\", \"name\": \"object2\", \"description\": \"description2\"}}"
      )
    );
    elasticsearchService.insert(indexName, data);
    elasticsearchService.refresh(indexName);

    final int countBefore = elasticsearchService
      .getEnrichPolicies()
      .policies()
      .size();
    final String name1 = elasticsearchService.createEnrichPolicy(
      indexName,
      "keyword_feature.id",
      List.of("keyword_feature.name", "keyword_feature.description")
    );
    final String name2 = elasticsearchService.createEnrichPolicy(
      indexName,
      "keyword_feature.id",
      List.of("keyword_feature.name", "keyword_feature.description")
    );
    final int countAfter = elasticsearchService
      .getEnrichPolicies()
      .policies()
      .size();
    Assertions.assertEquals(countBefore + 1, countAfter);
    Assertions.assertEquals(name1, name2);
  }

  @Test
  void testOrderingOfFieldsDoesNotAffectPolicyCreation() throws IOException {
    final String indexName = randomIndexName();
    elasticsearchService.createIndex(indexName);
    final List<JsonNode> data = List.of(
      objectMapper.readTree(
        "{\"keyword_feature\": {\"id\": \"1\", \"name\": \"object1\", \"description\": \"description1\"}}"
      ),
      objectMapper.readTree(
        "{\"keyword_feature\": {\"id\": \"2\", \"name\": \"object2\", \"description\": \"description2\"}}"
      )
    );
    elasticsearchService.insert(indexName, data);
    elasticsearchService.refresh(indexName);

    final String name1 = elasticsearchService.createEnrichPolicy(
      indexName,
      "keyword_feature.id",
      List.of("keyword_feature.description", "keyword_feature.name")
    );
    final String name2 = elasticsearchService.createEnrichPolicy(
      indexName,
      "keyword_feature.id",
      List.of("keyword_feature.name", "keyword_feature.description")
    );
    Assertions.assertEquals(name1, name2);
  }

  @Test
  void testItCanDeleteAnEnrichPolicy() throws IOException {
    final String indexName = randomIndexName();
    elasticsearchService.createIndex(indexName);
    final List<JsonNode> data = List.of(
      objectMapper.readTree(
        "{\"keyword_feature\": {\"id\": \"1\", \"name\": \"object1\", \"description\": \"description1\"}}"
