package software.uncharted.pantera.service.search;

import com.fasterxml.jackson.databind.JsonNode;
import com.fasterxml.jackson.databind.ObjectMapper;
import com.fasterxml.jackson.databind.node.ObjectNode;
import java.util.ArrayList;
import java.util.Arrays;
import java.util.Collection;
import java.util.HashMap;
import java.util.List;
import java.util.Map;
import java.util.Objects;
import lombok.RequiredArgsConstructor;
import lombok.extern.slf4j.Slf4j;
import org.springframework.stereotype.Service;
import software.uncharted.pantera.configuration.Config;
import software.uncharted.pantera.model.provenance.Provenance;
import software.uncharted.pantera.model.provenance.ProvenanceType;
import software.uncharted.pantera.model.search.DefaultProperty;
import software.uncharted.pantera.model.search.LatLng;
import software.uncharted.pantera.model.search.Location;
import software.uncharted.pantera.model.search.PanteraDocument;
import software.uncharted.pantera.model.search.Property;
import software.uncharted.pantera.model.search.PropertyDescriptor;
import software.uncharted.pantera.service.ProjectImageService;
import software.uncharted.pipeline.common.features.PropertyTag;
import software.uncharted.pipeline.util.ImageServerUtil;

@Service
@RequiredArgsConstructor
@Slf4j
public class DocumentParsingService {

  private final Config config;

  private final ProjectImageService projectImageService;

  /**
   * Parse the search response into a document
   *
   * @param id                  The document id
   * @param hitSource           The search hit source (_source)
   * @param propertyDescriptors The property descriptors
   * @return The document
   */
  public PanteraDocument parse(
    final String id,
    final Double score,
    final JsonNode hitSource,
    final List<PropertyDescriptor> propertyDescriptors
  ) {
    final List<PropertyDescriptor> locationPropertyDescriptors =
      propertyDescriptors
        .stream()
        .filter(
          pd ->
            pd.containsTag(PropertyTag.LOCATION) &&
            !pd.containsTag(PropertyTag.IMAGE)
        )
        .toList();
    final Map<String, List<Location>> locations = new HashMap<>();
    locationPropertyDescriptors.forEach(pd -> {
      try {
        final String jacksonPtr = pd.getJacksonMapping();
        final JsonNode locationsNode = hitSource.at(jacksonPtr);
        if (locationsNode != null) {
          locationsNode
            .elements()
            .forEachRemaining(locationNode -> {
              final JsonNode location = locationNode.at("/location");
              final JsonNode label = locationNode.at("/label");
              final Location loc = new Location()
                .setLocation(
                  new LatLng()
                    .setLat(location.at("/lat").asDouble())
                    .setLng(location.at("/lon").asDouble())
                )
                .setLabel(label.asText());
              final List<Location> locs = locations.getOrDefault(
                pd.getKey(),
                new ArrayList<>()
              );
              locs.add(loc);
              locations.put(pd.getKey(), locs);
            });
        }
      } catch (final Exception e) {
        log.warn(
          "Error parsing location property {} with mapping {}",
          pd.getKey(),
          pd.getJacksonMapping(),
          e
        );
      }
    });

    final List<PropertyDescriptor> locatedImagePropertyDescriptors =
      propertyDescriptors
        .stream()
        .filter(
          pd ->
            pd.containsTag(PropertyTag.LOCATION) &&
            pd.containsTag(PropertyTag.IMAGE)
        )
        .toList();

    final List<PropertyDescriptor> nonLocationPropertyDescriptors =
      propertyDescriptors
        .stream()
        .filter(pd -> !pd.containsTag(PropertyTag.LOCATION))
        .toList();
    final List<PropertyDescriptor> otherPropertyDescriptors = new ArrayList<
      PropertyDescriptor
    >();
    otherPropertyDescriptors.addAll(locatedImagePropertyDescriptors);
    otherPropertyDescriptors.addAll(nonLocationPropertyDescriptors);

    final List<Property> properties = otherPropertyDescriptors
      .stream()
      .map(pd -> parseProperties(hitSource, pd))
      .filter(Objects::nonNull)
      .flatMap(Collection::stream)
      .toList();

    final ObjectMapper objectMapper = new ObjectMapper();
    final List<Provenance> provenances = new ArrayList<>();

    final List<Map<String, Object>> provList = objectMapper.convertValue(
      hitSource.at("/provenance_feature/provenance"),
      objectMapper
        .getTypeFactory()
        .constructCollectionType(List.class, Map.class)
    );

    if (provList != null) {
      for (Map<String, Object> provMap : provList) {
        long timestampMs = -1;
        String userId = null;
        String userName = null;
        String type = null;
        String source = null;
        if (provMap.get(Provenance.TIMESTAMP) instanceof List<?> timeStamps) {
          timestampMs = Long.parseLong(timeStamps.getFirst().toString());
        } else if (
          provMap.get(Provenance.TIMESTAMP) instanceof Long timeStamp
        ) {
          timestampMs = (long) timeStamp;
        }
        if (provMap.get(Provenance.USERID) instanceof List<?> userIds) {
          userId = userIds.getFirst().toString();
        } else if (provMap.get(Provenance.USERID) instanceof String userIdStr) {
          userId = userIdStr;
        }
        if (provMap.get(Provenance.USERNAME) instanceof List<?> userNames) {
          userName = userNames.getFirst().toString();
        } else if (
          provMap.get(Provenance.USERNAME) instanceof String userNameStr
        ) {
          userName = userNameStr;
        }
        if (provMap.get(Provenance.TYPE) instanceof List<?> types) {
          type = types.getFirst().toString();
        } else if (provMap.get(Provenance.TYPE) instanceof String typeStr) {
          type = typeStr;
        }
        if (provMap.get(Provenance.SOURCE) instanceof List<?> sources) {
          source = sources.getFirst().toString();
        } else if (provMap.get(Provenance.SOURCE) instanceof String sourceStr) {
          source = sourceStr;
        }
        String beforeStr = null;
        String afterStr = null;

        if (provMap.get(Provenance.BEFORE) instanceof List<?> befores) {
          beforeStr = befores.getFirst().toString();
        } else if (provMap.get(Provenance.BEFORE) instanceof String bStr) {
          beforeStr = bStr;
        }

        if (provMap.get(Provenance.AFTER) instanceof List<?> afters) {
          afterStr = afters.getFirst().toString();
        } else if (provMap.get(Provenance.AFTER) instanceof String aStr) {
          afterStr = aStr;
        }

        ObjectNode beforeNode = null;
        ObjectNode afterNode = null;
        try {
          beforeNode = (ObjectNode) objectMapper.readTree(beforeStr);
          afterNode = (ObjectNode) objectMapper.readTree(afterStr);
        } catch (Exception e) {
          log.warn("Error parsing before/after node: {}", beforeStr, e);
        }

        provenances.add(
          new Provenance()
            .setSource(source)
            .setUserId(userId)
            .setTimestampMS(timestampMs)
            .setUserName(userName)
            .setBefore(beforeNode)
            .setAfter(afterNode)
            .setType(ProvenanceType.valueOf(type))
        );
      }
    }

    return new PanteraDocument()
      .setProperties(properties)
      .setLocations(locations)
      .setProvenances(provenances)
      .setScore(score)
      .setId(id);
  }

  /**
   * Parses the search hit for the given property descriptor and returns the list of properties (values)
   *
   * @param hitSource The _source from the Elasticsearch hit instance
   * @param pd        The property descriptor to parse
   * @return The list of property values extracted from the hit
   */
  private List<Property> parseProperties(
    final JsonNode hitSource,
    final PropertyDescriptor pd
  ) {
    try {
      if (hitSource == null) {
        return new ArrayList<>();
      }
      final String jacksonPtr = pd.getJacksonMapping();
      final List<JsonNode> values = new ArrayList<>();
      final List<String> paths = Arrays.stream(jacksonPtr.split("/"))
        .map(s -> "/" + s)
        .filter(s -> !s.equals("/"))
        .toList();
      parse(hitSource, paths, values);

      final List<Property> properties = new ArrayList<>();
      values.forEach(value -> {
        //if property tag is IMAGE,
        //then set the value as the image url else add properties as normal
        if (pd.containsTag(PropertyTag.IMAGE)) {
          if (value.isTextual() || !value.isEmpty()) {
            final String projectId = pd.getProject().getId();
            final String imageURL;
            if (pd.containsTag(PropertyTag.GEOREFERENCING)) {
              imageURL = projectImageService.getGeolocatedImageURL(
                projectId,
                value.get("map_id").asText()
              );
            } else if (pd.containsTag(PropertyTag.LOCATION)) {
              imageURL = projectImageService.getGeolocatedImageURL(
                projectId,
                value.asText()
              );
            } else {
              imageURL = projectImageService.getImageURL(
                pd.getProject().getId(),
                value.asText()
              );
            }
            properties.add(
              new Property()
                .setKey(pd.getKey())
                .setValue(imageURL) //replace the value with the image url
                .setTags(pd.getTags())
            );
          }
        } else if (
          pd.getKey().equals(DefaultProperty.RESOURCE_LOCATION.getKey())
        ) {
          // Get the pantera-image-server URL for the filename of any PDF file
          // we encounter and store it in the filename_resource property.
          if (
            (value.isTextual() || !value.isEmpty()) &&
            value.asText().endsWith(".pdf")
          ) {
            final String pdfUrl = ImageServerUtil.getImageUrl(
              value.asText(),
              config.getImageServerUrl() + config.getImageServerPdfEndpoint(),
              config.getImageServerEncryptionKey()
            );
            properties.add(
              new Property()
                .setKey(DefaultProperty.FILENAME_RESOURCE.getKey())
                .setValue(
                  pdfUrl + (pdfUrl.contains("?") ? "&" : "?") + "profile=google"
                ) // replace the value with the PDF url
                .setTags(pd.getTags())
            );
          }
          properties.add(
            new Property()
              .setKey(pd.getKey())
              .setValue(value.asText())
              .setTags(pd.getTags())
          );
        } else if (
          !pd.getKey().equals(DefaultProperty.FILENAME_RESOURCE.getKey())
        ) {
          properties.add(
            new Property()
              .setKey(pd.getKey())
              .setValue(value.asText())
              .setTags(pd.getTags())
          );
        }
      });
      return properties;
    } catch (final Exception e) {
      log.warn(
        "Error parsing property {} with mapping {}",
        pd.getKey(),
        pd.getJacksonMapping(),
        e
      );
      return null;
    }
  }

  /**
   * Parses a json node and populate the values list with the values at the given paths. Any component of the nested
   * object can be an array and this will get all values.
   * <p>
   * For example, consider the object:
   * {
   * "foo": [
   * {
   * "bar": ["1", "2", "3"]
   * },
   * {
   * "bar": ["4", "5", "6"]
   * }
   * }
   * <p>
   * The mapping "/foo/bar" would produce the values ["1", "2", "3", "4", "5", "6"]
   *
   * @param node   The json node
   * @param paths  The subpaths into the json object
   * @param values The partially populated list of values
   */
  private void parse(
    final JsonNode node,
    final List<String> paths,
    final List<JsonNode> values
  ) {
    final JsonNode value = node.at(paths.getFirst());

    // Check if the node actually exists and isn't missing/null
    if (value.isMissingNode() || value.isNull()) {
      return; // Don't process missing or null nodes
    }

    if (paths.size() > 1) {
      if (value.isArray()) {
        for (final JsonNode element : value) {
          parse(element, paths.subList(1, paths.size()), values);
        }
        return;
      } else {
        parse(value, paths.subList(1, paths.size()), values);
        return;
      }
    }
    if (value.isArray()) {
      for (final JsonNode element : value) {
        values.add(element);
      }
    } else {
      values.add(value);
    }
  }
}

