import co.elastic.clients.elasticsearch.core.GetResponse;
import com.fasterxml.jackson.databind.JsonNode;
import jakarta.annotation.PostConstruct;
import java.util.Optional;
import java.util.UUID;
import lombok.RequiredArgsConstructor;
import lombok.extern.slf4j.Slf4j;
import org.springframework.beans.factory.annotation.Value;
import org.springframework.stereotype.Service;
import software.uncharted.elasticsearch.ElasticsearchService;
import software.uncharted.pantera.configuration.Config;
import software.uncharted.pantera.model.project.Project;
import software.uncharted.pantera.model.s3.S3Object;
import software.uncharted.pantera.model.search.DefaultProperty;
import software.uncharted.pantera.model.search.PanteraDocument;
import software.uncharted.pantera.model.search.Property;
import software.uncharted.pipeline.common.FileUpload;

@Service
@RequiredArgsConstructor
@Slf4j
public class DocumentService {

  private final ProjectDataService projectDataService;
  private final S3ClientService s3ClientService;
  private final Config config;
  private final ElasticsearchService elasticsearchService;
  private final ProjectService projectService;

  @Value("${pantera.document-s3-bucket-name:pantera-file-storage}")
  private String DOCUMENT_BUCKET_NAME;

  @Value("${pantera.document-s3-client-name:default}")
  private String DOCUMENT_S3_CLIENT_NAME;

  @PostConstruct
  void init() {
    // Create the document bucket if it doesn't exist
    final S3Service s3Service = s3ClientService.getS3Service(
      DOCUMENT_S3_CLIENT_NAME
    );
    if (!s3Service.bucketExists(DOCUMENT_BUCKET_NAME)) {
      s3Service.createBucket(DOCUMENT_BUCKET_NAME);
    }

    if (!s3Service.bucketExists(config.getMediaBucket())) {
      s3Service.createBucket(config.getMediaBucket());
    }
  }

  public String getBucketName() {
    return DOCUMENT_BUCKET_NAME;
  }

  /**
   * Check if the document already has rendered page images in Elasticsearch.
   * This is to avoid re-processing images if they already exist.
   *
   * @param file The {@link FileUpload} information containing metadata about the file
   * @return true if the document has rendered page images, false otherwise
   */
  public boolean hasRenderedImages(final FileUpload file) {
    try {
      final Project project = projectService.fetch(file.projectId().toString());
      if (project == null) {
        return false;
      }

      final GetResponse<JsonNode> response = elasticsearchService.getDocument(
        project.getIndexName(),
        file.sha256Hash(),
        JsonNode.class
      );

      if (response.found()) {
        final JsonNode source = response.source();
        if (source != null && source.has("rendered_page_image_feature")) {
          final JsonNode renderedPageImageFeature = source.get(
            "rendered_page_image_feature"
          );
          return (
            renderedPageImageFeature != null &&
            renderedPageImageFeature.isObject() &&
            !renderedPageImageFeature.isEmpty()
          );
        }
      }
    } catch (Exception e) {
      log.warn(
        "Failed to check document for rendered images: {}",
        e.getMessage()
      );
    }
    return false;
  }

  /**
   * Check if the document already has raw text content in Elasticsearch.
   * This is to avoid re-processing text extraction if it already exists.
   *
   * @param file The {@link FileUpload} information containing metadata about the file
   * @return true if the document has raw text content, false otherwise
   */
  public boolean hasRawText(FileUpload file) {
    try {
      final Project project = projectService.fetch(file.projectId().toString());
      if (project == null) {
        return false;
      }

      final GetResponse<JsonNode> response = elasticsearchService.getDocument(
        project.getIndexName(),
        file.sha256Hash(),
        JsonNode.class
      );

      if (response.found()) {
        final JsonNode source = response.source();
        if (source != null && source.has("analyzed_feature")) {
          final JsonNode analyzedFeature = source.get("analyzed_feature");
          if (analyzedFeature == null || !analyzedFeature.isObject()) {
            return false;
          }
          final JsonNode textField = analyzedFeature.get("text");
          if (textField != null) {
            // Handle text stored as a string
            if (textField.isTextual() && !textField.asText().trim().isEmpty()) {
              return true;
            }
            // Handle text stored as an array
            if (textField.isArray() && !textField.isEmpty()) {
              for (JsonNode arrayItem : textField) {
                if (
                  arrayItem.isTextual() && !arrayItem.asText().trim().isEmpty()
                ) {
                  return true;
                }
              }
            }
          }
        }
      }
    } catch (Exception e) {
      log.warn("Failed to check document for raw text: {}", e.getMessage());
    }
    return false;
  }

  public FileUpload getFileUploadFromDocId(
    final String projectId,
    final String documentId
  ) throws Exception {
    final S3Service s3Service = s3ClientService.getS3Service(
      DOCUMENT_S3_CLIENT_NAME
    );

    final Project project = projectService.fetch(projectId);
    final PanteraDocument document = projectDataService.getDocument(
      project,
      documentId
    );
    Property filenameProperty = document
      .getProperties()
      .stream()
      .filter(p -> DefaultProperty.FILENAME.getKey().equals(p.getKey()))
      .findFirst()
      .orElseThrow(() ->
        new Exception(
          String.format(
            "Cannot find the filename for document %s provided",
            documentId
          )
        )
      );

    Property resourceLocationProperty = document
      .getProperties()
      .stream()
      .filter(p -> DefaultProperty.RESOURCE_LOCATION.getKey().equals(p.getKey())
      )
      .findFirst()
      .orElseThrow(() ->
        new Exception(
          String.format(
            "Cannot find the resourceLocation for document {} provided",
            documentId
          )
        )
      );

    final String filename = filenameProperty.getValue();
    final String resourceLocation = resourceLocationProperty.getValue();

    final S3Object s3Object = s3Service.getObjectInformation(
      DOCUMENT_BUCKET_NAME,
      resourceLocation
    );

    return new FileUpload(
      UUID.fromString(projectId),
      resourceLocation,
      filename,
      s3Object.getSizeInBytes(),
      Optional.of(false),
      documentId,
      Optional.empty()
    );
  }
}

