    28→/**
    29→ * Service class responsible for parsing and converting document features into structured data types.
    30→ * This class handles the transformation of various document elements (text, dates, numbers, entities)
    31→ * into strongly-typed feature objects that can be used in the document processing pipeline.
    32→ *
    33→ * Key responsibilities:
    34→ * - Converting raw text and data into TextFeature, LongFeature, and DateFeature objects
    35→ * - Parsing SpaCy NLP entities (organizations, dates, people, money, locations)
    36→ * - Processing image metadata from document extractions
    37→ * - Handling page render information
    38→ * - Breaking down text into logical paragraph blocks
    39→ *
    40→ * The class works in conjunction with ValidatorUtil to ensure data validity and proper formatting
    41→ * during the conversion process.
    42→ */
    43→@RequiredArgsConstructor
    44→@Slf4j
    45→@Service
    46→public class DocumentFeatureParser {
    47→
    48→  private final ValidatorUtil validatorUtil;
    49→
    50→  /**
    51→   * Creates a TextFeature from a single string value.
    52→   *
    53→   * @param text The text value to create the feature from
    54→   * @param key The key identifier for the feature
    55→   * @return TextFeature object containing the parsed text
    56→   * @throws RuntimeException if parsing fails
    57→   */
    58→  public TextFeature createTextFeature(String text, String key) {
    59→    final var mapValidators = validatorUtil.getMapValidators();
    60→    final var textValidator = mapValidators.get("TextFeature");
    61→    TextFeature textFeature = null;
    62→    try {
    63→      textFeature = textValidator.parseString(List.of(text), key);
    64→    } catch (Exception e) {
    65→      throw new RuntimeException(e);
    66→    }
    67→    return textFeature;
    68→  }
    69→
    70→  /**
    71→   * Creates a TextFeature from a list of strings.
    72→   *
    73→   * @param textlist List of text values to create the feature from
    74→   * @param key The key identifier for the feature
    75→   * @return TextFeature object containing the parsed text list
    76→   * @throws RuntimeException if parsing fails
    77→   */
    78→  public TextFeature createTextFeature(List<String> textlist, String key) {
    79→    final var mapValidators = validatorUtil.getMapValidators();
    80→    final var textValidator = mapValidators.get("TextFeature");
    81→    TextFeature textFeature = null;
    82→    try {
    83→      textFeature = textValidator.parseString(textlist, key);
    84→    } catch (Exception e) {
    85→      throw new RuntimeException(e);
    86→    }
    87→    return textFeature;
    88→  }
    89→
    90→  /**
    91→   * Creates a LongFeature from a string value.
    92→   * If the string cannot be parsed as a long, returns null and logs a warning.
    93→   *
    94→   * @param text The text value to parse as a long
    95→   * @param key The key identifier for the feature
    96→   * @return LongFeature object containing the parsed long value, or null if parsing fails
    97→   * @throws RuntimeException if feature creation fails (after successful number parsing)
    98→   */
    99→  public LongFeature createLongFeature(String text, String key) {
   100→    final var mapValidators = validatorUtil.getMapValidators();
   101→    final var longValidator = mapValidators.get("LongFeature");
   102→    LongFeature longFeature = null;
   103→    try {
   104→      Long value = Long.valueOf(text);
   105→      JsonNode numberNode = JsonNodeFactory.instance.numberNode(value);
   106→      longFeature = (LongFeature) longValidator.parse(
   107→        List.of(numberNode),
   108→        key,
   109→        null
   110→      );
   111→    } catch (NumberFormatException e) {
   112→      log.warn("Could not parse '{}' as long for key '{}'", text, key);
   113→    } catch (Exception e) {
   114→      throw new RuntimeException(e);
   115→    }
   116→    return longFeature;
   117→  }
   118→
   119→  /**
   120→   * Creates a DateFeature from a list of LocalDateTime objects.
   121→   *
   122→   * @param input List of LocalDateTime objects to create the feature from
   123→   * @param key The key identifier for the feature
   124→   * @return DateFeature object containing the parsed dates
   125→   * @throws RuntimeException if parsing fails
   126→   */
   127→  public DateFeature createDateFeature(List<LocalDateTime> input, String key) {
   128→    final var mapValidators = validatorUtil.getMapValidators();
   129→    final var dateValidator = mapValidators.get("DateFeature");
   130→    DateFeature dateFeature = null;
   131→
   132→    try {
   133→      dateFeature = (DateFeature) dateValidator.parseDate(input, key);
   134→    } catch (Exception e) {
   135→      throw new RuntimeException(e);
   136→    }
   137→    return dateFeature;
   138→  }
   139→
   140→  /**
   141→   * Parses SpacyEntity objects to create appropriate feature types based on the label.
   142→   *
   143→   * @param spacyEntity List of SpacyEntity objects to parse
   144→   * @param label The SpacyNerLabel indicating the type of entities
   145→   * @return BaseFeature containing the parsed entities (TextFeature, DateFeature, or DoubleFeature)
   146→   */
   147→  public BaseFeature parseSpacyFeature(
   148→    final List<SpacyEntity> spacyEntity,
   149→    final SpacyRequest.SpacyNerLabel label
   150→  ) {
   151→    final var mapValidators = validatorUtil.getMapValidators();
   152→    final var textValidator = mapValidators.get("TextFeature");
   153→    final var doubleValidator = mapValidators.get("DoubleFeature");
   154→    final var dateValidator = mapValidators.get("DateFeature");
   155→
   156→    BaseFeature spacyFeature = null;
   157→
   158→    if (label == SpacyRequest.SpacyNerLabel.ORG) {
   159→      final List<JsonNode> textList = spacyEntity
   160→        .stream()
   161→        .map(e -> (JsonNode) JsonNodeFactory.instance.textNode(e.getText()))
   162→        .toList();
   163→      spacyFeature = textValidator.parse(
   164→        textList,
   165→        DefaultProperty.SPACY_ORGANIZATIONS.getKey(),
   166→        null
   167→      );
   168→    } else if (label == SpacyRequest.SpacyNerLabel.DATE) {
   169→      final List<JsonNode> textList = spacyEntity
   170→        .stream()
   171→        .map(e -> (JsonNode) JsonNodeFactory.instance.textNode(e.getText()))
   172→        .toList();
   173→      spacyFeature = dateValidator.parse(
   174→        textList,
   175→        DefaultProperty.SPACY_DATES.getKey(),
   176→        null
   177→      );
   178→      //date feature
   179→    } else if (label == SpacyRequest.SpacyNerLabel.PERSON) {
   180→      final List<JsonNode> textList = spacyEntity
   181→        .stream()
   182→        .map(e -> (JsonNode) JsonNodeFactory.instance.textNode(e.getText()))
   183→        .toList();
   184→      spacyFeature = textValidator.parse(
   185→        textList,
   186→        DefaultProperty.SPACY_PEOPLE.getKey(),
   187→        null
   188→      );
   189→    } else if (label == SpacyRequest.SpacyNerLabel.MONEY) {
   190→      final List<JsonNode> textList = spacyEntity
   191→        .stream()
   192→        .map(e -> {
   193→          try {
   194→            return (JsonNode) JsonNodeFactory.instance.numberNode(
   195→              Double.valueOf(e.getText())
   196→            );
   197→          } catch (final NumberFormatException e1) {
   198→            return null;
   199→          }
   200→        })
   201→        .filter(Objects::nonNull)
   202→        .toList();
   203→      spacyFeature = doubleValidator.parse(
   204→        textList,
   205→        DefaultProperty.SPACY_MONEY.getKey(),
   206→        null
