package software.uncharted.pantera.service.pipeline;

import com.fasterxml.jackson.databind.JsonNode;
import com.fasterxml.jackson.databind.node.ArrayNode;
import com.fasterxml.jackson.databind.node.JsonNodeFactory;
import java.time.LocalDateTime;
import java.util.ArrayList;
import java.util.HashMap;
import java.util.List;
import java.util.Map;
import java.util.Objects;
import java.util.regex.Matcher;
import java.util.regex.Pattern;
import lombok.RequiredArgsConstructor;
import lombok.extern.slf4j.Slf4j;
import org.springframework.stereotype.Service;
import software.uncharted.pantera.model.pdfextractor.SpacyEntity;
import software.uncharted.pantera.model.pdfextractor.SpacyRequest;
import software.uncharted.pantera.model.pdfextractor.SpacyResponse;
import software.uncharted.pantera.model.search.DefaultProperty;
import software.uncharted.pantera.util.ValidatorUtil;
import software.uncharted.pipeline.common.features.BaseFeature;
import software.uncharted.pipeline.common.features.DateFeature;
import software.uncharted.pipeline.common.features.LongFeature;
import software.uncharted.pipeline.common.features.ObjectFeature;
import software.uncharted.pipeline.common.features.TextFeature;

/**
 * Service class responsible for parsing and converting document features into structured data types.
 * This class handles the transformation of various document elements (text, dates, numbers, entities)
 * into strongly-typed feature objects that can be used in the document processing pipeline.
 *
 * Key responsibilities:
 * - Converting raw text and data into TextFeature, LongFeature, and DateFeature objects
 * - Parsing SpaCy NLP entities (organizations, dates, people, money, locations)
 * - Processing image metadata from document extractions
 * - Handling page render information
 * - Breaking down text into logical paragraph blocks
 *
 * The class works in conjunction with ValidatorUtil to ensure data validity and proper formatting
 * during the conversion process.
 */
@RequiredArgsConstructor
@Slf4j
@Service
public class DocumentFeatureParser {

  private final ValidatorUtil validatorUtil;

  /**
   * Creates a TextFeature from a single string value.
   *
   * @param text The text value to create the feature from
   * @param key The key identifier for the feature
   * @return TextFeature object containing the parsed text
   * @throws RuntimeException if parsing fails
   */
  public TextFeature createTextFeature(String text, String key) {
    final var mapValidators = validatorUtil.getMapValidators();
    final var textValidator = mapValidators.get("TextFeature");
    TextFeature textFeature = null;
    try {
      textFeature = textValidator.parseString(List.of(text), key);
    } catch (Exception e) {
      throw new RuntimeException(e);
    }
    return textFeature;
  }

  /**
   * Creates a TextFeature from a list of strings.
   *
   * @param textlist List of text values to create the feature from
   * @param key The key identifier for the feature
   * @return TextFeature object containing the parsed text list
   * @throws RuntimeException if parsing fails
   */
  public TextFeature createTextFeature(List<String> textlist, String key) {
    final var mapValidators = validatorUtil.getMapValidators();
    final var textValidator = mapValidators.get("TextFeature");
    TextFeature textFeature = null;
    try {
      textFeature = textValidator.parseString(textlist, key);
    } catch (Exception e) {
      throw new RuntimeException(e);
    }
    return textFeature;
  }

  /**
   * Creates a LongFeature from a string value.
   * If the string cannot be parsed as a long, returns null and logs a warning.
   *
   * @param text The text value to parse as a long
   * @param key The key identifier for the feature
   * @return LongFeature object containing the parsed long value, or null if parsing fails
   * @throws RuntimeException if feature creation fails (after successful number parsing)
   */
  public LongFeature createLongFeature(String text, String key) {
    final var mapValidators = validatorUtil.getMapValidators();
    final var longValidator = mapValidators.get("LongFeature");
    LongFeature longFeature = null;
    try {
      Long value = Long.valueOf(text);
      JsonNode numberNode = JsonNodeFactory.instance.numberNode(value);
      longFeature = (LongFeature) longValidator.parse(
        List.of(numberNode),
        key,
        null
      );
    } catch (NumberFormatException e) {
      log.warn("Could not parse '{}' as long for key '{}'", text, key);
    } catch (Exception e) {
      throw new RuntimeException(e);
    }
    return longFeature;
  }

  /**
   * Creates a DateFeature from a list of LocalDateTime objects.
   *
   * @param input List of LocalDateTime objects to create the feature from
   * @param key The key identifier for the feature
   * @return DateFeature object containing the parsed dates
   * @throws RuntimeException if parsing fails
   */
  public DateFeature createDateFeature(List<LocalDateTime> input, String key) {
    final var mapValidators = validatorUtil.getMapValidators();
    final var dateValidator = mapValidators.get("DateFeature");
    DateFeature dateFeature = null;

    try {
      dateFeature = (DateFeature) dateValidator.parseDate(input, key);
    } catch (Exception e) {
      throw new RuntimeException(e);
    }
    return dateFeature;
  }

  /**
   * Parses SpacyEntity objects to create appropriate feature types based on the label.
   *
   * @param spacyEntity List of SpacyEntity objects to parse
   * @param label The SpacyNerLabel indicating the type of entities
   * @return BaseFeature containing the parsed entities (TextFeature, DateFeature, or DoubleFeature)
   */
  public BaseFeature parseSpacyFeature(
    final List<SpacyEntity> spacyEntity,
    final SpacyRequest.SpacyNerLabel label
  ) {
    final var mapValidators = validatorUtil.getMapValidators();
    final var textValidator = mapValidators.get("TextFeature");
    final var doubleValidator = mapValidators.get("DoubleFeature");
    final var dateValidator = mapValidators.get("DateFeature");

    BaseFeature spacyFeature = null;

    if (label == SpacyRequest.SpacyNerLabel.ORG) {
      final List<JsonNode> textList = spacyEntity
        .stream()
        .map(e -> (JsonNode) JsonNodeFactory.instance.textNode(e.getText()))
        .toList();
      spacyFeature = textValidator.parse(
        textList,
        DefaultProperty.SPACY_ORGANIZATIONS.getKey(),
        null
      );
    } else if (label == SpacyRequest.SpacyNerLabel.DATE) {
      final List<JsonNode> textList = spacyEntity
        .stream()
        .map(e -> (JsonNode) JsonNodeFactory.instance.textNode(e.getText()))
        .toList();
      spacyFeature = dateValidator.parse(
        textList,
        DefaultProperty.SPACY_DATES.getKey(),
        null
      );
      //date feature
    } else if (label == SpacyRequest.SpacyNerLabel.PERSON) {
      final List<JsonNode> textList = spacyEntity
        .stream()
        .map(e -> (JsonNode) JsonNodeFactory.instance.textNode(e.getText()))
        .toList();
      spacyFeature = textValidator.parse(
        textList,
        DefaultProperty.SPACY_PEOPLE.getKey(),
        null
      );
    } else if (label == SpacyRequest.SpacyNerLabel.MONEY) {
      final List<JsonNode> textList = spacyEntity
        .stream()
        .map(e -> {
          try {
            return (JsonNode) JsonNodeFactory.instance.numberNode(
              Double.valueOf(e.getText())
            );
          } catch (final NumberFormatException e1) {
            return null;
          }
        })
        .filter(Objects::nonNull)
        .toList();
      spacyFeature = doubleValidator.parse(
        textList,
        DefaultProperty.SPACY_MONEY.getKey(),
        null
      );
    } else if (label == SpacyRequest.SpacyNerLabel.LOC) {
      final List<JsonNode> textList = spacyEntity
        .stream()
        .map(e -> (JsonNode) JsonNodeFactory.instance.textNode(e.getText()))
        .toList();
      spacyFeature = textValidator.parse(
        textList,
        DefaultProperty.SPACY_LOCATIONS.getKey(),
        null
      );
    }
    return spacyFeature;
  }

  /**
   * Groups SpacyEntity objects from a SpacyResponse by their labels.
   *
   * @param response The SpacyResponse containing entities to be grouped
   * @return Map of SpacyNerLabel to lists of corresponding SpacyEntity objects
   */
  public Map<SpacyRequest.SpacyNerLabel, List<SpacyEntity>> parseSpacyEntities(
    final SpacyResponse response
  ) {
    // Initialize a map to group entities by their labels
    final Map<SpacyRequest.SpacyNerLabel, List<SpacyEntity>> groupedEntities =
      new HashMap<>();

    // Iterate through the list of SpacyEntity objects
    for (final SpacyEntity entity : response.getSpacy()) {
      // Match the label to the corresponding SpacyNerLabel enum
      final SpacyRequest.SpacyNerLabel label =
        SpacyRequest.SpacyNerLabel.valueOf(entity.getLabel().toUpperCase());

      // Add the entity to the map under the appropriate label
      groupedEntities
        .computeIfAbsent(label, k -> new ArrayList<>())
        .add(entity);
    }

    return groupedEntities;
  }

  /**
   * Parses extracted image information from an ArrayNode into an ObjectFeature.
   *
   * @param images ArrayNode containing image metadata
   * @return ObjectFeature containing parsed image information including filename, dimensions, bbox, and page number
   */
  public BaseFeature parseExtractedImages(final ArrayNode images) {
    final var mapValidators = validatorUtil.getMapValidators();
    final var textValidator = mapValidators.get("TextFeature");
    final var longValidator = mapValidators.get("LongFeature");

    final var extractedImagesFeature = new ObjectFeature();

    // parse node to create images' object
    final List<Map<String, Object>> extractedImages = new ArrayList<>();

    images.forEach(image -> {
      final var map = new HashMap<String, Object>();
      final var filenameFeature = textValidator.parse(
        List.of(image.get("filename")),
        DefaultProperty.FILENAME.getKey(),
        null
      );
      map.put("filename", filenameFeature);
      final var widthFeature = longValidator.parse(
        List.of(image.get("width")),
        "width",
        null
      );
      map.put("width", widthFeature);
      final var heightFeature = longValidator.parse(
        List.of(image.get("height")),
        "height",
        null
      );
      map.put("height", heightFeature);
      final var bboxFeature = longValidator.parse(
        List.of(image.get("bbox")),
        "bbox",
        null
      );
      map.put("bbox", bboxFeature);
      final var pageNumberFeature = longValidator.parse(
        List.of(image.get("page_number")),
        "page_number",
        null
      );
      map.put("page_number", pageNumberFeature);
      extractedImages.add(map);
    });

    extractedImagesFeature.init(Map.of("images", extractedImages), "images");
    return extractedImagesFeature;
  }

  /**
   * Parses page render image information from an ArrayNode into an ObjectFeature.
   *
   * @param pageRender ArrayNode containing page render metadata
   * @return ObjectFeature containing parsed page render information including filenames
   */
  public BaseFeature parsePageRenderImages(final ArrayNode pageRender) {
    final var mapValidators = validatorUtil.getMapValidators();
    final var textValidator = mapValidators.get("TextFeature");

    final var renderedPageImagesFeature = new ObjectFeature();

    final var map = new HashMap<String, Object>();

    final var pageUrls = new ArrayList<JsonNode>();
    pageRender.forEach(page -> {
      final var url = page.get("filename");
      pageUrls.add(url);
    });

    final var filenameFeature = textValidator.parse(
      pageUrls,
      DefaultProperty.FILENAME.getKey(),
      null
    );

    map.put("filename", filenameFeature);
    // parse node to create images' object
    final List<Map<String, Object>> images = new ArrayList<>();
    images.add(map);

    renderedPageImagesFeature.init(
      Map.of("rendered_page_image", images),
      "rendered_page_image"
    );

    return renderedPageImagesFeature;
  }

  /**
   * Splits a string into paragraph blocks based on various text patterns.
   * Recognizes patterns for tables, lists, headers, quotes, and regular paragraphs.
   *
   * @param s The input string to be split into paragraph blocks
   * @return List of strings where each string represents a paragraph block
   */
  public List<String> stringToParagraphBlocks(final String s) {
    List<String> paragraphBlocks = new ArrayList<>();
    String paragraphBlockRegexPattern =
      "((^\\|.*\\|\\s*$\\n?)+|(^\\s*[-*] .+\\n?)+|(^\\s*\\d+\\. .+\\n?)+|(^#+ .+\\n?)|(^> .+\\n?)|(^.+\\n?))";

    // Add the flags using Pattern constants
    Pattern blockPattern = Pattern.compile(
      paragraphBlockRegexPattern,
      Pattern.MULTILINE
    );
    Matcher matcher = blockPattern.matcher(s);
    while (matcher.find()) {
      paragraphBlocks.add(matcher.group());
    }
    return paragraphBlocks;
  }
}

