package software.uncharted.pipeline.common;

import com.fasterxml.jackson.databind.ObjectMapper;
import com.fasterxml.jackson.databind.node.ObjectNode;
import java.io.Serializable;
import java.util.List;
import lombok.Getter;
import lombok.NonNull;
import software.uncharted.pipeline.common.features.BaseFeature;
import software.uncharted.pipeline.common.features.TextFeature;

/**
 * This class represents a document in the ElasticSearch index. it will conform
 * to the template in the index
 */
public class ESDocument implements Serializable {

  @Getter
  String id;

  @Getter
  private final ObjectNode document;

  public ESDocument(
    final RawPipelineDocument document,
    @NonNull final Instructions instructions
  ) {
    id = document.getId();

    final ObjectMapper mapper = new ObjectMapper();
    this.document = mapper.createObjectNode();
    id = document.getId();
    this.document.put("id", id.toString());

    append(
      new TextFeature(
        List.of(document.getProjectId().toString()),
        "project_id"
      ),
      instructions
    ); // todo remove hardcoded name

    document
      .getFeatures()
      .forEach((key, feature) -> {
        if (feature == null) {
          return;
        }
        append(feature, instructions);
      });
  }

  public void append(
    final BaseFeature feature,
    final Instructions instructions
  ) {
    final String location = feature.getOutputLocation();
    if (location != null) {
      if (!document.has(location)) {
        document.set(location, new ObjectMapper().createObjectNode());
      }

      final var concat = instructions
        .getExportInstruction()
        .getConcatFields()
        .get(feature.getOutputName());
      final var dedupe = instructions
        .getExportInstruction()
        .getDedupeFields()
        .get(feature.getOutputName());

      final ObjectMapper mapper = new ObjectMapper();
      final ObjectNode locationNode = (ObjectNode) document.get(location);

      var value = feature.getValue();
      if (concat != null && concat && dedupe != null && dedupe) {
        value = feature.getUniqueConcatenatedValues();
      } else if (concat != null && concat) {
        value = feature.getConcatenatedValues();
      } else if (dedupe != null && dedupe) {
        value = feature.getUniqueValues();
      }

      locationNode.set(feature.getOutputName(), mapper.valueToTree(value));
    }
  }
}

