// Initialize results tracking
Map results = ['processed': 0, 'removed': 0, 'errors': [], 'skipped': []];

// Input validation
if (params == null) {
  results.errors.add('params is null');
  ctx._source._deletion_results = results;
  return;
}
if (params.deletionCriteria == null) {
  results.errors.add('deletionCriteria is null');
  ctx._source._deletion_results = results;
  return;
}
if (params.deletionCriteria.isEmpty()) {
  results.errors.add('deletionCriteria is empty');
  ctx._source._deletion_results = results;
  return;
}

// Helper function to check if a value is in numeric range
boolean isNumericInRange(def value, String minValue, String maxValue) {
  try {
    double numValue = Double.parseDouble(String.valueOf(value));
    double min = minValue != null ? Double.parseDouble(minValue) : Double.NEGATIVE_INFINITY;
    double max = maxValue != null ? Double.parseDouble(maxValue) : Double.POSITIVE_INFINITY;
    return numValue >= min && numValue <= max;
  } catch (Exception e) {
    return false; // Not a valid numeric value
  }
}

// Helper function to check if a value is in string range
boolean isStringInRange(def value, String minValue, String maxValue) {
  String strValue = String.valueOf(value);
  boolean inRange = true;
  if (minValue != null) {
    inRange = strValue.compareTo(minValue) >= 0;
  }
  if (inRange && maxValue != null) {
    inRange = strValue.compareTo(maxValue) <= 0;
  }
  return inRange;
}

// Helper function to check if a value is in range (tries numeric first, then string)
boolean isValueInRange(def value, String minValue, String maxValue) {
  if (value == null) {
    return false;
  }

  // Try numeric comparison first
  if (isNumericInRange(value, minValue, maxValue)) {
    return true;
  }

  // Fall back to string comparison
  return isStringInRange(value, minValue, maxValue);
}

for (criteria in params.deletionCriteria) {
  // Validate input parameters
  if (criteria.propertyKey == null || criteria.propertyKey.isEmpty()) {
    results.skipped.add('Empty or null propertyKey');
    continue; // Skip invalid criteria
  }

  // Validate range values if both are provided
  if (criteria.minValue != null && criteria.maxValue != null) {
    try {
      double min = Double.parseDouble(criteria.minValue);
      double max = Double.parseDouble(criteria.maxValue);
      if (min > max) {
        results.skipped.add('Invalid range: min > max for property ' + criteria.propertyKey);
        continue; // Skip invalid range
      }
    } catch (Exception e) {
      // For non-numeric values, string comparison will be used
      // Validate string range
      if (criteria.minValue.compareTo(criteria.maxValue) > 0) {
        results.skipped.add('Invalid string range for property ' + criteria.propertyKey);
        continue;
      }
    }
  }

  // Track processing for this criteria
  boolean criteriaProcessed = false;
  int totalRemoved = 0;

  // Check all feature namespaces that exist in the document
  for (namespace in ctx._source.keySet()) {
    // Only process namespaces that end with '_feature'
    if (!namespace.endsWith('_feature')) {
      continue;
    }

    if (ctx._source[namespace] != null && ctx._source[namespace].containsKey(criteria.propertyKey)) {
      def originalFieldValue = ctx._source[namespace][criteria.propertyKey];

      // Handle null values
      if (originalFieldValue == null) {
        continue;
      }

      // Cache string value to avoid repeated conversions
      String cachedStringValue = null;
      if (!(originalFieldValue instanceof List)) {
        cachedStringValue = String.valueOf(originalFieldValue);
      }

      // Create defensive copy for arrays to prevent concurrent modification issues
      def fieldValue = originalFieldValue instanceof List ? new ArrayList(originalFieldValue) : originalFieldValue;

      boolean shouldRemove = false;
      int removedFromThisField = 0;

      // Exact match case
      if (criteria.exactValue != null) {
        if (fieldValue instanceof List) {
          // Handle array values - remove matching items from array
          int originalSize = fieldValue.size();
          fieldValue.removeIf(item -> {
            if (item == null) return false;
            return String.valueOf(item).equals(criteria.exactValue);
          });
          removedFromThisField = originalSize - fieldValue.size();

          if (removedFromThisField > 0) {
            // Update the source with modified array
            ctx._source[namespace][criteria.propertyKey] = fieldValue;
            criteriaProcessed = true;
            totalRemoved += removedFromThisField;

            // If array is now empty, remove the entire field
            if (fieldValue.isEmpty()) {
              shouldRemove = true;
            }
          }
        } else {
          // Handle single values - use cached string value if available
          String valueStr = cachedStringValue != null ? cachedStringValue : String.valueOf(fieldValue);
          shouldRemove = valueStr.equals(criteria.exactValue);
          if (shouldRemove) {
            criteriaProcessed = true;
            totalRemoved += 1;
          }
        }
      } else {
        // Range case - handle arrays and single values
        if (fieldValue instanceof List) {
          // For arrays in range queries, remove items that are in range
          int originalSize = fieldValue.size();
          fieldValue.removeIf(item -> isValueInRange(item, criteria.minValue, criteria.maxValue));
          removedFromThisField = originalSize - fieldValue.size();

          if (removedFromThisField > 0) {
            // Update the source with modified array
            ctx._source[namespace][criteria.propertyKey] = fieldValue;
            criteriaProcessed = true;
            totalRemoved += removedFromThisField;

            // If array is now empty, remove the entire field
            if (fieldValue.isEmpty()) {
              shouldRemove = true;
            }
          }
        } else {
          // Handle single values
          shouldRemove = isValueInRange(fieldValue, criteria.minValue, criteria.maxValue);
          if (shouldRemove) {
            criteriaProcessed = true;
            totalRemoved += 1;
          }
        }
      }

      if (shouldRemove) {
        ctx._source[namespace].remove(criteria.propertyKey);
      }
    }
  }

  // Update results for this criteria
  if (criteriaProcessed) {
    results.processed += 1;
    results.removed += totalRemoved;
  }
}

// Add results to document for debugging/monitoring
ctx._source._deletion_results = results;

