  @Test
  void testBulkRemovePropertyValues_Debug()
    throws IOException, InterruptedException {
    final String index = randomIndexName();
    elasticsearchService.createIndex(index);

    // Simple test document
    final List<JsonNode> testData = List.of(
      objectMapper.readTree(
        """
        {
          "title": "Debug Document",
          "properties": [
            {"key": "category", "value": "science"}
          ]
        }
        """
      )
    );

    final List<String> documentIds = List.of("debug1");
    elasticsearchService.insert(index, testData, documentIds);
    elasticsearchService.refresh(index);

    // Debug: Check what the document looks like before deletion
    SearchResponse<JsonNode> beforeResponse = elasticsearchService.search(
      index,
      Query.of(b -> b.matchAll(m -> m)),
      JsonNode.class
    );
    System.out.println("Before deletion: " + beforeResponse.hits().hits().get(0).source());

    // Try to remove "science" from "category"
    final List<Object> criteria = List.of(
      new TestDeletionCriteria("category", "science", null, null)
    );
    final String taskId = elasticsearchService.bulkRemovePropertyValues(
      index,
      criteria,
      documentIds
    );

    elasticsearchService.waitForTaskCompletion(taskId, 10).join();
    elasticsearchService.refresh(index);

    // Debug: Check what the document looks like after deletion
    SearchResponse<JsonNode> afterResponse = elasticsearchService.search(
      index,
      Query.of(b -> b.matchAll(m -> m)),
      JsonNode.class
    );
    JsonNode afterDoc = afterResponse.hits().hits().get(0).source();
    System.out.println("After deletion: " + afterDoc);

    // Check for debug results
    if (afterDoc.has("_deletion_results")) {
      System.out.println("Deletion results: " + afterDoc.get("_deletion_results"));
    }
  }
}

