  @Test
  public void testItCanUpsertDataAndPipelineAddsProperty()
    throws IOException, InterruptedException {
    final String name = randomIndexName();
    elasticsearchService.createIndex(name);
    Assertions.assertTrue(elasticsearchService.hasIndex(name));

    // Initial upsert with some test data
    final List<JsonNode> initialData = List.of(
      objectMapper
        .createObjectNode()
        .put("testField", "object1")
        .put("title", "Test Document 1"),
      objectMapper
        .createObjectNode()
        .put("testField", "object2")
        .put("title", "Test Document 2")
    );
    final List<String> ids = List.of("test-doc-1", "test-doc-2");

    elasticsearchService.upsert(name, initialData, ids);

    // Wait for async full_text_search refresh to complete
    Thread.sleep(1000);

    elasticsearchService.refresh(name);

    // Verify documents were upserted and pipeline ran
    SearchResponse<JsonNode> response = elasticsearchService.search(
      name,
      Query.of(b -> b.matchAll(m -> m)),
      JsonNode.class
    );
    Assertions.assertEquals(2L, response.hits().total().value());

    // Verify full_text_search_data was created by pipeline
    Assertions.assertTrue(
      response
        .hits()
        .hits()
        .stream()
        .allMatch(
          hit ->
            hit
              .source()
              .at("/analyzed_feature/full_text_search_data")
              .isArray() &&
            hit.source().at("/analyzed_feature/full_text_search_data").size() >
            0
        )
    );

    // Verify the full_text_search_data contains our test fields
    response
      .hits()
      .hits()
      .forEach(hit -> {
        ArrayNode fullTextData = (ArrayNode) hit
          .source()
          .at("/analyzed_feature/full_text_search_data");
        List<String> searchData = new ArrayList<>();
        fullTextData.forEach(node -> searchData.add(node.asText()));

        // Should contain values from testField and title
        boolean containsTestField = searchData
          .stream()
          .anyMatch(s -> s.startsWith("object"));
        boolean containsTitle = searchData
          .stream()
          .anyMatch(s -> s.contains("Test Document"));

        Assertions.assertTrue(
          containsTestField,
          "full_text_search_data should contain testField values"
        );
        Assertions.assertTrue(
          containsTitle,
          "full_text_search_data should contain title values"
        );
      });
  }

