    Hit<JsonNode> hit = response.hits().hits().get(0);
    ArrayNode fullTextData = (ArrayNode) hit
      .source()
      .at("/analyzed_feature/full_text_search_data");
    List<String> searchData = new ArrayList<>();
    fullTextData.forEach(node -> searchData.add(node.asText()));

    // Should contain updated values
    boolean containsUpdatedTestField = searchData
      .stream()
      .anyMatch(s -> s.contains("updated"));
    boolean containsUpdatedTitle = searchData
      .stream()
      .anyMatch(s -> s.contains("Updated Test Document"));
    boolean containsNewField = searchData
      .stream()
      .anyMatch(s -> s.contains("additional data"));

    Assertions.assertTrue(
      containsUpdatedTestField,
      "full_text_search_data should contain updated testField values"
    );
    Assertions.assertTrue(
      containsUpdatedTitle,
      "full_text_search_data should contain updated title values"
    );
    Assertions.assertTrue(
      containsNewField,
      "full_text_search_data should contain new field values"
    );

    // Should NOT contain old values (since this is an upsert/replace operation)
    boolean containsOldTitle = searchData
      .stream()
      .anyMatch(s -> s.equals("Test Document 1"));
    Assertions.assertFalse(
      containsOldTitle,
      "full_text_search_data should not contain old title values after upsert"
    );
  }

