  @Test
  public void testItCanExecuteAnEnrichPolicy()
    throws InterruptedException, IOException {
    final String indexName = randomIndexName();
    elasticsearchService.createIndex(indexName);
    // Create a set of 20000 documents to ensure the enrich policy is executed in multiple batches
    final List<JsonNode> data = new ArrayList<>();
    for (int i = 0; i < 20000; i++) {
      data.add(
        objectMapper
          .createObjectNode()
          .put("keyword_feature", String.valueOf(i))
      );
    }
    elasticsearchService.insert(indexName, data);
    elasticsearchService.refresh(indexName);

    final String policyName = elasticsearchService.createEnrichPolicy(
      indexName,
      "keyword_feature",
      List.of("keyword_feature")
    );
    final String taskId = elasticsearchService.executeEnrichPolicy(policyName);
    Assertions.assertNotNull(taskId);
    Assertions.assertDoesNotThrow(() ->
      elasticsearchService.waitForEnrichTaskCompletion(taskId, 10).join()
    );
    Assertions.assertEquals(
      elasticsearchService.count(indexName),
      elasticsearchService.count(".enrich-" + policyName)
    );
  }

  @Test
  void testItCanUpdateAllByQuery() throws IOException, InterruptedException {
    final String pipeline =
      """
      {
        "processors": [
          {
            "script": {
              "source": "ctx.keyword_feature.name = 'updated'",
              "lang": "painless"
            }
          }
        ]
      }
      """;
    final String pipelineName = UUID.randomUUID().toString();
    elasticsearchService.putPipeline(
      pipelineName,
      new ByteArrayInputStream(pipeline.getBytes())
    );

    final String indexName = randomIndexName();
    elasticsearchService.createIndex(indexName);
    final List<JsonNode> data = List.of(
      objectMapper.readTree(
        "{\"keyword_feature\": {\"id\": \"1\", \"name\": \"object1\", \"description\": \"description1\"}}"
      ),
      objectMapper.readTree(
        "{\"keyword_feature\": {\"id\": \"2\", \"name\": \"object2\", \"description\": \"description2\"}}"
      )
    );
    elasticsearchService.insert(indexName, data);
    elasticsearchService.refresh(indexName);

    final String taskId = elasticsearchService.updateByQuery(
      indexName,
      pipelineName
    );
    Thread.sleep(1000); // Wait for the task to start
    Assertions.assertDoesNotThrow(() ->
      elasticsearchService.waitForTaskCompletion(taskId, 10).join()
    );

    // Ensure the documents were updated
    final SearchResponse<JsonNode> response = elasticsearchService.search(
      indexName,
      Query.of(b -> b.matchAll(m -> m)),
      JsonNode.class
    );
    Assertions.assertEquals(2L, response.hits().total().value());
    Assertions.assertTrue(
      response
        .hits()
        .hits()
        .stream()
        .allMatch(hit ->
          hit.source().at("/keyword_feature/name").asText().equals("updated")
        )
    );
  }

