  @Test
  void testBulkRemovePropertyValues_SuccessfulRemoval()
    throws IOException, InterruptedException {
    final String index = randomIndexName();
    elasticsearchService.createIndex(index);

    // Create test documents with properties array
    final List<JsonNode> testData = List.of(
      objectMapper.readTree(
        """
        {
          "title": "Document 1",
          "properties": [
            {"key": "category", "value": "science"},
            {"key": "author", "value": "John Doe"},
            {"key": "category", "value": "technology"}
          ]
        }
        """
      ),
      objectMapper.readTree(
        """
        {
          "title": "Document 2",
          "properties": [
            {"key": "category", "value": "science"},
            {"key": "language", "value": "English"},
            {"key": "category", "value": "history"}
          ]
        }
        """
      ),
      objectMapper.readTree(
        """
        {
          "title": "Document 3",
          "properties": [
            {"key": "author", "value": "Jane Smith"},
            {"key": "language", "value": "French"}
          ]
        }
        """
      )
    );

    final List<String> documentIds = List.of("doc1", "doc2", "doc3");
    elasticsearchService.insert(index, testData, documentIds);
    elasticsearchService.refresh(index);

    // Remove "science" values from "category" property in doc1 and doc2
    final List<Object> criteria = List.of(
      new TestDeletionCriteria("category", "science", null, null)
    );
    final String taskId = elasticsearchService.bulkRemovePropertyValues(
      index,
      criteria,
      List.of("doc1", "doc2")
    );

    Assertions.assertNotNull(taskId);
    Assertions.assertFalse(taskId.isEmpty());

    // Wait for task completion
    Assertions.assertDoesNotThrow(() ->
      elasticsearchService.waitForTaskCompletion(taskId, 10).join()
    );

    elasticsearchService.refresh(index);

    // Verify results
    final SearchResponse<JsonNode> response = elasticsearchService.search(
      index,
      Query.of(b -> b.matchAll(m -> m)),
      JsonNode.class
    );

    Assertions.assertEquals(3L, response.hits().total().value());

    // Check doc1: should have removed "science" but kept "technology" and "author"
    final JsonNode doc1 = response
      .hits()
      .hits()
      .stream()
      .filter(hit -> hit.id().equals("doc1"))
      .findFirst()
      .orElseThrow()
      .source();

    final JsonNode doc1Properties = doc1.get("properties");
    Assertions.assertTrue(doc1Properties.isArray());

    boolean hasCategoryScience = false;
    boolean hasCategoryTechnology = false;
    boolean hasAuthor = false;

    for (JsonNode prop : doc1Properties) {
      String key = prop.get("key").asText();
      String value = prop.get("value").asText();

