// Input validation
if (params == null || params.deletionCriteria == null) {
  return;
}

// Helper function to check if a value is in range (supports both numeric and string comparison)
boolean isValueInRange(def value, String minValue, String maxValue) {
  if (value == null) {
    return false;
  }

  // Try numeric comparison first
  try {
    double numValue = Double.parseDouble(String.valueOf(value));
    double min = minValue != null ? Double.parseDouble(minValue) : Double.NEGATIVE_INFINITY;
    double max = maxValue != null ? Double.parseDouble(maxValue) : Double.POSITIVE_INFINITY;
    return numValue >= min && numValue <= max;
  } catch (Exception e) {
    // Fall back to string comparison
    String strValue = String.valueOf(value);
    boolean inRange = true;
    if (minValue != null) {
      inRange = strValue.compareTo(minValue) >= 0;
    }
    if (inRange && maxValue != null) {
      inRange = strValue.compareTo(maxValue) <= 0;
    }
    return inRange;
  }
}

for (criteria in params.deletionCriteria) {
  if (criteria.propertyKey == null || criteria.propertyKey.isEmpty()) {
    continue;
  }

  // Validate range values if both are provided
  if (criteria.minValue != null && criteria.maxValue != null) {
    try {
      double min = Double.parseDouble(criteria.minValue);
      double max = Double.parseDouble(criteria.maxValue);
      if (min > max) {
        continue;
      }
    } catch (Exception e) {
      // For non-numeric values, use string comparison
      if (criteria.minValue.compareTo(criteria.maxValue) > 0) {
        continue;
      }
    }
  }

  // Handle legacy "properties" array format - remove ALL matching values
  if (ctx._source.containsKey('properties') && ctx._source['properties'] instanceof List) {
    def propertiesArray = ctx._source['properties'];
    def indicesToRemove = [];

    for (int i = 0; i < propertiesArray.size(); i++) {
      def prop = propertiesArray[i];
      if (prop != null && prop.containsKey('key') && prop.containsKey('value')) {
        String propKey = String.valueOf(prop['key']);
        def propValue = prop['value'];

        if (propKey.equals(criteria.propertyKey)) {
          boolean shouldRemove = false;

          // Exact match case
          if (criteria.exactValue != null) {
            shouldRemove = String.valueOf(propValue).equals(criteria.exactValue);
          } else if (criteria.minValue != null || criteria.maxValue != null) {
            // Range case
            shouldRemove = isValueInRange(propValue, criteria.minValue, criteria.maxValue);
          }

          if (shouldRemove) {
            indicesToRemove.add(i);
          }
        }
      }
    }

    // Remove items in reverse order to maintain correct indices
    for (int i = indicesToRemove.size() - 1; i >= 0; i--) {
      int index = indicesToRemove.get(i);
      propertiesArray.remove(index);
    }
  }

  // Handle _feature namespaces - process ALL namespaces (no early breaks)
  for (namespace in ctx._source.keySet()) {
    if (!namespace.endsWith('_feature')) {
      continue;
    }

    if (ctx._source[namespace] != null && ctx._source[namespace].containsKey(criteria.propertyKey)) {
      def originalFieldValue = ctx._source[namespace][criteria.propertyKey];

      if (originalFieldValue == null) {
        continue;
      }

      boolean shouldRemove = false;

      // Handle arrays and single values
      if (originalFieldValue instanceof List) {
        def fieldValue = new ArrayList(originalFieldValue);
        int originalSize = fieldValue.size();

        if (criteria.exactValue != null) {
          fieldValue.removeIf(item -> {
            if (item == null) return false;
            return String.valueOf(item).equals(criteria.exactValue);
          });
        } else if (criteria.minValue != null || criteria.maxValue != null) {
          fieldValue.removeIf(item -> isValueInRange(item, criteria.minValue, criteria.maxValue));
        }

        if (fieldValue.size() < originalSize) {
          ctx._source[namespace][criteria.propertyKey] = fieldValue;

          if (fieldValue.isEmpty()) {
            shouldRemove = true;
          }
        }
      } else {
        // Handle single values
        if (criteria.exactValue != null) {
          shouldRemove = String.valueOf(originalFieldValue).equals(criteria.exactValue);
        } else if (criteria.minValue != null || criteria.maxValue != null) {
          shouldRemove = isValueInRange(originalFieldValue, criteria.minValue, criteria.maxValue);
        }
      }

      if (shouldRemove) {
        ctx._source[namespace].remove(criteria.propertyKey);
      }
    }
  }
}

