   256→  @GetMapping
   257→  public ResponseEntity<ElasticsearchPage<PanteraDocument>> search(
   258→    @PathVariable("id") String id,
   259→    @RequestParam(required = false, defaultValue = "*") String queryString,
   260→    @RequestParam(required = false) String filter,
   261→    @RequestParam(required = false, defaultValue = "0") Integer page,
   262→    @RequestParam(required = false, defaultValue = "5") Integer rows,
   263→    @RequestParam(required = false) String sortField,
   264→    @RequestParam(required = false) Integer sortOrder
   265→  ) {
   266→    final Project project = projectService.fetch(id);
   267→    final PanteraSearchRequest request = new PanteraSearchRequest()
   268→      .setQuery(queryString)
   269→      .setFilter(filter)
   270→      .setPage(page)
   271→      .setSize(rows > 10000 ? 10000 : rows)
   272→      .setSort(sortField)
   273→      .setOrder(
   274→        sortOrder == null || sortOrder == 1 ? SortOrder.Asc : SortOrder.Desc
   275→      )
   276→      .setTrackTotalHits(true);
   277→    return ResponseEntity.ok(projectDataService.search(project, request));
   278→  }
