   280→  /**
   281→   * Search for document IDs only (similar to search but returns only the list of document IDs)
   282→   * @param id          The project id
   283→   * @param queryString The query string
   284→   * @param filter      The filter string
   285→   * @param rows        The number of rows per page
   286→   * @param sortField   The field to sort by
   287→   * @param sortOrder   The sort order
   288→   * @return            A list of document IDs
   289→   */
   290→  @GetMapping("/searchIds")
   291→  public ResponseEntity<List<String>> searchIds(
   292→    @PathVariable("id") String id,
   293→    @RequestParam(required = false, defaultValue = "*") String queryString,
   294→    @RequestParam(required = false) String filter,
   295→    @RequestParam(required = false, defaultValue = "10000") Integer rows,
   296→    @RequestParam(required = false) String sortField,
   297→    @RequestParam(required = false) Integer sortOrder
   298→  ) {
   299→    final Project project = projectService.fetch(id);
   300→    final PanteraSearchRequest request = new PanteraSearchRequest()
   301→      .setQuery(queryString)
   302→      .setFilter(filter)
   303→      .setPage(0)
   304→      .setSize(rows > 10000 ? 10000 : rows)
   305→      .setSort(sortField)
   306→      .setOrder(
   307→        sortOrder == null || sortOrder == 1 ? SortOrder.Asc : SortOrder.Desc
   308→      )
   309→      .setTrackTotalHits(true);
   310→
   311→    ElasticsearchPage<PanteraDocument> searchResult = projectDataService.search(
   312→      project,
   313→      request
   314→    );
   315→    List<String> documentIds = searchResult
   316→      .getContent()
   317→      .stream()
   318→      .map(PanteraDocument::getId)
   319→      .collect(Collectors.toList());
   320→
   321→    return ResponseEntity.ok(documentIds);
   322→  }
