   352→  /**
   353→   * Compute a timeline date histogram. The timeline is computed for the given property key and interval and based upon
   354→   * the query. The bucket count is the number of buckets to return. The max timestamp a timestamp to use as the "rightmost"
   355→   * bucket. If not provided, we calculate the max timestamp from the data and query. This is to show the most recent data
   356→   * first.
   357→   *
   358→   * @param id                The project id
   359→   * @param query             The query string
   360→   * @param filter            The filter string
   361→   * @param interval          The {@link TimelineInterval} to use
   362→   * @param propertyKey       The property key to use. Will return 400 if this is not tagged as a {@link PropertyTag#DATE}
   363→   * @param bucketCount       The number of buckets to return
   364→   * @param maxTimestampMsIn  A timestamp contained in the max (rightmost) bucket
   365→   * @return                  The {@link TimelineResult}
   366→   */
   367→  @GetMapping("/timeline")
   368→  public ResponseEntity<TimelineResult> getTimeline(
   369→    @PathVariable("id") String id,
   370→    @RequestParam(required = false, defaultValue = "*") String query,
   371→    @RequestParam(required = false, defaultValue = "") String filter,
   372→    @RequestParam String interval,
   373→    @RequestParam String propertyKey,
   374→    @RequestParam int bucketCount,
   375→    @RequestParam(
   376→      value = "maxTimestamp",
   377→      required = false
   378→    ) Long maxTimestampMsIn
   379→  ) {
   380→    final Project project = projectService.fetch(id);
   381→    final PanteraSearchRequest request = new PanteraSearchRequest()
   382→      .setQuery(query)
   383→      .setFilter(filter)
   384→      .setTrackTotalHits(false);
   385→
   386→    final PropertyDescriptor propertyDescriptor =
   387→      propertyDescriptorService.findByKey(project, propertyKey);
   388→
   389→    Long maxTimestampMs = maxTimestampMsIn;
   390→    if (maxTimestampMsIn == null) {
   391→      final MinMax minMax = projectDataService.getMinMax(
   392→        project,
   393→        request,
   394→        propertyDescriptor
   395→      );
   396→      maxTimestampMs = minMax.getMax().longValue();
   397→    }
   398→
   399→    final TimelineResult result = projectDataService.getTimeline(
   400→      project,
   401→      request,
   402→      propertyDescriptor,
   403→      TimelineInterval.valueOf(interval.toUpperCase()),
   404→      bucketCount,
   405→      maxTimestampMs
   406→    );
   407→
   408→    return ResponseEntity.ok(result);
   409→  }
