   505→  /**
   506→   * Get the facets for the project
   507→   * @param id      The project id
   508→   * @param queryString   The query string.
   509→   * @param filter  The lucene filter string to use for the search
   510→   * @param keys    The {@link PropertyDescriptor} keys to facet on.
   511→   * @return        The {@link FacetsResult} for the project
   512→   */
   513→  @PostMapping("/getFacets")
   514→  public ResponseEntity<FacetsResult> getFacets(
   515→    @PathVariable("id") String id,
   516→    @RequestParam(required = false, defaultValue = "*") String queryString,
   517→    @RequestParam(required = false) String filter,
   518→    @RequestBody(required = false) Map<
   519→      String,
   520→      CategoricalFacetDisplayState
   521→    > displayState,
   522→    @RequestParam String[] keys
   523→  ) {
   524→    final Project project = projectService.fetch(id);
   525→    final PanteraSearchRequest request = new PanteraSearchRequest()
   526→      .setSize(0)
   527→      .setQuery(queryString)
   528→      .setFilter(filter)
   529→      .setTrackTotalHits(true)
   530→      .setTrackScores(false);
   531→
   532→    final FacetsResult result = projectDataService.getFacets(
   533→      project,
   534→      request,
   535→      Arrays.asList(keys),
   536→      (displayState == null) ? new HashMap<>() : displayState
   537→    );
   538→    return ResponseEntity.ok(result);
   539→  }
