   541→  /**
   542→   * Get the facets for a specific document in the project
   543→   * @param id            The project id
   544→   * @param documentId    The document id
   545→   * @param queryString   The query string.
   546→   * @param filter        The filter string
   547→   * @param displayState  The display state for the facets
   548→   * @param keys          The {@link PropertyDescriptor} keys to facet on.
   549→   * @return              The {@link FacetsResult} for the project and document
   550→   */
   551→  @PostMapping("/{documentId}/getFacets")
   552→  public ResponseEntity<FacetsResult> getDocumentFacets(
   553→    @PathVariable("id") String id,
   554→    @PathVariable("documentId") String documentId,
   555→    @RequestParam(required = false, defaultValue = "*") String queryString,
   556→    @RequestParam(required = false) String filter,
   557→    @RequestBody(required = false) Map<
   558→      String,
   559→      CategoricalFacetDisplayState
   560→    > displayState,
   561→    @RequestParam String[] keys
   562→  ) {
   563→    final Project project = projectService.fetch(id);
   564→
   565→    // Create a document-specific filter that combines the existing filter with document ID
   566→    String documentFilter = "id:\"" + documentId + "\"";
   567→    String combinedFilter;
   568→
   569→    if (filter != null && !filter.trim().isEmpty()) {
   570→      combinedFilter = "(" + filter + ") AND " + documentFilter;
   571→    } else {
   572→      combinedFilter = documentFilter;
   573→    }
   574→
   575→    final PanteraSearchRequest request = new PanteraSearchRequest()
   576→      .setSize(0)
   577→      .setQuery(queryString)
   578→      .setFilter(combinedFilter) // Use the combined filter that includes document ID
   579→      .setTrackTotalHits(true)
   580→      .setTrackScores(false);
   581→
   582→    final FacetsResult result = projectDataService.getFacets(
   583→      project,
   584→      request,
   585→      Arrays.asList(keys),
   586→      (displayState == null) ? new HashMap() : displayState
   587→    );
   588→    return ResponseEntity.ok(result);
   589→  }
   590→}
