    36→@Service
    37→@RequiredArgsConstructor
    38→@Slf4j
    39→public class PropertyDescriptorService {
    40→
    41→  final ElasticsearchService elasticsearchService;
    42→  final PropertyDescriptorRepository propertyDescriptorRepository;
    43→
    44→  /**
    45→   * Get all property descriptors for the given project.
    46→   *
    47→   * @param project The project to get the property descriptors for
    48→   * @return The property descriptors for the project
    49→   */
    50→  public List<PropertyDescriptor> getConfiguredPropertyDescriptors(
    51→    final Project project
    52→  ) {
    53→    return propertyDescriptorRepository.findAllByProject(project);
    54→  }
    55→
    56→  /**
    57→   * Page the property descriptors for the given project
    58→   * @param project       the project to get the property descriptors for
    59→   * @param page          the page number
    60→   * @param rows          the number of rows per page
    61→   * @param sortField     the field to sort by
    62→   * @param sortOrder     the sort order
    63→   * @param queryString   the query string to filter by
    64→   * @return              the paged property descriptors for the project
    65→   */
    66→  public Page<PropertyDescriptor> getConfiguredPropertyDescriptors(
    67→    final Project project,
    68→    int page,
    69→    int rows,
    70→    String sortField,
    71→    Integer sortOrder,
    72→    String queryString
    73→  ) {
    74→    return propertyDescriptorRepository.findAll(
    75→      PropertyDescriptorSpecification.filterBy(project, queryString),
    76→      SortedPageRequest.of(sortField, sortOrder, page, rows)
    77→    );
    78→  }
    79→
    80→  /**
    81→   * Get the property descriptors that have associated data indexed in the given project
    82→   *
    83→   * @param project The project to get the property descriptors for
    84→   * @return The property descriptors for the project that have associated data indexed
    85→   */
    86→  @PrefixedCacheable
    87→  public List<PropertyDescriptor> getExistingPropertyDescriptors(
    88→    final Project project
    89→  ) {
    90→    final List<PropertyDescriptor> configuredPropertyDescriptors =
    91→      getConfiguredPropertyDescriptors(project);
    92→    if (
    93→      configuredPropertyDescriptors == null ||
    94→      configuredPropertyDescriptors.isEmpty()
    95→    ) {
    96→      return List.of();
    97→    }
    98→    final MsearchRequest request = MsearchRequest.of(
    99→      multiSearchRequestBuilder -> {
   100→        multiSearchRequestBuilder
   101→          .index(project.getIndexName())
   102→          .ignoreUnavailable(true)
   103→          .searches(
   104→            configuredPropertyDescriptors
   105→              .stream()
   106→              .map(propertyDescriptor ->
   107→                RequestItem.of(requestItemBuild ->
   108→                  requestItemBuild
   109→                    .body(bodyBuilder ->
   110→                      bodyBuilder
   111→                        .query(
   112→                          Query.of(queryBuilder ->
   113→                            queryBuilder.exists(existsQueryBuilder ->
   114→                              existsQueryBuilder.field(
   115→                                propertyDescriptor.getMapping()
   116→                              )
   117→                            )
   118→                          )
   119→                        )
   120→                        .size(1)
   121→                        .trackTotalHits(TrackHits.of(v -> v.count(1)))
   122→                    )
   123→                    .header(headerBuilder ->
   124→                      headerBuilder.index(project.getIndexName())
   125→                    )
   126→                )
   127→              )
   128→              .collect(Collectors.toList())
   129→          );
   130→        return multiSearchRequestBuilder;
   131→      }
   132→    );
   133→
   134→    final Map<PropertyDescriptor, Boolean> propertyDescriptorToHasValues =
   135→      new HashMap<>();
   136→    final MsearchResponse<JsonNode> response = elasticsearchService.msearch(
   137→      request,
   138→      JsonNode.class
   139→    );
   140→    for (int i = 0; i < response.responses().size(); i++) {
   141→      final MultiSearchResponseItem<JsonNode> multiSearchResponse = response
   142→        .responses()
   143→        .get(i);
   144→      if (multiSearchResponse.isFailure()) {
   145→        final var error = multiSearchResponse.failure().error();
   146→        throw new ResponseStatusException(
   147→          HttpStatus.INTERNAL_SERVER_ERROR,
   148→          "Error determining whether the Elasticsearch field described by a property descriptor exists. " +
   149→          error.type() +
   150→          ": " +
   151→          error.reason()
   152→        );
   153→      } else {
   154→        final boolean hasValues =
   155→          multiSearchResponse.result().hits().total().value() > 0;
   156→        propertyDescriptorToHasValues.put(
   157→          configuredPropertyDescriptors.get(i),
   158→          hasValues
   159→        );
   160→      }
   161→    }
   162→
   163→    final List<PropertyDescriptor> descriptors = configuredPropertyDescriptors
   164→      .stream()
   165→      .filter(propertyDescriptorToHasValues::get)
   166→      .collect(Collectors.toList());
   167→
   168→    descriptors.addAll(
   169→      configuredPropertyDescriptors
   170→        .stream()
   171→        .filter(pd -> pd.getKey().equals(DefaultProperty.SEARCH_SET.getKey()))
   172→        .toList()
   173→    );
   174→
   175→    return descriptors;
   176→  }
   177→
   178→  /**
   179→   * Find a property descriptor by its key
   180→   *
   181→   * @param project the configured project
   182→   * @param key     the key of the property descriptor
   183→   * @return the property descriptor with the given key, or null if it does not exist
   184→   */
   185→  public PropertyDescriptor findByKey(final Project project, final String key) {
   186→    return propertyDescriptorRepository
   187→      .findById(PropertyDescriptor.getId(key, project))
   188→      .orElse(null);
   189→  }
   190→
   191→  /**
   192→   * Find
   193→   * @param project
   194→   * @param tag
   195→   * @return
   196→   */
   197→  public List<PropertyDescriptor> findAllByTag(
   198→    final Project project,
   199→    final PropertyTag tag
   200→  ) {
   201→    return propertyDescriptorRepository
   202→      .findAllByProject(project)
   203→      .stream()
   204→      .filter(pd -> pd.containsTag(tag))
   205→      .collect(Collectors.toList());
   206→  }
   207→
   208→  /**
   209→   * Create a new property descriptor for the given project
   210→   *
   211→   * @param project            the project to create the property descriptor for
   212→   * @param propertyDescriptor the property descriptor to create
   213→   * @return the persisted property descriptor
   214→   */
   215→  @PrefixedCacheEvict
   216→  public PropertyDescriptor create(
   217→    final Project project,
   218→    final PropertyDescriptor propertyDescriptor
   219→  ) {
   220→    propertyDescriptor.setProject(project);
   221→    try {
   222→      final PropertyDescriptor created = propertyDescriptorRepository.save(
   223→        propertyDescriptor
   224→      );
   225→      log.info(
   226→        "Created property descriptor {} for project {}",
   227→        created.getKey(),
   228→        project.getId()
   229→      );
   230→      return created;
   231→    } catch (final Exception e) {
   232→      return null;
   233→    }
   234→  }
   235→
   236→  /**
   237→   * Create a list of new property descriptors for the given project
   238→   *
   239→   * @param project             the project to create the property descriptors for
   240→   * @param propertyDescriptors the property descriptors to create
   241→   * @return the persisted property descriptors
   242→   */
   243→  @PrefixedCacheEvict
   244→  public List<PropertyDescriptor> create(
   245→    final Project project,
   246→    final List<PropertyDescriptor> propertyDescriptors
   247→  ) {
   248→    if (propertyDescriptors.isEmpty()) {
   249→      return List.of();
   250→    }
   251→
   252→    // Set project and deduplicate input
   253→    Map<String, PropertyDescriptor> uniqueByKey = propertyDescriptors
   254→      .stream()
   255→      .peek(pd -> pd.setProject(project))
   256→      .collect(
   257→        Collectors.toMap(
   258→          PropertyDescriptor::getKey,
   259→          Function.identity(),
   260→          (existing, replacement) -> existing,
   261→          LinkedHashMap::new
   262→        )
   263→      );
   264→
   265→    // Get all existing descriptors in ONE query
   266→    List<PropertyDescriptor> existingDescriptors =
   267→      propertyDescriptorRepository.findAllByProjectAndKeyIn(
   268→        project,
   269→        uniqueByKey.keySet()
   270→      );
   271→
   272→    Set<String> existingKeys = existingDescriptors
   273→      .stream()
   274→      .map(PropertyDescriptor::getKey)
   275→      .collect(Collectors.toSet());
   276→
   277→    // Only create new ones
   278→    List<PropertyDescriptor> toCreate = uniqueByKey
   279→      .values()
   280→      .stream()
   281→      .filter(pd -> !existingKeys.contains(pd.getKey()))
   282→      .collect(Collectors.toList());
   283→
   284→    List<PropertyDescriptor> result = new ArrayList<>(existingDescriptors);
   285→    if (!toCreate.isEmpty()) {
   286→      try {
   287→        result.addAll(propertyDescriptorRepository.saveAll(toCreate));
   288→        log.info(
   289→          "Created {} new property descriptors for project {}",
   290→          toCreate.size(),
   291→          project.getId()
   292→        );
   293→      } catch (Exception e) {
   294→        // Handle race condition - some may have been created by another thread
   295→        log.warn(
   296→          "Some property descriptors may have been created by another thread: {}",
   297→          e.getMessage()
   298→        );
   299→
   300→        // Query again to get the ones that were created by other threads
   301→        List<PropertyDescriptor> allDescriptors =
   302→          propertyDescriptorRepository.findAllByProjectAndKeyIn(
   303→            project,
   304→            uniqueByKey.keySet()
   305→          );
   306→        result = allDescriptors;
   307→      }
   308→    }
   309→
   310→    log.info(
   311→      "Found {} existing, ensured {} total property descriptors for project {}",
   312→      existingKeys.size(),
   313→      result.size(),
   314→      project.getId()
   315→    );
   316→
   317→    return result;
   318→  }
   319→
   320→  /**
   321→   * Remove a property descriptor from the given project
   322→   *
   323→   * @param project the project to remove the property descriptor from
   324→   * @param key     the key of the property descriptor to remove
   325→   */
   326→  @PrefixedCacheEvict
   327→  public void remove(final Project project, final String key) {
   328→    propertyDescriptorRepository.deleteById(
   329→      PropertyDescriptor.getId(key, project)
   330→    );
   331→    log.info(
   332→      "Deleted property descriptor {} for project {}",
   333→      key,
   334→      project.getId()
   335→    );
   336→  }
   337→
   338→  /**
   339→   * Update a property descriptor
   340→   *
   341→   * @param project            the project to update the property descriptor for
   342→   * @param propertyDescriptor the property descriptor to update
   343→   * @return the updated property descriptor
   344→   */
   345→  @PrefixedCacheEvict
   346→  public PropertyDescriptor update(
   347→    final Project project,
   348→    final PropertyDescriptor propertyDescriptor
   349→  ) {
   350→    final PropertyDescriptor existing = propertyDescriptorRepository
   351→      .findById(PropertyDescriptor.getId(propertyDescriptor.getKey(), project))
   352→      .orElse(null);
   353→    if (existing == null) {
   354→      throw new IllegalArgumentException(
   355→        "No property descriptor with the given id exists"
   356→      );
   357→    }
   358→    existing.merge(propertyDescriptor);
   359→    final PropertyDescriptor updated = propertyDescriptorRepository.save(
   360→      existing
   361→    );
   362→    log.info(
   363→      "Updated property descriptor {} for project {}",
   364→      updated.getKey(),
   365→      project.getId()
   366→    );
   367→    return updated;
   368→  }
   369→
   370→  /**
   371→   * Update a list of property descriptors
   372→   *
   373→   * @param project             the project to update the property descriptors for
   374→   * @param propertyDescriptors the property descriptors to update
   375→   * @return the updated property descriptors
   376→   */
   377→  @PrefixedCacheEvict
   378→  public List<PropertyDescriptor> update(
   379→    final Project project,
   380→    final List<PropertyDescriptor> propertyDescriptors
   381→  ) {
   382→    final List<PropertyDescriptor> existing =
   383→      propertyDescriptorRepository.findAllById(
   384→        propertyDescriptors
   385→          .stream()
   386→          .map(propertyDescriptor ->
   387→            PropertyDescriptor.getId(propertyDescriptor.getKey(), project)
   388→          )
   389→          .toList()
   390→      );
   391→    if (existing.size() != propertyDescriptors.size()) {
   392→      throw new IllegalArgumentException("Not all property descriptors exist");
   393→    }
   394→
   395→    existing.forEach(existingDescriptor ->
   396→      propertyDescriptors
   397→        .stream()
   398→        .filter(propertyDescriptor ->
   399→          propertyDescriptor.getKey().equals(existingDescriptor.getKey())
   400→        )
   401→        .findFirst()
   402→        .ifPresent(existingDescriptor::merge)
   403→    );
   404→
   405→    return propertyDescriptorRepository.saveAll(existing);
   406→  }
   407→
   408→  /**
   409→   * Gets a list of all data in a given project index that is not mapped to a property descriptor
   410→   *
   411→   * @param project the project to get the unmapped fields for
   412→   * @return a list of all fields in the project index that are not mapped to a property descriptor
   413→   */
   414→  public List<String> getUnmappedProperties(final Project project) {
   415→    final List<String> allMappings = elasticsearchService.getAllPropertyNames(
   416→      project.getIndexName()
   417→    );
   418→    final List<String> configuredMappings = getConfiguredPropertyDescriptors(
   419→      project
   420→    )
   421→      .stream()
   422→      .map(PropertyDescriptor::getMapping)
   423→      .toList();
   424→    return allMappings
   425→      .stream()
   426→      .filter(mapping -> !configuredMappings.contains(mapping))
   427→      .toList();
   428→  }
   429→
   430→  /**
   431→   * Create default property descriptors from DefaultProperty constants for the given project
   432→   *
   433→   * @param project the project to create the default property descriptors for
   434→   * @return the list of created/existing property descriptors
   435→   */
   436→  @PrefixedCacheEvict
   437→  public List<PropertyDescriptor> createDefaultPropertyDescriptors(
   438→    final Project project
   439→  ) {
   440→    List<PropertyDescriptor> defaultDescriptors =
   441→      DefaultProperty.getAllDescriptors();
   442→    log.info(
   443→      "Creating {} default property descriptors for project {}",
   444→      defaultDescriptors.size(),
   445→      project.getId()
   446→    );
   447→    return create(project, defaultDescriptors);
   448→  }
   449→}
