@Service
@RequiredArgsConstructor
@Slf4j
public class PropertyDescriptorService {

  final ElasticsearchService elasticsearchService;
  final PropertyDescriptorRepository propertyDescriptorRepository;

  /**
   * Get all property descriptors for the given project.
   *
   * @param project The project to get the property descriptors for
   * @return The property descriptors for the project
   */
  public List<PropertyDescriptor> getConfiguredPropertyDescriptors(
    final Project project
  ) {
    return propertyDescriptorRepository.findAllByProject(project);
  }

  /**
   * Page the property descriptors for the given project
   * @param project       the project to get the property descriptors for
   * @param page          the page number
   * @param rows          the number of rows per page
   * @param sortField     the field to sort by
   * @param sortOrder     the sort order
   * @param queryString   the query string to filter by
   * @return              the paged property descriptors for the project
   */
  public Page<PropertyDescriptor> getConfiguredPropertyDescriptors(
    final Project project,
    int page,
    int rows,
    String sortField,
    Integer sortOrder,
    String queryString
  ) {
    return propertyDescriptorRepository.findAll(
      PropertyDescriptorSpecification.filterBy(project, queryString),
      SortedPageRequest.of(sortField, sortOrder, page, rows)
    );
  }

  /**
   * Get the property descriptors that have associated data indexed in the given project
   *
   * @param project The project to get the property descriptors for
   * @return The property descriptors for the project that have associated data indexed
   */
  @PrefixedCacheable
  public List<PropertyDescriptor> getExistingPropertyDescriptors(
    final Project project
  ) {
    final List<PropertyDescriptor> configuredPropertyDescriptors =
      getConfiguredPropertyDescriptors(project);
    if (
      configuredPropertyDescriptors == null ||
      configuredPropertyDescriptors.isEmpty()
    ) {
      return List.of();
    }
    final MsearchRequest request = MsearchRequest.of(
      multiSearchRequestBuilder -> {
        multiSearchRequestBuilder
          .index(project.getIndexName())
          .ignoreUnavailable(true)
          .searches(
            configuredPropertyDescriptors
              .stream()
              .map(propertyDescriptor ->
                RequestItem.of(requestItemBuild ->
                  requestItemBuild
                    .body(bodyBuilder ->
                      bodyBuilder
                        .query(
                          Query.of(queryBuilder ->
                            queryBuilder.exists(existsQueryBuilder ->
                              existsQueryBuilder.field(
                                propertyDescriptor.getMapping()
                              )
                            )
                          )
                        )
                        .size(1)
                        .trackTotalHits(TrackHits.of(v -> v.count(1)))
                    )
                    .header(headerBuilder ->
                      headerBuilder.index(project.getIndexName())
                    )
                )
              )
              .collect(Collectors.toList())
          );
        return multiSearchRequestBuilder;
      }
    );

    final Map<PropertyDescriptor, Boolean> propertyDescriptorToHasValues =
      new HashMap<>();
    final MsearchResponse<JsonNode> response = elasticsearchService.msearch(
      request,
      JsonNode.class
    );
    for (int i = 0; i < response.responses().size(); i++) {
      final MultiSearchResponseItem<JsonNode> multiSearchResponse = response
        .responses()
        .get(i);
      if (multiSearchResponse.isFailure()) {
        final var error = multiSearchResponse.failure().error();
        throw new ResponseStatusException(
          HttpStatus.INTERNAL_SERVER_ERROR,
          "Error determining whether the Elasticsearch field described by a property descriptor exists. " +
          error.type() +
          ": " +
          error.reason()
        );
      } else {
        final boolean hasValues =
          multiSearchResponse.result().hits().total().value() > 0;
        propertyDescriptorToHasValues.put(
          configuredPropertyDescriptors.get(i),
          hasValues
        );
      }
    }

    final List<PropertyDescriptor> descriptors = configuredPropertyDescriptors
      .stream()
      .filter(propertyDescriptorToHasValues::get)
      .collect(Collectors.toList());

    descriptors.addAll(
      configuredPropertyDescriptors
        .stream()
        .filter(pd -> pd.getKey().equals(DefaultProperty.SEARCH_SET.getKey()))
        .toList()
    );

    return descriptors;
  }

  /**
   * Find a property descriptor by its key
   *
   * @param project the configured project
   * @param key     the key of the property descriptor
   * @return the property descriptor with the given key, or null if it does not exist
   */
  public PropertyDescriptor findByKey(final Project project, final String key) {
    return propertyDescriptorRepository
      .findById(PropertyDescriptor.getId(key, project))
      .orElse(null);
  }

  /**
   * Find
   * @param project
   * @param tag
   * @return
   */
  public List<PropertyDescriptor> findAllByTag(
    final Project project,
    final PropertyTag tag
  ) {
    return propertyDescriptorRepository
      .findAllByProject(project)
      .stream()
      .filter(pd -> pd.containsTag(tag))
      .collect(Collectors.toList());
  }

  /**
   * Create a new property descriptor for the given project
   *
   * @param project            the project to create the property descriptor for
   * @param propertyDescriptor the property descriptor to create
   * @return the persisted property descriptor
   */
  @PrefixedCacheEvict
  public PropertyDescriptor create(
    final Project project,
    final PropertyDescriptor propertyDescriptor
  ) {
    propertyDescriptor.setProject(project);
    try {
      final PropertyDescriptor created = propertyDescriptorRepository.save(
        propertyDescriptor
      );
      log.info(
        "Created property descriptor {} for project {}",
        created.getKey(),
        project.getId()
      );
      return created;
    } catch (final Exception e) {
      return null;
    }
  }

  /**
   * Create a list of new property descriptors for the given project
   *
   * @param project             the project to create the property descriptors for
   * @param propertyDescriptors the property descriptors to create
   * @return the persisted property descriptors
   */
  @PrefixedCacheEvict
  public List<PropertyDescriptor> create(
    final Project project,
    final List<PropertyDescriptor> propertyDescriptors
  ) {
    if (propertyDescriptors.isEmpty()) {
      return List.of();
    }

    // Set project and deduplicate input
    Map<String, PropertyDescriptor> uniqueByKey = propertyDescriptors
      .stream()
      .peek(pd -> pd.setProject(project))
      .collect(
        Collectors.toMap(
          PropertyDescriptor::getKey,
          Function.identity(),
          (existing, replacement) -> existing,
          LinkedHashMap::new
        )
      );

    // Get all existing descriptors in ONE query
    List<PropertyDescriptor> existingDescriptors =
      propertyDescriptorRepository.findAllByProjectAndKeyIn(
        project,
        uniqueByKey.keySet()
      );

    Set<String> existingKeys = existingDescriptors
      .stream()
      .map(PropertyDescriptor::getKey)
      .collect(Collectors.toSet());

    // Only create new ones
    List<PropertyDescriptor> toCreate = uniqueByKey
      .values()
      .stream()
      .filter(pd -> !existingKeys.contains(pd.getKey()))
      .collect(Collectors.toList());

    List<PropertyDescriptor> result = new ArrayList<>(existingDescriptors);
    if (!toCreate.isEmpty()) {
      try {
        result.addAll(propertyDescriptorRepository.saveAll(toCreate));
        log.info(
          "Created {} new property descriptors for project {}",
          toCreate.size(),
          project.getId()
        );
      } catch (Exception e) {
        // Handle race condition - some may have been created by another thread
        log.warn(
          "Some property descriptors may have been created by another thread: {}",
          e.getMessage()
        );

        // Query again to get the ones that were created by other threads
        List<PropertyDescriptor> allDescriptors =
          propertyDescriptorRepository.findAllByProjectAndKeyIn(
            project,
            uniqueByKey.keySet()
          );
        result = allDescriptors;
      }
    }

    log.info(
      "Found {} existing, ensured {} total property descriptors for project {}",
      existingKeys.size(),
      result.size(),
      project.getId()
    );

    return result;
  }

  /**
   * Remove a property descriptor from the given project
   *
   * @param project the project to remove the property descriptor from
   * @param key     the key of the property descriptor to remove
   */
  @PrefixedCacheEvict
  public void remove(final Project project, final String key) {
    propertyDescriptorRepository.deleteById(
      PropertyDescriptor.getId(key, project)
    );
    log.info(
      "Deleted property descriptor {} for project {}",
      key,
      project.getId()
    );
  }

  /**
   * Update a property descriptor
   *
   * @param project            the project to update the property descriptor for
   * @param propertyDescriptor the property descriptor to update
   * @return the updated property descriptor
   */
  @PrefixedCacheEvict
  public PropertyDescriptor update(
    final Project project,
    final PropertyDescriptor propertyDescriptor
  ) {
    final PropertyDescriptor existing = propertyDescriptorRepository
      .findById(PropertyDescriptor.getId(propertyDescriptor.getKey(), project))
      .orElse(null);
    if (existing == null) {
      throw new IllegalArgumentException(
        "No property descriptor with the given id exists"
      );
    }
    existing.merge(propertyDescriptor);
    final PropertyDescriptor updated = propertyDescriptorRepository.save(
      existing
    );
    log.info(
      "Updated property descriptor {} for project {}",
      updated.getKey(),
      project.getId()
    );
    return updated;
  }

  /**
   * Update a list of property descriptors
   *
   * @param project             the project to update the property descriptors for
   * @param propertyDescriptors the property descriptors to update
   * @return the updated property descriptors
   */
  @PrefixedCacheEvict
  public List<PropertyDescriptor> update(
    final Project project,
    final List<PropertyDescriptor> propertyDescriptors
  ) {
    final List<PropertyDescriptor> existing =
      propertyDescriptorRepository.findAllById(
        propertyDescriptors
          .stream()
          .map(propertyDescriptor ->
            PropertyDescriptor.getId(propertyDescriptor.getKey(), project)
          )
          .toList()
      );
    if (existing.size() != propertyDescriptors.size()) {
      throw new IllegalArgumentException("Not all property descriptors exist");
    }

    existing.forEach(existingDescriptor ->
      propertyDescriptors
        .stream()
        .filter(propertyDescriptor ->
          propertyDescriptor.getKey().equals(existingDescriptor.getKey())
        )
        .findFirst()
        .ifPresent(existingDescriptor::merge)
    );

    return propertyDescriptorRepository.saveAll(existing);
  }

  /**
   * Gets a list of all data in a given project index that is not mapped to a property descriptor
   *
   * @param project the project to get the unmapped fields for
   * @return a list of all fields in the project index that are not mapped to a property descriptor
   */
  public List<String> getUnmappedProperties(final Project project) {
    final List<String> allMappings = elasticsearchService.getAllPropertyNames(
      project.getIndexName()
    );
    final List<String> configuredMappings = getConfiguredPropertyDescriptors(
      project
    )
      .stream()
      .map(PropertyDescriptor::getMapping)
      .toList();
    return allMappings
      .stream()
      .filter(mapping -> !configuredMappings.contains(mapping))
      .toList();
  }

  /**
   * Create default property descriptors from DefaultProperty constants for the given project
   *
   * @param project the project to create the default property descriptors for
   * @return the list of created/existing property descriptors
   */
  @PrefixedCacheEvict
  public List<PropertyDescriptor> createDefaultPropertyDescriptors(
    final Project project
  ) {
    List<PropertyDescriptor> defaultDescriptors =
      DefaultProperty.getAllDescriptors();
    log.info(
      "Creating {} default property descriptors for project {}",
      defaultDescriptors.size(),
      project.getId()
    );
    return create(project, defaultDescriptors);
  }
}

