/**
 * Close the given index
 *
 * @param index The index to close
 * @return The acknowledged response
 */
public AcknowledgedResponse closeIndex(final String index) {
  try {
    return client.indices().close(d -> d.index(index));
  } catch (final IOException e) {
    log.error("Error closing index {}", index, e);
  }
  return null;
}

/**
 * Refresh the given index
 *
 * @param index The index to refresh
 */
public void refresh(final String index) {
  try {
    client.indices().refresh(r -> r.index(index).ignoreUnavailable(true));
  } catch (final IOException e) {
    log.error("Error refreshing index {}", index, e);
  }
}

/**
 * Insert the given documents into the given index with the given ids
 *
 * @param index     The index to insert the documents into
 * @param documents the documents to insert
 * @param ids       The ids of the documents to insert, parallel to the documents
 * @return The bulk response
 * @throws IOException              If the bulk insert fails
 * @throws IllegalArgumentException If the number of documents and ids are not the same
 */
public BulkResponse insert(
  final String index,
  final List documents,
  final List<String> ids
) throws IOException {
  if (ids != null && documents.size() != ids.size()) {
    throw new IllegalArgumentException(
      "The number of documents and ids must be the same"
    );
  }

  final List<BulkOperation> bulkOperations = new ArrayList<>();
  for (int i = 0; i < documents.size(); i++) {
    final String id = ids != null ? ids.get(i) : UUID.randomUUID().toString();
    final Object document = documents.get(i);
    bulkOperations.add(
      BulkOperation.of(b ->
        b.index(op -> op.index(index).id(id).document(document))
      )
    );
  }

  log.info(
    "Elasticsearch | Bulk | Inserting {} documents into index {}",
    documents.size(),
    index
  );
  return client.bulk(
    BulkRequest.of(bulkRequest ->
      bulkRequest
        .index(index)
        .operations(bulkOperations)
        .pipeline("full_text_search")
    )
  );
}

