public boolean putTemplate(final String name, final String templateJson) {
  return putTyped(name, templateJson, "template", "_template");
}

/**
 * Returns a list of template names that match the given index based on the pattern specified
 * in the template
 *
 * @param index           The index to match templates for
 * @param templatePattern The (optional) pattern to filter template names against
 * @return The list of matching template names
 */
public List<String> getMatchingTemplates(
  final String index,
  final String templatePattern
) {
  final List<String> matching = new ArrayList<>();
  final Map<String, List<String>> templateIndexPatterns =
    getTemplateIndexPatterns();
  final Pattern templateNamePattern = Pattern.compile(
    templatePattern != null ? templatePattern.replace("*", ".*") : ".*"
  );
  templateIndexPatterns.forEach((templateName, indexPatterns) -> {
    if (templateNamePattern.matcher(templateName).matches()) {
      indexPatterns
        .stream()
        .map(pattern -> pattern.replace("*", ".*"))
        .forEach(pattern -> {
          if (Pattern.compile(pattern).matcher(index).matches()) {
            matching.add(templateName);
          }
        });
    }
  });
  return matching;
}

