private boolean putTyped(
  final String name,
  final String typedJson,
  final String typeName,
  final String indexName
) {
  log.info("Putting " + typeName + ": {}", name);

  try {
    final HttpHeaders headers = new HttpHeaders();
    headers.setContentType(MediaType.APPLICATION_JSON);
    final HttpEntity<String> entity = new HttpEntity<>(typedJson, headers);
    final ResponseEntity<JsonNode> response = getRestTemplate()
      .exchange(
        new URI(url + "/" + indexName + "/" + name),
        HttpMethod.PUT,
        entity,
        JsonNode.class
      );
    final JsonNode body = response.getBody();
    if (body != null) {
      return body.at("/acknowledged").asBoolean();
    }
  } catch (final Exception e) {
    log.error("Error putting " + typeName + " {}", name, e);
  }
  return false;
}

public boolean putComponentTemplate(
  final String name,
  final String templateJson
) {
  return putTyped(
    name,
    appendHash(templateJson),
    "component template",
    "_component_template"
  );
}

private String appendHash(String templateJson) {
  try {
    final String hash = hash(templateJson);
    final ObjectNode templateNode = (ObjectNode) mapper.readTree(
      templateJson
    );
    ObjectNode metaNode;
    if (templateNode.has("_meta") && templateNode.get("_meta").isObject()) {
      metaNode = (ObjectNode) templateNode.get("_meta");
    } else {
      metaNode = templateNode.putObject("_meta");
    }
    metaNode.put("hash", hash);
    return mapper.writeValueAsString(templateNode);
  } catch (final JsonProcessingException e) {
    throw new RuntimeException(e);
  }
}

public JsonNode getTemplate(final String name, final TEMPLATE_TYPE type) {
  try {
    final ResponseEntity<JsonNode> response = getRestTemplate()
