@Service
@Slf4j
@RequiredArgsConstructor
public class ElasticsearchService {

  private final RestTemplateBuilder restTemplateBuilder;
  private final ObjectMapper mapper;

  @Value("${elasticsearch.url}")
  private String url;

  @Value("${elasticsearch.username:}")
  private String username;

  @Value("${elasticsearch.password:}")
  private String password;

  private RestTemplate restTemplate;

  @Getter
  private ElasticsearchClient client = null;

  private static final String SCROLL_TIME = "5m";

  private static final Map<String, TaskType> TASK_TYPES = new HashMap<>();

  static {
    TASK_TYPES.put("indices:data/write/reindex", REINDEX);
    TASK_TYPES.put("indices:data/write/delete/byquery", DELETE_BY_QUERY);
    TASK_TYPES.put("indices:data/write/update/byquery", UPDATE_BY_QUERY);
  }

  @Getter
  public enum TEMPLATE_TYPE {
    INDEX("index_template"),
    COMPONENT("component_template");

    private final String name;

    TEMPLATE_TYPE(final String name) {
      this.name = name;
    }

    public String getName() {
      return name;
    }

    public String getEndpoint() {
      return "_" + name;
    }

    public String getJacksonAccessor() {
      return "/" + name + "s/0/" + name;
    }
  }

  @PostConstruct
  public void init() {
    final RestClientBuilder httpClientBuilder = RestClient.builder(
      HttpHost.create(url)
    );
    if (hasAuthentication()) {
      httpClientBuilder.setDefaultHeaders(
        new Header[] {
          new BasicHeader(
            "Authorization",
            "Basic " +
            Base64.getEncoder()
              .encodeToString((username + ":" + password).getBytes())
          ),
        }
      );
    }

    // Create the new Java Client with the same low level client
    final ElasticsearchTransport transport = new RestClientTransport(
      httpClientBuilder.build(),
      new JacksonJsonpMapper(mapper)
    );

    client = new ElasticsearchClient(transport);
    try {
      client.ping();
    } catch (final IOException e) {
      log.error("Error pinging Elasticsearch", e);
    }
  }

  protected RestTemplate getRestTemplate() {
    if (restTemplate == null) {
      initRestTemplate();
    }
    return restTemplate;
  }

  public ResponseEntity<JsonNode> executeWithRestClient(
    final String endpoint,
    final HttpMethod method,
    final HttpEntity<?> entity
  ) {
    final URI uri = URI.create(
      url + (endpoint.startsWith("/") ? endpoint : "/" + endpoint)
    );
    return getRestTemplate().exchange(uri, method, entity, JsonNode.class);
  }

  private void initRestTemplate() {
    if (hasAuthentication()) {
      restTemplate = restTemplateBuilder
        .basicAuthentication(username, password)
        .build();
    } else {
      restTemplate = restTemplateBuilder.build();
    }
  }

  private String hash(String input) {
    try {
      MessageDigest digest = MessageDigest.getInstance("SHA-256");
      byte[] hash = digest.digest(input.getBytes(StandardCharsets.UTF_8));
      StringBuilder hexString = new StringBuilder();
      for (byte b : hash) {
        String hex = Integer.toHexString(0xff & b);
        if (hex.length() == 1) hexString.append('0');
        hexString.append(hex);
      }
      return hexString.toString();
    } catch (NoSuchAlgorithmException e) {
      throw new RuntimeException("SHA-256 hashing algorithm not available", e);
    }
  }

  /**
   * Checks if we need to connect to Elasticsearch with authentication
   *
   * @return True if we need to connect with authentication, false otherwise
   */
  boolean hasAuthentication() {
    return (
      username != null &&
      password != null &&
      !username.isEmpty() &&
      !password.isEmpty()
    );
  }

  /**
