  /**
   * Search for documents using a {@link SearchRequest} instance
   *
   * @param request The search request
   * @param clazz   The class of the documents to return
   * @param <T>     The type of the documents to return
   * @return The search response
   */
  public <T> SearchResponse<T> search(
    final SearchRequest request,
    final Class<T> clazz
  ) {
    try {
      log.info("Elasticsearch | Query | {}", request);
      return client.search(request, clazz);
    } catch (final Exception e) {
      if (e instanceof ElasticsearchException) {
        final ErrorCause firstCause =
          ((ElasticsearchException) e).response()
            .error()
            .rootCause()
            .getFirst();
        if (firstCause != null) {
          log.error(
            "Error searching index {} | {}: {}",
            request.index(),
            firstCause.type(),
            firstCause.reason(),
            e
          );
        }
      } else {
        log.error("Error searching index {}", request.index(), e);
      }
    }
    return null;
  }

