  /**
   * Scroll through the given index and produce batches of documents.  Example:
   * <pre>
   *   final SearchRequest request = SearchRequest.of(b -> b
   *    .index("books")
   *    .query(Query.of(q -> q.matchAll(m -> m)))
   *    .sort(Sort.of(s -> s.field("title")))
   *    .size(100));
   *
   *  elasticsearchService.scroll(request, Book.class, batch -> {
   *     batch.forEach(book -> // do something with the book);
   *   });
   * </pre>
   * <p>
   * <p>
   * NOTE: The scroll API is not implemented in the Java client, so we have to use the rest client to implement this. The
   * recommended way of using search_after only works when you have a sort field that is unique across all documents.
   * Since we cannot assume this for all datasets, we have to use the scroll API.
   *
   * @param request  The {@link SearchRequest} to use for the scroll. The size field indicates the number of documents
   *                 to return in each batch
   * @param clazz    The class of the documents to return
   * @param producer The producer to call with each batch of documents and for error handling
   * @param <T>      The type of the documents to return
   */
  public <T> void scroll(
    final SearchRequest request,
    final Class<T> clazz,
    final BatchProducer<List<Hit<T>>> producer
  ) throws IOException {
    if (request.sort() == null || request.sort().isEmpty()) {
      throw new IllegalArgumentException("Scrolling requires a sort field");
    }

    // Scroll through the documents
    final AtomicReference<String> scrollIdRef = new AtomicReference<>();
    boolean done = false;
    do {
      final int size = request.size() != null ? request.size() : 100;
      final SearchRequest.Builder copy = new SearchRequest.Builder()
        .size(size)
        .sort(request.sort())
        .index(request.index())
        .scroll(t -> t.time(SCROLL_TIME))
        .query(
          request.query() != null
            ? request.query()
            : Query.of(q -> q.matchAll(m -> m))
        );

      if (request.source() != null) {
        copy.source(request.source());
      }

      final ResponseBody<T> response;
      if (scrollIdRef.get() == null) {
        response = getClient().search(copy.build(), clazz);
      } else {
        response = getClient()
          .scroll(
            ScrollRequest.of(b ->
              b.scrollId(scrollIdRef.get()).scroll(s -> s.time(SCROLL_TIME))
            ),
            clazz
          );
      }

      final List<Hit<T>> batch = response.hits().hits().stream().toList();
      producer.onBatch(batch);
      if (response.hits().hits().size() < size) {
        done = true;
        if (scrollIdRef.get() != null) {
          getClient().clearScroll(b -> b.scrollId(scrollIdRef.get()));
        }
      } else {
        scrollIdRef.set(response.scrollId());
      }
    } while (!done);
  }

