    throws IOException {
    return insert(index, documents, null);
  }

  /**
   * Upsert the given documents into the given index with the given ids
   *
   * @param index     The index to upsert the documents into
   * @param documents The documents to upsert
   * @param ids       The ids of the documents to upsert, parallel to the documents
   * @return The bulk response
   */
  public BulkResponse upsert(
    final String index,
    final List documents,
    final List<String> ids
  ) {
    if (ids != null && documents.size() != ids.size()) {
      throw new IllegalArgumentException(
        "The number of documents and ids must be the same"
      );
    }

    final List<BulkOperation> bulkOperations = new ArrayList<>();
    for (int i = 0; i < documents.size(); i++) {
      final String id = ids != null ? ids.get(i) : UUID.randomUUID().toString();
      final Object document = documents.get(i);
      bulkOperations.add(
        BulkOperation.of(b ->
          b.update(op ->
            op
              .index(index)
              .id(id)
              .action(ua -> ua.doc(document).docAsUpsert(true))
          )
        )
      );
    }

    log.info(
      "Elasticsearch | Bulk | Upserting {} documents into index {}",
      documents.size(),
      index
    );

    BulkResponse bulkResponse = null;
    try {
      // Perform the upsert operation
      bulkResponse = client.bulk(
        BulkRequest.of(bulkRequest ->
          bulkRequest.index(index).operations(bulkOperations)
        )
      );

      // Refresh to ensure documents are searchable immediately
      refresh(index);

      if (ids != null && !ids.isEmpty()) {
        refreshFullTextSearchForDocuments(index, ids);
      }
    } catch (final IOException e) {
      log.error("Error upserting documents", e);
    }
    return bulkResponse;
  }

  /**
   * Helper method to refresh full_text_search for specific documents
   */
  private void refreshFullTextSearchForDocuments(
    final String index,
    final List<String> documentIds
  ) {
    try {
      // Build query to match only the specified document IDs
      final String idsQuery = String.format(
        "{\"ids\": {\"values\": [%s]}}",
        documentIds
          .stream()
          .map(id -> "\"" + id + "\"")
          .collect(Collectors.joining(", "))
      );

      // Use updateByQuery with the full_text_search pipeline
      String taskId = updateByQuery(index, idsQuery, "full_text_search");

      log.info(
        "Started full_text_search refresh for {} documents, task: {}",
        documentIds.size(),
        taskId
      );
    } catch (Exception e) {
      log.error(
        "Failed to refresh full_text_search for upserted documents: {}",
        e.getMessage()
      );
    }
  }

