  public static String generateTemplateName(final String json) {
    try {
      // Compute SHA-256 hash of the JSON string
      final MessageDigest digest = MessageDigest.getInstance("SHA-256");
      final byte[] hash = digest.digest(json.getBytes(StandardCharsets.UTF_8));
      // Convert the byte array to a hexadecimal string
      final StringBuilder hexString = new StringBuilder();
      for (final byte b : hash) {
        final String hex = Integer.toHexString(0xff & b);
        if (hex.length() == 1) hexString.append('0');
        hexString.append(hex);
      }
      // Prefix the hexadecimal string with a project-specific name
      return hexString.toString();
    } catch (final NoSuchAlgorithmException e) {
      throw new RuntimeException("SHA-256 hashing algorithm not available", e);
    }
  }

