  /**
   * Get the number of documents in the project
   * @param project The project to get the document count for
   * @return        The number of documents in the project
   */
  @PrefixedCacheable
  public Long getDocumentCount(final Project project) {
    final User currentUser = currentUserService.get();
    if (
      currentUser != null &&
      !projectService.hasPermission(
        project,
        currentUser,
        ProjectPermissionLevel.READ
      )
    ) {
      throw new AccessDeniedException(
        String.format(
          "User %s does not have permission to get document count for project %s",
          currentUser.getId(),
          project.getId()
        )
      );
    }
    return elasticsearchService.count(project.getIndexName());
  }

  /**
   * Get the number of documents in the project based on a search request.
   * @param project         the project to get the document count for
   * @param searchRequest   the search request to use for filtering the documents
   * @return                the full document count if the search request is null, otherwise the count of documents matching the search request
   */
  @PrefixedCacheable
  public Long getDocumentCount(
    final Project project,
    final PanteraSearchRequest searchRequest
  ) {
    if (searchRequest == null) {
      return getDocumentCount(project);
    }
    final SearchRequest request = searchRequestService
      .builder(project, searchRequest)
      .trackScores(false)
      .trackTotalHits(b -> b.enabled(true))
      .size(0)
      .build();

    final SearchResponse<Void> response = elasticsearchService.search(
      request,
      Void.class
    );
    if (response.hits() == null || response.hits().total() == null) {
      return 0L;
    }
    return response.hits().total().value();
  }

