  /**
   * Search for documents in the given project. The query defaults to a match all query and the results
   * should be used in a table.
   * @param project The project to search
   * @param request The search request
   */
  @PrefixedCacheable
  public ElasticsearchPage<PanteraDocument> search(
    final Project project,
    final PanteraSearchRequest request
  ) {
    final List<PropertyDescriptor> projectProperties =
      propertyDescriptorService.getConfiguredPropertyDescriptors(project);
    if (projectProperties == null) {
      throw new IllegalArgumentException(
        "Project %s does not have any properties".formatted(project.getName())
      );
    }

    final SearchRequest searchRequest = searchRequestService.build(
      project,
      request
    );
    final SearchResponse<JsonNode> response = elasticsearchService.search(
      searchRequest,
      JsonNode.class
    );

    // Create the pageable
    final List<ElasticsearchPageableSort> sorts = List.of(
      new ElasticsearchPageableSort()
        .setDirection(Sort.Direction.ASC.toString())
        .setProperty(request.getSort())
    );
    return new ElasticsearchPage<PanteraDocument>()
      .setContent(
        response
          .hits()
          .hits()
          .stream()
          .map(hit ->
            documentParsingService.parse(
              hit.id(),
              hit.score(),
              hit.source(),
              projectProperties
            )
          )
          .collect(Collectors.toList())
      )
      .setPageable(
        new ElasticsearchPageable()
          .setPageNumber(request.getPage())
          .setPageSize(request.getSize())
          .setSort(sorts)
      )
      .setNumber(request.getPage())
      .setSize(request.getSize())
      .setTotalElements(response.hits().total().value())
      .setTotalPages(
        (int) Math.ceil(
          (double) response.hits().total().value() / request.getSize()
        )
      )
      .setFirst(request.getPage() == 0)
      .setLast(
        request.getPage() == response.hits().total().value() / request.getSize()
      )
      .setNumber(request.getPage())
      .setSort(sorts)
      .setNumberOfElements(response.hits().hits().size())
      .setFirst(request.getPage() == 0)
      .setEmpty(response.hits().hits().isEmpty());
  }

  @PrefixedCacheable
  public PanteraDocument getDocument(
    final Project project,
    final String documentId
  ) {
    final List<PropertyDescriptor> projectProperties =
      propertyDescriptorService.getConfiguredPropertyDescriptors(project);
    if (projectProperties == null) {
      throw new IllegalArgumentException(
        "Project %s does not have any properties".formatted(project.getName())
      );
    }
    final GetResponse<JsonNode> response = elasticsearchService.getDocument(
      project.getIndexName(),
      documentId,
      JsonNode.class
    );
    return documentParsingService.parse(
      response.id(),
      0d, //not needed here
      response.source(),
      projectProperties
    );
  }

  @PrefixedCacheEvict
  public void deleteDocument(final Project project, final String documentId) {
    elasticsearchService.deleteDocument(project.getIndexName(), documentId);
  }

  @PrefixedCacheable
  public Map<String, Map<AggregateOperation, Number>> aggregate(
    final Project project,
    final PanteraSearchRequest panteraSearchRequest,
    final String xField,
    final String yField,
    final List<AggregateOperation> operations
  ) {
    final PropertyDescriptor xFieldDescriptor =
      propertyDescriptorService.findByKey(project, xField);
    final PropertyDescriptor yFieldDescriptor =
      propertyDescriptorService.findByKey(project, yField);

    final AggregateTermsType xType = getAggregateTermsType(xFieldDescriptor);
    if (xType == null) {
      throw new IllegalArgumentException(
        "Field %s is not a valid field for aggregation".formatted(xField)
      );
    }

    final AggregateTermsType yType = getAggregateTermsType(yFieldDescriptor);
    if (yType == null) {
      throw new IllegalArgumentException(
        "Field %s is not a valid field for aggregation".formatted(yField)
      );
    }

    final SearchRequest.Builder searchRequestBuilder =
      searchRequestService.builder(project, panteraSearchRequest);
    searchRequestBuilder.aggregations("x", aggregationBuilder -> {
      final Aggregation.Builder.ContainerBuilder builder =
        aggregationBuilder.terms(termsBuilder ->
          termsBuilder.field(xFieldDescriptor.getMapping()).size(1000)
        );

      for (final AggregateOperation operation : operations) {
        if (operation == AggregateOperation.COUNT) {
          continue;
        }

        final Aggregation a = Aggregation.of(subBuilder ->
          switch (operation) {
            case UNIQUE -> subBuilder.cardinality(b ->
              b.field(yFieldDescriptor.getMapping())
            );
            case SUM -> subBuilder.sum(b ->
              b.field(yFieldDescriptor.getMapping())
            );
            case AVERAGE -> subBuilder.avg(b ->
              b.field(yFieldDescriptor.getMapping())
            );
            case MIN -> subBuilder.min(b ->
              b.field(yFieldDescriptor.getMapping())
            );
            case MAX -> subBuilder.max(b ->
              b.field(yFieldDescriptor.getMapping())
            );
            case UPPER_QUARTILE, LOWER_QUARTILE -> subBuilder.percentiles(b ->
              b.field(yFieldDescriptor.getMapping())
            );
            default -> throw new IllegalStateException(
              "Unexpected value: " + operation
            );
          }
        );
        builder.aggregations("y" + operation.toString(), a);
      }

      return builder;
    });

    final SearchResponse<JsonNode> response = elasticsearchService.search(
      searchRequestBuilder.build(),
      JsonNode.class
    );

    return switch (xType) {
      case STRING -> parse(
        response.aggregations().get("x").sterms().buckets().array(),
        operations
      );
      case LONG -> parse(
        response.aggregations().get("x").lterms().buckets().array(),
        operations
      );
      case DOUBLE -> parse(
        response.aggregations().get("x").dterms().buckets().array(),
        operations
      );
    };
  }

  private <T extends TermsBucketBase> Map<
    String,
    Map<AggregateOperation, Number>
  > parse(final List<T> buckets, final List<AggregateOperation> operations) {
    final Map<String, Map<AggregateOperation, Number>> result = new HashMap<>();
    for (final T bucket : buckets) {
      final Map<AggregateOperation, Number> values = new HashMap<>();
      if (operations.contains(AggregateOperation.COUNT)) {
        values.put(AggregateOperation.COUNT, bucket.docCount());
      }
      for (final AggregateOperation operation : operations) {
        values.put(
          operation,
          switch (operation) {
            case SUM -> bucket
              .aggregations()
              .get("y" + operation)
              .sum()
              .value();
            case AVERAGE -> bucket
              .aggregations()
              .get("y" + operation)
              .avg()
              .value();
            case COUNT -> bucket.docCount();
            case UNIQUE -> bucket
              .aggregations()
              .get("y" + operation)
              .cardinality()
              .value();
            case MIN -> bucket
              .aggregations()
              .get("y" + operation)
              .min()
              .value();
            case MAX -> bucket
              .aggregations()
              .get("y" + operation)
              .max()
              .value();
            case UPPER_QUARTILE -> Double.valueOf(
              bucket
                .aggregations()
                .get("y" + operation)
                .tdigestPercentiles()
                .values()
                .keyed()
                .get("75.0")
            );
            case LOWER_QUARTILE -> Double.valueOf(
              bucket
                .aggregations()
                .get("y" + operation)
                .tdigestPercentiles()
                .values()
                .keyed()
                .get("25.0")
            );
          }
        );
      }
      final String key =
        switch (bucket) {
          case final StringTermsBucket stringBucket -> stringBucket
            .key()
            .stringValue();
          case final LongTermsBucket longBucket -> "" + longBucket.key();
          case final DoubleTermsBucket doubleBucket -> "" + doubleBucket.key();
          default -> throw new IllegalArgumentException(
            "Unknown bucket type %s".formatted(bucket.getClass().getName())
          );
        };

      result.put(key, values);
    }
    return result;
  }

  private AggregateTermsType getAggregateTermsType(
    final PropertyDescriptor descriptor
  ) {
    if (descriptor.getMapping().startsWith("long")) {
      return AggregateTermsType.LONG;
    } else if (descriptor.getMapping().startsWith("double")) {
      return AggregateTermsType.DOUBLE;
    } else if (descriptor.getMapping().startsWith("keyword")) {
      return AggregateTermsType.STRING;
    } else {
      throw new IllegalArgumentException(
        "Unknown mapping type %s".formatted(descriptor.getMapping())
      );
    }
  }

  /**
   * Gets the locations for the given project
   * @param project   The project to get the locations for
   * @param request   The search request
   * @param layers    The layers to get the locations for
   * @return          The location response for the location counts in each layer
   */
  @PrefixedCacheable
  public LocationResponse getLocations(
    final Project project,
    final PanteraSearchRequest request,
    final List<String> layers
  ) {
    final String locationFeaturePrefix = "location_feature.";
    request.setSize(0).setTrackScores(false).setTrackTotalHits(true);

    final SearchRequest.Builder builder = searchRequestService.builder(
      project,
      request
    );

    // Add an aggregation for each layer that also includes the exemplar location so we can get the lat/lon for each location
    // and collect unique document IDs for each location
    layers.forEach(layer ->
      builder.aggregations(layer, b ->
        b
          .terms(tb ->
            tb
              .field(layer + ".label")
              .size(1000)
              .missing(AggregateProperty.NOT_AVAILABLE)
          )
          .aggregations("exemplar", eb ->
            eb.topHits(thb ->
              thb
                .size(1)
                .source(s ->
                  s.filter(fb ->
                    fb.includes(List.of(layer + ".location", layer + ".label"))
                  )
                )
            )
          )
          .aggregations(
            "unique_docs",
            ub -> ub.terms(tb -> tb.field("id").size(1000)) // Collect unique document IDs
          )
      )
    );

    final SearchResponse<JsonNode> response = elasticsearchService.search(
      builder.build(),
      JsonNode.class
    );

    // Parse the result
    final Map<String, List<LocationCount>> layerCounts = new HashMap<>();
    layers.forEach(layer -> {
      final List<LocationCount> layerResult = new ArrayList<>();
      final String layerPath =
        "/location_feature/" +
        layer.substring(locationFeaturePrefix.length(), layer.length());
      response
        .aggregations()
        .get(layer)
        .sterms()
        .buckets()
        .array()
        .forEach(bucket -> {
          final String locationKey = bucket.key().stringValue();
          final long count = bucket.docCount(); // Keep original location count

          // Extract unique document IDs from the unique_docs aggregation
          final List<String> docIds = bucket
            .aggregations()
            .get("unique_docs")
            .sterms()
            .buckets()
            .array()
            .stream()
            .map(docBucket -> docBucket.key().stringValue())
            .toList();

          bucket
            .aggregations()
            .get("exemplar")
            .topHits()
            .hits()
            .hits()
            .forEach(hit -> {
              final JsonData source = hit.source();

              if (source != null) {
                final JsonNode hitNode = source.to(JsonNode.class);
                final JsonNode arrayNode = hitNode.at(layerPath);
                if (arrayNode.isArray()) {
                  for (final JsonNode node : arrayNode) {
                    final JsonNode labelNode = node.get("label");
                    if (
                      labelNode != null &&
                      locationKey.equals(labelNode.asText())
                    ) {
                      final Double lon = node.at("/location/lon").asDouble();
                      final Double lat = node.at("/location/lat").asDouble();

                      final LocationCount locationCount =
                        (LocationCount) new LocationCount()
                          .setCount(count)
                          .setDocIds(docIds)
                          .setLocation(new LatLng().setLat(lat).setLng(lon))
                          .setLabel(bucket.key().stringValue());
                      layerResult.add(locationCount);
                    }
                  }
                } else {
                  final LocationCount locationCount =
                    (LocationCount) new LocationCount()
                      .setCount(count)
                      .setDocIds(docIds)
                      .setLocation(null)
                      .setLabel(bucket.key().stringValue());
                  layerResult.add(locationCount);
                }
              }
            });
        });
      layerCounts.put(layer, layerResult);
    });

    return new LocationResponse()
      .setLayers(layerCounts)
      .setTotal(response.hits().total().value());
  }

  /**
   * Gets the Georeferences for the given project
   * @param project   The project to get the locations for
   * @param request   The search request
   * @return          The location response for the location counts in each layer
   */
  @PrefixedCacheable
  public GeoreferencingResponse getGeoreferencings(
    final Project project,
    final PanteraSearchRequest request
  ) {
    final String georeferencingFeaturePrefix =
      LARAGeoreferencedResourceFeature.LARA_GEOREFERENCED_RESOURCE_FEATURE;
    request.setTrackScores(false).setTrackTotalHits(true);

    final SearchRequest.Builder builder = searchRequestService.builder(
      project,
      request
    );

    builder.source(
      SourceConfig.of(s ->
        s.filter(f -> f.includes(georeferencingFeaturePrefix))
      )
    );

    builder.size(1000);

    final SearchResponse<JsonNode> response = elasticsearchService.search(
      builder.build(),
      JsonNode.class
    );

    final List<Hit<JsonNode>> hits = response.hits().hits();
    final LARAGeoreferencedResourceFeatureValidator validator =
      new LARAGeoreferencedResourceFeatureValidator();
    final Map<String, List<Georeferencing>> layersOut = new HashMap<>();
    for (final Hit<JsonNode> hit : hits) {
      if (hit.source() == null) {
        continue;
      }
      final JsonNode source = hit.source().get(georeferencingFeaturePrefix);
      if (source == null) {
        continue;
      }
      for (
        final Iterator<Map.Entry<String, JsonNode>> it = source.fields();
        it.hasNext();
      ) {
        final Map.Entry<String, JsonNode> georefLayer = it.next();
        final ArrayList<JsonNode> georefLayerVals = StreamSupport.stream(
          Spliterators.spliteratorUnknownSize(
            georefLayer.getValue().elements(),
            Spliterator.ORDERED
          ),
          false
        ).collect(Collectors.toCollection(ArrayList::new));
        georefLayerVals.removeIf(val -> val == null || val.isNull());
        final LARAGeoreferencedResourceFeature georefFeature = validator.parse(
          georefLayerVals,
          georefLayer.getKey(),
          null
        );
        layersOut.putIfAbsent(georefLayer.getKey(), new ArrayList<>());
        final var layersOutList = layersOut.get(georefLayer.getKey());
        if (georefFeature.getValue() instanceof List) {
          final var georefs = (List<Georeferencing>) georefFeature.getValue();
          layersOutList.addAll(georefs);
        } else {
          final var georef = (Georeferencing) georefFeature.getValue();
          layersOutList.add(georef);
        }
      }
    }
    return new GeoreferencingResponse().setLayers(layersOut);
  }

  /**
   * Updates the document with the given id in the given project with the new georeferences
   * @param project                   the project to update the document in
   * @param docId                     the id of the document to update
   * @param updatedGeoreferenceLayers the map of the georeference layers with the values as the updated georeference points
   */
  @PrefixedCacheEvict
  public PanteraDocumentUpdateResponse updateGeoreferences(
    final Project project,
    final String docId,
    final Map<String, List<Georeferencing>> updatedGeoreferenceLayers
  ) {
    final ObjectNode objectNode = objectMapper.createObjectNode();

    objectNode.set(
      LARAGeoreferencedResourceFeature.LARA_GEOREFERENCED_RESOURCE_FEATURE,
      objectMapper.convertValue(updatedGeoreferenceLayers, JsonNode.class)
    );

    return updateProjectDocument(project, docId, List.of(objectNode));
  }

  /**
   * Replaces image with map in the markdown content of the document
   * @param project          the project to update the document in
   * @param docId            the id of the document to update
   * @param imageFileName    the image filename to match
   * @param mapId            the map ID to replace with
   * @return the updated document response
   */
  @PrefixedCacheEvict
  public PanteraDocumentUpdateResponse replaceImageWithMapInMarkdown(
    final Project project,
    final String docId,
    final String imageFileName,
    final String mapId
  ) {
    final GetResponse<JsonNode> doc = elasticsearchService.getDocument(
      project.getIndexName(),
      docId,
      JsonNode.class
    );
    final String markdownKey = DefaultProperty.MARKDOWN.getKey();
    final String markdownMapping = propertyDescriptorService
      .findByKey(project, markdownKey)
      .getMapping();
    final String markdownPath = "/" + markdownMapping.replace(".", "/");

    // Extract markdown content as List<String>
    List<String> markdownContent = new ArrayList<>();
    if (doc.found() && doc.source() != null) {
      JsonNode markdownNode = doc.source().at(markdownPath);
      if (markdownNode != null && markdownNode.isArray()) {
        // Convert JsonNode array to List<String>
        markdownContent = StreamSupport.stream(
          markdownNode.spliterator(),
          false
        )
          .map(JsonNode::asText)
          .map(content ->
            MarkdownUtil.replaceImageWithMap(content, imageFileName, mapId)
          )
          .collect(Collectors.toCollection(ArrayList::new));
      }
    }

    final Map<String, List<String>> payload = new HashMap<>();
    payload.put(markdownKey, markdownContent);

    return this.update(project, docId, payload);
  }

  /**
   * Get the available location layers for the given project
   * @param project
   * @return
   */
  @PrefixedCacheable
  public Set<String> getLocationLayers(final Project project) {
    final List<String> locationFieldMappings =
      elasticsearchService.getAllPropertyNames(
        project.getIndexName(),
        "location_feature*"
      );
    return locationFieldMappings
      .stream()
      .map(mapping -> mapping.split("\\.")[1])
      .collect(Collectors.toSet());
  }

  /**
   * Helper method to get the facets for the given project, search request, and keys. The default count is used for each key.
   * @param project   the project to get the facets for
   * @param request   the search request to use
   * @param keys      the {@link PropertyDescriptor} keys to get facets for. Only properties with the {@link PropertyTag#FACET} tag are used.
   * @return          the {@link FacetsResult}
   */
  @PrefixedCacheable
  public FacetsResult getFacets(
    final Project project,
    final PanteraSearchRequest request,
    final Collection<String> keys
  ) {
    return getFacetsResult(project, request, keys, new HashMap<>());
  }

  /**
   * Gets the {@link  FacetsResult} for the given project, search request, and keys. The countMap is used to specify
   * the number of facets to return for each key. If a key is not in the countMap, the default count is used.
   * @param project   the project to get the facets for
   * @param request   the search request to use
   * @param keys      the {@link PropertyDescriptor} keys to get facets for. Only properties with the {@link PropertyTag#FACET} tag are used.
   * @return          the {@link FacetsResult}
   */
  @PrefixedCacheable
  public FacetsResult getFacets(
    final Project project,
    final PanteraSearchRequest request,
    final Collection<String> keys,
    final Map<String, CategoricalFacetDisplayState> displayStates
  ) {
    // Check if any of includes in CategorialFacetDisplayState are set
    boolean hasIncludes = displayStates
      .values()
      .stream()
      .anyMatch(
        state ->
          state != null &&
          state.getIncludes() != null &&
          !state.getIncludes().isEmpty()
      );
    if (!hasIncludes) {
      // If no includes are set, just call the other getFacetsResult method
      return getFacetsResult(project, request, keys, displayStates);
    }
    // If includes are set, concurrently call getFacetsResult with includes removed and getFacetsResult with includes and combine the results
    Map<String, CategoricalFacetDisplayState> displayStatesWithoutIncludes =
      new HashMap<>();
    displayStates.forEach((key, state) -> {
      // put a copy without includes
      final CategoricalFacetDisplayState clone =
        new CategoricalFacetDisplayState();
      clone.setQueryString(state.getQueryString());
      clone.setSortDirection(state.getSortDirection());
      clone.setSortDirection(state.getSortDirection());
      clone.setVisibleRowCount(state.getVisibleRowCount());
      displayStatesWithoutIncludes.put(key, clone);
    });

    // Execute both getFacetsResult calls concurrently
    CompletableFuture<FacetsResult> baseResultsFuture =
      CompletableFuture.supplyAsync(() ->
        getFacetsResult(project, request, keys, displayStatesWithoutIncludes)
      );

    CompletableFuture<FacetsResult> includeOnlyResultsFuture =
      CompletableFuture.supplyAsync(() ->
        getFacetsResult(project, request, keys, displayStates)
      );

    // Wait for both to complete
    FacetsResult baseResults = baseResultsFuture.join();
    FacetsResult includeOnlyResults = includeOnlyResultsFuture.join();

    for (String facetKey : keys) {
      final CategoricalFacetDisplayState displayState = displayStates.get(
        facetKey
      );
      final List<String> includeTerms = displayState != null
        ? displayState.getIncludes()
        : null;

      // Skip if no includes for this facet - the base result already has the correct data
      if (includeTerms == null || includeTerms.isEmpty()) {
        continue;
      }

      final AggregateProperties propertiesWithoutIncludes = baseResults
        .getFacets()
        .get(facetKey);
      final AggregateProperties propertiesWithIncludes = includeOnlyResults
        .getFacets()
        .get(facetKey);

      if (propertiesWithoutIncludes == null) {
        // If no properties without includes, just use included properties if available
        if (propertiesWithIncludes != null) {
          baseResults.getFacets().put(facetKey, propertiesWithIncludes);
        }
        continue;
      }

      // Create a new list with included terms first, then remaining terms
      List<AggregateProperty> combinedProperties = new ArrayList<>();

      // First, add all properties from the included results (these go to the top)
      if (propertiesWithIncludes != null) {
        combinedProperties.addAll(propertiesWithIncludes.getProperties());
      }

      // Then, add properties from the without-includes result that are not already included
      for (AggregateProperty property : propertiesWithoutIncludes.getProperties()) {
        // Check if this property value is already in the included terms
        boolean alreadyIncluded = includeTerms.contains(property.getValue());
        if (!alreadyIncluded) {
          combinedProperties.add(property);
        }
      }

      // Update the properties list in the existing AggregateProperties object
      propertiesWithoutIncludes.setProperties(combinedProperties);

      // Ensure the combined list doesn't exceed the visible row count limit
      if (displayState != null) {
        final int visibleRowCount = displayState.getVisibleRowCount();
        if (combinedProperties.size() > visibleRowCount) {
          List<AggregateProperty> truncatedProperties =
            combinedProperties.subList(0, visibleRowCount);
          propertiesWithoutIncludes.setProperties(
            new ArrayList<>(truncatedProperties)
          );
        }
      }
    }

    return baseResults;
  }

  /**
   * Gets the {@link  FacetsResult} for the given project, search request, and keys with optional facet filter terms restriction.
   * @param project   the project to get the facets for
   * @param request   the search request to use
   * @param keys      the {@link PropertyDescriptor} keys to get facets for. Only properties with the {@link PropertyTag#FACET} tag are used.
   * @param displayStates the display states for facets
   * @return          the {@link FacetsResult}
   */
  @PrefixedCacheable
  public FacetsResult getFacetsResult(
    final Project project,
    final PanteraSearchRequest request,
    final Collection<String> keys,
    final Map<String, CategoricalFacetDisplayState> displayStates
  ) {
    // Get a list of the properties configured to be facets
    final List<PropertyDescriptor> facetDescriptors = keys
      .stream()
      .map(key -> propertyDescriptorService.findByKey(project, key))
      .filter(Objects::nonNull)
      .filter(
        descriptor ->
          descriptor.getTags().contains(PropertyTag.FACET) ||
          descriptor.getTags().contains(PropertyTag.DOCUMENT_FACET)
      )
      .toList();

    // Get the numeric and date property stats
    final Map<String, Stats> propertyStats = projectStatsDataService.getStats(
      project,
      request,
      facetDescriptors
        .stream()
        .filter(
          descriptor ->
            descriptor.containsTag(PropertyTag.NUMBER) ||
            descriptor.containsTag(PropertyTag.DATE) ||
            descriptor.containsTag(PropertyTag.LOCATION)
        )
        .collect(Collectors.toSet())
    );

    // Build the search request, ensuring that String properties are aggregated as terms
    final SearchRequest.Builder searchRequestBuilder =
      searchRequestService.builder(project, request);
    facetDescriptors.forEach(descriptor -> {
      final Stats stats = propertyStats.get(descriptor.getKey());
      final CategoricalFacetDisplayState displayState = displayStates.get(
        descriptor.getKey()
      );

      if (descriptor.getKey().equals(DefaultProperty.SEARCH_SET.getKey())) {
        addSearchSetFacetAggregation(searchRequestBuilder, descriptor, project);
      } else if (descriptor.containsTag(PropertyTag.STRING)) {
        addStringFacetAggregation(
          searchRequestBuilder,
          descriptor,
          displayState
        );
      } else if (descriptor.containsTag(PropertyTag.NUMBER)) {
        addNumberFacetAggregation(
          searchRequestBuilder,
          descriptor,
          stats,
          project,
          request
        );
      } else if (descriptor.containsTag(PropertyTag.DATE)) {
        addDateFacetAggregation(
          searchRequestBuilder,
          descriptor,
          stats,
          project,
          request
        );
      } else if (descriptor.containsTag(PropertyTag.LOCATION)) {
        addLocationFacetAggregation(
          searchRequestBuilder,
          descriptor,
          displayState
        );
      } else if (descriptor.containsTag(PropertyTag.PROVENANCE)) {
        addProvenanceFacetAggregation(
          searchRequestBuilder,
          descriptor,
          displayState
        );
      } else {
        log.warn(
          "Property {} is not a valid facet type. Skipping.",
          descriptor.getKey()
        );
      }
    });

    // Execute the search and initialize the response
    final SearchRequest originalRequest = searchRequestBuilder.build();
    final SearchResponse<JsonNode> response = elasticsearchService.search(
      originalRequest,
      JsonNode.class
    );
    assert response.hits().total() != null;
    final FacetsResult result = new FacetsResult()
      .setCount(response.hits().total().value())
      .setMaxUniqueValueCount(MAX_UNIQUE_VALUE_COUNT);

    // Parse the aggregations based on their type
    final Map<String, Aggregate> aggregations = response.aggregations();
    facetDescriptors.forEach(descriptor -> {
      if (!aggregations.containsKey(descriptor.getKey())) {
        return;
      }

      if (descriptor.getKey().equals(DefaultProperty.SEARCH_SET.getKey())) {
        final AggregateProperties aggregateProperties =
          parseSearchSetFacetResults(aggregations, descriptor, displayStates);
        result.getFacets().put(descriptor.getKey(), aggregateProperties);
      } else if (descriptor.containsTag(PropertyTag.STRING)) {
        final AggregateProperties aggregateProperties = parseStringFacetResults(
          aggregations,
          descriptor,
          displayStates
        );
        final long docCount = getFacetDocumentCount(
          originalRequest,
          descriptor.getMapping()
        );
        aggregateProperties.setDocumentCount(docCount);
        result.getFacets().put(descriptor.getKey(), aggregateProperties);
      } else if (descriptor.containsTag(PropertyTag.NUMBER)) {
        final AggregateProperties aggregateProperties = parseNumberFacetResults(
          aggregations,
          descriptor,
          propertyStats
        );
        final long docCount = getFacetDocumentCount(
          originalRequest,
          descriptor.getMapping()
        );
        aggregateProperties.setDocumentCount(docCount);
        result.getFacets().put(descriptor.getKey(), aggregateProperties);
      } else if (descriptor.containsTag(PropertyTag.DATE)) {
        final AggregateProperties aggregateProperties = parseDateFacetResults(
          aggregations,
          descriptor,
          propertyStats
        );
        final long docCount = getFacetDocumentCount(
          originalRequest,
          descriptor.getMapping()
        );
        aggregateProperties.setDocumentCount(docCount);
        result.getFacets().put(descriptor.getKey(), aggregateProperties);
      } else if (descriptor.containsTag(PropertyTag.LOCATION)) {
        final AggregateProperties aggregateProperties =
          parseLocationFacetResults(aggregations, descriptor, displayStates);
        final long docCount = getFacetDocumentCount(
          originalRequest,
          descriptor.getMapping()
        );
        aggregateProperties.setDocumentCount(docCount);
        result.getFacets().put(descriptor.getKey(), aggregateProperties);
      } else if (descriptor.containsTag(PropertyTag.PROVENANCE)) {
        final AggregateProperties aggregateProperties =
          parseProvenanceFacetResults(aggregations, descriptor, displayStates);
        result.getFacets().put(descriptor.getKey(), aggregateProperties);
      } else {
        log.warn(
          "Property {} is not a valid facet type. Skipping.",
          descriptor.getKey()
        );
      }
    });

    return result;
  }

  /**
   * Adds string facet aggregation to the search request builder
   */
  private void addStringFacetAggregation(
    final SearchRequest.Builder searchRequestBuilder,
    final PropertyDescriptor descriptor,
    final CategoricalFacetDisplayState displayState
  ) {
    // ASSUMPTION: This is a categorical facet.
    // NOTE: If the requested sort order is ascending (1) instead of descending (-1), the ES
    //  documentation recommends using `rare_terms` aggregation instead of modifying the
    //  `order` parameter of the `terms`, due to unreliable results for large data:
    // https://www.elastic.co/guide/en/elasticsearch/reference/current/search-aggregations-bucket-terms-aggregation.html#search-aggregations-bucket-terms-aggregation-order
    // However, rare_terms doesn't support limiting the number of buckets returned, so this
    //  implementation uses `terms` with ascending sort order instead.
    final SortOrder order = (displayState != null &&
        displayState.getSortDirection() == 1)
      ? SortOrder.Asc
      : SortOrder.Desc;

    if (displayState != null && displayState.getQueryString().length() > 0) {
      // The user has specified a search query within this facet, so we add a filter
      //  aggregation before adding the count aggregation.
      searchRequestBuilder.aggregations(
        descriptor.getKey(),
        aggregationBuilder ->
          aggregationBuilder
            .filter(filterBuilder ->
              filterBuilder.wildcard(termBuilder ->
                termBuilder
                  .field(descriptor.getMapping())
                  // Append a * to the end of the query so users don't need to type the exact bucket value
                  .value(displayState.getQueryString() + "*")
              )
            )
            .aggregations(
              SUB_AGGREGATION_KEY,
              new Aggregation.Builder()
                .terms(termsBuilder ->
                  termsBuilder
                    .field(descriptor.getMapping())
                    // Sort buckets by count (either ascending or descending, depending on display state)
                    .order(new NamedValue("_count", order))
                    .size(displayState.getVisibleRowCount())
                    .missing(AggregateProperty.NOT_AVAILABLE)
                )
                .build()
            )
      );
    } else {
      // The user has not specified a search query within this facet, so we add the count
      //  aggregation directly to the search request.
      searchRequestBuilder.aggregations(
        descriptor.getKey(),
        aggregationBuilder ->
          aggregationBuilder.terms(termsBuilder ->
            termsBuilder
              .field(descriptor.getMapping())
              // Sort buckets by count (either ascending or descending, depending on display state)
              .order(new NamedValue("_count", order))
              .size(
                (displayState != null)
                  ? displayState.getVisibleRowCount()
                  : DEFAULT_FACET_COUNT
              )
              .missing(AggregateProperty.NOT_AVAILABLE)
          )
      );
    }
    final var bucketCountAggregation = Aggregation.of(aggregationBuilder ->
      aggregationBuilder.cardinality(builder ->
        builder
          .field(descriptor.getMapping())
          .precisionThreshold(MAX_UNIQUE_VALUE_COUNT)
      )
    );
    searchRequestBuilder.aggregations(
      descriptor.getKey() + UNIQUE_VALUE_COUNT_KEY_SUFFIX,
      bucketCountAggregation
    );
  }

  /**
   * Gets the unique value count for a given field
   */
  private long getUniqueValueCount(
    final Project project,
    final PanteraSearchRequest request,
    final PropertyDescriptor descriptor
  ) {
    final SearchRequest.Builder searchRequestBuilder =
      searchRequestService.builder(project, request);

    searchRequestBuilder
      .size(0)
      .trackTotalHits(b -> b.enabled(false))
      .trackScores(false)
      .aggregations("unique_count", aggregationBuilder ->
        aggregationBuilder.cardinality(builder ->
          builder
            .field(descriptor.getMapping())
            .precisionThreshold(MAX_UNIQUE_VALUE_COUNT)
        )
      );

    final SearchResponse<JsonNode> response = elasticsearchService.search(
      searchRequestBuilder.build(),
      JsonNode.class
    );

    return response.aggregations().get("unique_count").cardinality().value();
  }

  /**
   * Adds number facet aggregation to the search request builder
   */
  private void addNumberFacetAggregation(
    final SearchRequest.Builder searchRequestBuilder,
    final PropertyDescriptor descriptor,
    final Stats stats,
    final Project project,
    final PanteraSearchRequest request
  ) {
    final boolean areStatsValid =
      !Double.isInfinite(stats.getMin()) && !Double.isInfinite(stats.getMax());

    if (!areStatsValid) {
      return;
    }

    // Get the unique value count first
    final long uniqueValueCount = getUniqueValueCount(
      project,
      request,
      descriptor
    );

    // Handle single value case or when there are few unique values (< 10)
    if (stats.getInterval() == 0 || uniqueValueCount < 10) {
      // For single value or few values, use a terms aggregation like string facets
      searchRequestBuilder.aggregations(
        descriptor.getKey(),
        aggregationBuilder ->
          aggregationBuilder.terms(termsBuilder ->
            termsBuilder
              .field(descriptor.getMapping())
              .size((int) Math.min(uniqueValueCount, MAX_UNIQUE_VALUE_COUNT))
          )
      );
    } else {
      // Normal histogram aggregation for multiple values
      searchRequestBuilder.aggregations(
        descriptor.getKey(),
        aggregationBuilder ->
          aggregationBuilder.histogram(extendedStatsBuilder ->
            extendedStatsBuilder
              .field(descriptor.getMapping())
              .interval(stats.getInterval())
              .minDocCount(0)
              .extendedBounds(extendedBoundsBuilder ->
                extendedBoundsBuilder.min(stats.getMin()).max(stats.getMax())
              )
          )
      );
    }

    final var bucketCountAggregation = Aggregation.of(aggregationBuilder ->
      aggregationBuilder.cardinality(builder ->
        builder
          .field(descriptor.getMapping())
          .precisionThreshold(MAX_UNIQUE_VALUE_COUNT)
      )
    );
    searchRequestBuilder.aggregations(
      descriptor.getKey() + UNIQUE_VALUE_COUNT_KEY_SUFFIX,
      bucketCountAggregation
    );
  }

  /**
   * Adds date facet aggregation to the search request builder
   */
  private void addDateFacetAggregation(
    final SearchRequest.Builder searchRequestBuilder,
    final PropertyDescriptor descriptor,
    final Stats stats,
    final Project project,
    final PanteraSearchRequest request
  ) {
    final boolean areStatsValid =
      !Double.isInfinite(stats.getMin()) && !Double.isInfinite(stats.getMax());

    if (!areStatsValid) {
      return;
    }

    // Get the unique value count first
    final long uniqueValueCount = getUniqueValueCount(
      project,
      request,
      descriptor
    );

    // Handle single value case or when there are few unique values (< 10)
    if (stats.getInterval() == 0 || uniqueValueCount < 10) {
      // For single value or few values, use a terms aggregation like string facets
      searchRequestBuilder.aggregations(
        descriptor.getKey(),
        aggregationBuilder ->
          aggregationBuilder.terms(termsBuilder ->
            termsBuilder
              .field(descriptor.getMapping())
              .size((int) Math.min(uniqueValueCount, MAX_UNIQUE_VALUE_COUNT))
          )
      );
    } else {
      // Normal date histogram aggregation for multiple values
      searchRequestBuilder.aggregations(
        descriptor.getKey(),
        aggregationBuilder ->
          aggregationBuilder.dateHistogram(dateHistogramBuilder ->
            dateHistogramBuilder
              .field(descriptor.getMapping())
              .calendarInterval(
                stats.getTimelineInterval().getCalendarInterval()
              )
              .minDocCount(0)
              .extendedBounds(extendedBoundsBuilder ->
                extendedBoundsBuilder
                  .min(FieldDateMath.of(fdmb -> fdmb.value(stats.getMin())))
                  .max(FieldDateMath.of(fdmb -> fdmb.value(stats.getMax())))
              )
          )
      );
    }

    final var bucketCountAggregation = Aggregation.of(aggregationBuilder ->
      aggregationBuilder.cardinality(builder ->
        builder
          .field(descriptor.getMapping())
          .precisionThreshold(MAX_UNIQUE_VALUE_COUNT)
      )
    );
    searchRequestBuilder.aggregations(
      descriptor.getKey() + UNIQUE_VALUE_COUNT_KEY_SUFFIX,
      bucketCountAggregation
    );
  }

  /**
   * Adds location facet aggregation to the search request builder
   */
  private void addLocationFacetAggregation(
    final SearchRequest.Builder searchRequestBuilder,
    final PropertyDescriptor descriptor,
    final CategoricalFacetDisplayState displayState
  ) {
    final List<String> includeTerms = displayState != null
      ? displayState.getIncludes()
      : List.of();
    final SortOrder order = (displayState != null &&
        displayState.getSortDirection() == 1)
      ? SortOrder.Asc
      : SortOrder.Desc;

    if (displayState != null && displayState.getQueryString().length() > 0) {
      // Filter aggregation for search query
      searchRequestBuilder.aggregations(
        descriptor.getKey(),
        aggregationBuilder ->
          aggregationBuilder
            .filter(filterBuilder ->
              filterBuilder.wildcard(termBuilder ->
                termBuilder
                  .field(descriptor.getMapping() + ".label")
                  .value(displayState.getQueryString() + "*")
              )
            )
            .aggregations(
              SUB_AGGREGATION_KEY,
              new Aggregation.Builder()
                .terms(termsBuilder ->
                  termsBuilder
                    .field(descriptor.getMapping() + ".label")
                    .order(new NamedValue("_count", order))
                    .size(displayState.getVisibleRowCount())
                    .missing(AggregateProperty.NOT_AVAILABLE)
                )
                .build()
            )
      );
    } else {
      // Direct terms aggregation
      searchRequestBuilder.aggregations(
        descriptor.getKey(),
        aggregationBuilder ->
          aggregationBuilder.terms(termsBuilder -> {
            termsBuilder
              .field(descriptor.getMapping() + ".label")
              .order(new NamedValue("_count", order))
              .size(
                (displayState != null)
                  ? displayState.getVisibleRowCount()
                  : DEFAULT_FACET_COUNT
              )
              .missing(AggregateProperty.NOT_AVAILABLE);

            // Check if this field has specific terms in the filter and include only those
            if (includeTerms != null && !includeTerms.isEmpty()) {
              termsBuilder.include(includeBuilder ->
                includeBuilder.terms(includeTerms)
              );
            }

            return termsBuilder;
          })
      );
    }

    final var bucketCountAggregation = Aggregation.of(aggregationBuilder ->
      aggregationBuilder.cardinality(builder ->
        builder
          .field(descriptor.getMapping() + ".label")
          .precisionThreshold(MAX_UNIQUE_VALUE_COUNT)
      )
    );
    searchRequestBuilder.aggregations(
      descriptor.getKey() + UNIQUE_VALUE_COUNT_KEY_SUFFIX,
      bucketCountAggregation
    );
  }

  /**
   * Adds provenance facet aggregation to the search request builder.
   */
  private void addProvenanceFacetAggregation(
    final SearchRequest.Builder searchRequestBuilder,
    final PropertyDescriptor descriptor,
    final CategoricalFacetDisplayState displayState
  ) {
    searchRequestBuilder.aggregations(descriptor.getKey(), aggregationBuilder ->
      aggregationBuilder
        .filter(filterBuilder ->
          filterBuilder.exists(existsBuilder ->
            existsBuilder.field("provenance_feature.provenance")
          )
        )
        .aggregations("provenance_details", subAggBuilder ->
          subAggBuilder.topHits(topHitsBuilder ->
            topHitsBuilder
              .size(
                (displayState != null)
                  ? displayState.getVisibleRowCount()
                  : DEFAULT_FACET_COUNT
              )
              .source(sourceBuilder ->
                sourceBuilder.filter(filterBuilder ->
                  filterBuilder.includes("provenance_feature.provenance")
                )
              )
          )
        )
    );
  }

  /**
   * Adds search set facet aggregation to the search request builder.
   * Creates a filters aggregation with keyed filters for each search set.
   */
  private void addSearchSetFacetAggregation(
    final SearchRequest.Builder searchRequestBuilder,
    final PropertyDescriptor descriptor,
    final Project project
  ) {
    if (searchSetService.countAll(project, currentUserService.get()) > 0) {
      searchRequestBuilder.aggregations(
        descriptor.getKey(),
        aggregationBuilder ->
          aggregationBuilder.filters(filterBuilder ->
            filterBuilder.filters(fb -> {
              final List<SearchSet> searchSets = searchSetService.findAll(
                project,
                currentUserService.get()
              );
              final Map<String, Query> keyedFilters = new HashMap<>();
              searchSets.forEach(searchSet -> {
                try {
                  final String queryString = searchSet.getLuceneQuery();
                  final Query setQuery = queryBuilderService.build(
                    project,
                    queryString
                  );
                  keyedFilters.put(searchSet.getName(), setQuery);
                } catch (Exception e) {
                  log.error(
                    "Error building query for search set {}: {}",
                    searchSet.getName(),
                    e.getMessage()
                  );
                }
              });
              fb.keyed(keyedFilters);
              return fb;
            })
          )
      );
    }
  }

  /**
   * Parses string facet results from aggregations
   */

  private AggregateProperties parseStringFacetResults(
    final Map<String, Aggregate> aggregations,
    final PropertyDescriptor descriptor,
    final Map<String, CategoricalFacetDisplayState> displayStates
  ) {
    // Get the aggregated buckets from the search results.
    final CategoricalFacetDisplayState displayState = displayStates.get(
      descriptor.getKey()
    );
    // If this facet includes a search query, the buckets are within the filter aggregation.
    final Aggregate aggregation = (displayState != null &&
        displayState.getQueryString().length() > 0)
      ? aggregations
        .get(descriptor.getKey())
        .filter()
        .aggregations()
        .get(SUB_AGGREGATION_KEY)
      // Otherwise, the buckets are at the top level
      : aggregations.get(descriptor.getKey());
    final List<StringTermsBucket> buckets = aggregation
      .sterms()
      .buckets()
      .array();
    final List<AggregateProperty> values = buckets
      .stream()
      .map(bucket ->
        (AggregateProperty) new AggregateProperty()
          .setCount(bucket.docCount())
          .setKey(descriptor.getKey())
          .setValue(bucket.key().stringValue())
          .setTags(descriptor.getTags())
      )
      .collect(Collectors.toList());

    // Filter out NOT_AVAILABLE bucket if it is the only bucket present
    final List<AggregateProperty> filteredValues = (values.size() == 1 &&
        AggregateProperty.NOT_AVAILABLE.equals(values.get(0).getValue()))
      ? List.of()
      : values;

    final AggregateProperties aggregateProperties = new AggregateProperties()
      .setProperties(filteredValues)
      .setOther(aggregation.sterms().sumOtherDocCount());
    if (
      aggregations.containsKey(
        descriptor.getKey() + UNIQUE_VALUE_COUNT_KEY_SUFFIX
      )
    ) {
      aggregateProperties.setUniqueValueCount(
        aggregations
          .get(descriptor.getKey() + UNIQUE_VALUE_COUNT_KEY_SUFFIX)
          .cardinality()
          .value()
      );
    }
    return aggregateProperties;
  }

  /**
   * Parses number facet results from aggregations
   */
  private AggregateProperties parseNumberFacetResults(
    final Map<String, Aggregate> aggregations,
    final PropertyDescriptor descriptor,
    final Map<String, Stats> propertyStats
  ) {
    final Aggregate aggregation = aggregations.get(descriptor.getKey());
    final List<AggregateProperty> values;

    if (aggregation.isHistogram()) {
      final List<HistogramBucket> buckets = aggregation
        .histogram()
        .buckets()
        .array();
      values = buckets
        .stream()
        .map(bucket ->
          (AggregateNumericProperty) new AggregateNumericProperty()
            .setMin(bucket.key())
            .setMax(
              bucket.key() +
              propertyStats.get(descriptor.getKey()).getInterval()
            )
            .setCount(bucket.docCount())
            .setKey(descriptor.getKey())
            .setValue(bucket.key() + "")
            .setTags(descriptor.getTags())
        )
        .collect(Collectors.toList());
    } else {
      // Terms aggregation case (single value) - handle both long and double terms
      if (aggregation.isLterms()) {
        final List<LongTermsBucket> buckets = aggregation
          .lterms()
          .buckets()
          .array();
        values = buckets
          .stream()
          .map(bucket ->
            (AggregateNumericProperty) new AggregateNumericProperty()
              .setMin(bucket.key())
              .setMax(bucket.key()) // For single value, min = max
              .setCount(bucket.docCount())
              .setKey(descriptor.getKey())
              .setValue(bucket.key() + "")
              .setTags(descriptor.getTags())
          )
          .collect(Collectors.toList());
      } else if (aggregation.isDterms()) {
        final List<DoubleTermsBucket> buckets = aggregation
          .dterms()
          .buckets()
          .array();
        values = buckets
          .stream()
          .map(bucket ->
            (AggregateNumericProperty) new AggregateNumericProperty()
              .setMin(bucket.key())
              .setMax(bucket.key()) // For single value, min = max
              .setCount(bucket.docCount())
              .setKey(descriptor.getKey())
              .setValue(bucket.key() + "")
              .setTags(descriptor.getTags())
          )
          .collect(Collectors.toList());
      } else {
        throw new IllegalStateException(
          "Unexpected terms aggregation type for numeric field: " +
          descriptor.getKey()
        );
      }
    }

    // If there are no unique non-missing values, drop the (missing-only) bucket results
    Long uniqueCount = null;
    if (
      aggregations.containsKey(
        descriptor.getKey() + UNIQUE_VALUE_COUNT_KEY_SUFFIX
      )
    ) {
      uniqueCount = aggregations
        .get(descriptor.getKey() + UNIQUE_VALUE_COUNT_KEY_SUFFIX)
        .cardinality()
        .value();
    }

    final List<AggregateProperty> finalValues = (uniqueCount != null &&
        uniqueCount == 0)
      ? List.of()
      : values;

    final AggregateProperties aggregateProperties = new AggregateProperties()
      .setProperties(finalValues);

    // Set unique value count if available
    if (uniqueCount != null) {
      aggregateProperties.setUniqueValueCount(uniqueCount);
    }
    return aggregateProperties;
  }

  /**
   * Parses date facet results from aggregations
   */
  private AggregateProperties parseDateFacetResults(
    final Map<String, Aggregate> aggregations,
    final PropertyDescriptor descriptor,
    final Map<String, Stats> propertyStats
  ) {
    final Aggregate aggregation = aggregations.get(descriptor.getKey());
    final List<AggregateProperty> values;

    // Check if this is a date histogram or terms aggregation (single value case)
    if (aggregation.isDateHistogram()) {
      // Normal date histogram case (multiple values)
      final List<DateHistogramBucket> buckets = aggregation
        .dateHistogram()
        .buckets()
        .array();
      values = buckets
        .stream()
        .map(bucket ->
          (AggregateNumericProperty) new AggregateNumericProperty()
            .setMin(bucket.key())
            .setMax(
              DateUtilsWrapper.add(
                Instant.ofEpochMilli(bucket.key()),
                propertyStats
                  .get(descriptor.getKey())
                  .getTimelineInterval()
                  .getCalendarUnit(),
                1
              ).toEpochMilli()
            )
            .setCount(bucket.docCount())
            .setKey(descriptor.getKey())
            .setValue(bucket.key() + "")
            .setValueFormatted(Instant.ofEpochMilli(bucket.key()).toString())
            .setTags(descriptor.getTags())
        )
        .collect(Collectors.toList());
    } else {
      // Terms aggregation case (single value)
      final List<LongTermsBucket> buckets = aggregation
        .lterms()
        .buckets()
        .array();
      values = buckets
        .stream()
        .map(bucket ->
          (AggregateNumericProperty) new AggregateNumericProperty()
            .setMin(bucket.key())
            .setMax(bucket.key()) // For single value, min = max
            .setCount(bucket.docCount())
            .setKey(descriptor.getKey())
            .setValue(bucket.key() + "")
            .setValueFormatted(Instant.ofEpochMilli(bucket.key()).toString())
            .setTags(descriptor.getTags())
        )
        .collect(Collectors.toList());
    }

    // If there are no unique non-missing values, drop the (missing-only) bucket results
    Long uniqueCount = null;
    if (
      aggregations.containsKey(
        descriptor.getKey() + UNIQUE_VALUE_COUNT_KEY_SUFFIX
      )
    ) {
      uniqueCount = aggregations
        .get(descriptor.getKey() + UNIQUE_VALUE_COUNT_KEY_SUFFIX)
        .cardinality()
        .value();
    }

    final List<AggregateProperty> finalValues = (uniqueCount != null &&
        uniqueCount == 0)
      ? List.of()
      : values;

    final AggregateProperties aggregateProperties = new AggregateProperties()
      .setProperties(finalValues);

    // Set unique value count if available
    if (uniqueCount != null) {
      aggregateProperties.setUniqueValueCount(uniqueCount);
    }
    return aggregateProperties;
  }

  /**
   * Parses location facet results from aggregations
   */
  private AggregateProperties parseLocationFacetResults(
    final Map<String, Aggregate> aggregations,
    final PropertyDescriptor descriptor,
    final Map<String, CategoricalFacetDisplayState> displayStates
  ) {
    // Parse location facets
    final CategoricalFacetDisplayState locationDisplayState = displayStates.get(
      descriptor.getKey()
    );

    // Determine if results are in a filter aggregation or at top level
    final Aggregate aggregation = (locationDisplayState != null &&
        locationDisplayState.getQueryString().length() > 0)
      ? aggregations
        .get(descriptor.getKey())
        .filter()
        .aggregations()
        .get(SUB_AGGREGATION_KEY)
      : aggregations.get(descriptor.getKey());

    final List<StringTermsBucket> buckets = aggregation
      .sterms()
      .buckets()
      .array();

    // Convert buckets to aggregate properties (labels only)
    final List<AggregateProperty> values = buckets
      .stream()
      .map(bucket ->
        (AggregateProperty) new AggregateProperty()
          .setCount(bucket.docCount())
          .setKey(descriptor.getKey())
          .setValue(bucket.key().stringValue())
          .setTags(descriptor.getTags())
      )
      .collect(Collectors.toList());

    // Filter out NOT_AVAILABLE bucket if it is the only bucket present
    final List<AggregateProperty> filteredValues = (values.size() == 1 &&
        AggregateProperty.NOT_AVAILABLE.equals(values.get(0).getValue()))
      ? List.of()
      : values;

    final AggregateProperties aggregateProperties = new AggregateProperties()
      .setProperties(filteredValues)
      .setOther(aggregation.sterms().sumOtherDocCount());

    // Set unique value count if available
    if (
      aggregations.containsKey(
        descriptor.getKey() + UNIQUE_VALUE_COUNT_KEY_SUFFIX
      )
    ) {
      aggregateProperties.setUniqueValueCount(
        aggregations
          .get(descriptor.getKey() + UNIQUE_VALUE_COUNT_KEY_SUFFIX)
          .cardinality()
          .value()
      );
    }
    return aggregateProperties;
  }

  /**
   * Parses provenance facet results from aggregations
   */
  private AggregateProperties parseProvenanceFacetResults(
    final Map<String, Aggregate> aggregations,
    final PropertyDescriptor descriptor,
    final Map<String, CategoricalFacetDisplayState> displayStates
  ) {
    final Aggregate aggregation = aggregations.get(descriptor.getKey());
    final List<AggregateProvenanceProperty> provenanceProperties =
      new ArrayList<>();
    final List<Provenance> allProvenance = new ArrayList<>();

    if (aggregation.filter().aggregations().containsKey("provenance_details")) {
      final var topHits = aggregation
        .filter()
        .aggregations()
        .get("provenance_details")
        .topHits();

      for (final var hit : topHits.hits().hits()) {
        if (hit.source() != null) {
          final JsonNode sourceNode = hit.source().to(JsonNode.class);
          final JsonNode provenanceArray = sourceNode.at(
            "/provenance_feature/provenance"
          );

          if (provenanceArray.isArray()) {
            for (final JsonNode provenanceNode : provenanceArray) {
              try {
                final ObjectNode cleanedNode = objectMapper.createObjectNode();

                provenanceNode
                  .fields()
                  .forEachRemaining(entry -> {
                    final String fieldName = entry.getKey();
                    final JsonNode fieldValue = entry.getValue();

                    if (fieldValue.isArray() && fieldValue.size() > 0) {
                      cleanedNode.set(fieldName, fieldValue.get(0));
                    } else if (!fieldValue.isArray()) {
                      cleanedNode.set(fieldName, fieldValue);
                    }
                  });

                final Provenance provenance = objectMapper.treeToValue(
                  cleanedNode,
                  Provenance.class
                );

                allProvenance.add(provenance);
              } catch (Exception e) {
                log.warn(
                  "Failed to parse provenance object: {}",
                  e.getMessage()
                );
              }
            }
          }
        }
      }
    }

    // Group provenance entries by source and date
    final Map<String, Map<String, List<Provenance>>> groupedProvenance =
      new HashMap<>();

    for (Provenance provenance : allProvenance) {
      // Calculate date from provenance timestamp
      String dateString = "unknown";
      if (provenance.getTimestampMS() > 0) {
        final Date date = new Date(provenance.getTimestampMS());
        final SimpleDateFormat dateFormat = new SimpleDateFormat("yyyy-MM-dd");
        dateString = dateFormat.format(date);
      }

      String source = provenance.getSource();
      if (source == null || source.isEmpty()) {
        source = "Unknown Source";
      }

      // Group: source -> date -> List<Provenance>
      groupedProvenance
        .computeIfAbsent(source, k -> new HashMap<>())
        .computeIfAbsent(dateString, k -> new ArrayList<>())
        .add(provenance);
    }

    // Create properties directly from the grouped data
    for (Map.Entry<
      String,
      Map<String, List<Provenance>>
    > sourceEntry : groupedProvenance.entrySet()) {
      String source = sourceEntry.getKey();

      for (Map.Entry<String, List<Provenance>> dateEntry : sourceEntry
        .getValue()
        .entrySet()) {
        String dateString = dateEntry.getKey();
        List<Provenance> provenances = dateEntry.getValue();

        if (provenances.isEmpty()) continue;

        AggregateProvenanceProperty property =
          new AggregateProvenanceProperty();
        property.setCount((long) provenances.size());
        property.setDate(dateString);
        property.setEntries(provenances);
        property.setSource(source);
        property.setValue(source + ":" + dateString);

        provenanceProperties.add(property);
      }
    }

    // Determine sort order from display state (1 = ascending, otherwise descending)
    final CategoricalFacetDisplayState displayState = displayStates.get(
      descriptor.getKey()
    );
    final boolean ascending =
      displayState != null && displayState.getSortDirection() == 1;

    // Sort by highest timestamp in each group according to requested order
    provenanceProperties.sort((a, b) -> {
      long maxTimestampA = a
        .getEntries()
        .stream()
        .mapToLong(Provenance::getTimestampMS)
        .max()
        .orElse(0L);
      long maxTimestampB = b
        .getEntries()
        .stream()
        .mapToLong(Provenance::getTimestampMS)
        .max()
        .orElse(0L);
      int cmp = Long.compare(maxTimestampA, maxTimestampB);
      return ascending ? cmp : -cmp;
    });

    if (
      displayState != null &&
      displayState.getQueryString() != null &&
      !displayState.getQueryString().isEmpty()
    ) {
      final String query = displayState.getQueryString().toLowerCase();
      provenanceProperties.removeIf(
        prop ->
          !prop.getSource().toLowerCase().contains(query) &&
          !prop.getDate().contains(query)
      );
    }

    // Set display indices after final list is prepared
    for (int i = 0; i < provenanceProperties.size(); i++) {
      provenanceProperties.get(i).setDisplayIndex(i + 1);
    }

    final AggregateProperties result = new AggregateProperties();
    result.setProperties(new ArrayList<>(provenanceProperties));
    result.setDocumentCount((long) provenanceProperties.size());
    result.setUniqueValueCount((long) provenanceProperties.size());

    return result;
  }

  /**
   * Parses search set facet results from aggregations
   */
  private AggregateProperties parseSearchSetFacetResults(
    final Map<String, Aggregate> aggregations,
    final PropertyDescriptor descriptor,
    final Map<String, CategoricalFacetDisplayState> displayStates
  ) {
    // Search sets are special, so we handle them separately
    final List<AggregateProperty> allValues = new ArrayList<>();

    aggregations
      .get(descriptor.getKey())
      .filters()
      .buckets()
      .keyed()
      .forEach((searchSetName, bucket) -> {
        if (bucket.docCount() > 0) {
          allValues.add(
            (AggregateProperty) new AggregateProperty()
              .setCount(bucket.docCount())
              .setKey(descriptor.getKey())
              .setValue(searchSetName)
              .setTags(descriptor.getTags())
          );
        }
      });

    // Filter and sort the aggregate properties according to the display state
    List<AggregateProperty> values;
    if (displayStates.containsKey(descriptor.getKey())) {
      values = allValues
        .stream()
        .filter(
          // Filter by the query string if it exists
          ap -> {
            if (
              displayStates.get(descriptor.getKey()).getQueryString() != null &&
              !displayStates.get(descriptor.getKey()).getQueryString().isEmpty()
            ) {
              return ap
                .getValue()
                .toLowerCase()
                .contains(
                  displayStates
                    .get(descriptor.getKey())
                    .getQueryString()
                    .toLowerCase()
                );
            }
            return true;
          }
        )
        .sorted((o1, o2) -> {
          // Sort by the count, either ascending or descending
          if (displayStates.get(descriptor.getKey()).getSortDirection() == 1) {
            return Long.compare(o1.getCount(), o2.getCount());
          } else {
            return Long.compare(o2.getCount(), o1.getCount());
          }
        })
        .limit(
          // Limit the number of results to the visible row count
          displayStates.get(descriptor.getKey()).getVisibleRowCount() == 0
            ? DEFAULT_FACET_COUNT
            : displayStates.get(descriptor.getKey()).getVisibleRowCount()
        )
        .collect(Collectors.toList());
    } else {
      // Default case, top 5 descending
      values = allValues
        .stream()
        .sorted(
          Comparator.comparingLong(AggregateProperty::getCount).reversed()
        )
        .limit(DEFAULT_FACET_COUNT)
        .collect(Collectors.toList());
    }

    final long searchSetCount = values.size();
    final long totalSearchSets = allValues.size();
    final long otherCount = totalSearchSets - searchSetCount;

    return new AggregateProperties()
      .setProperties(values)
      .setUniqueValueCount(totalSearchSets)
      .setOther(otherCount);
  }

  // Given a search query and a field mapping. check how many documents contain this field.
  private Long getFacetDocumentCount(
    SearchRequest originalRequest,
    String fieldMapping
  ) {
    // Build a new search request to count docs where this field exists
    final SearchRequest countRequest = SearchRequest.of(s ->
      s
        .index(originalRequest.index())
        .size(0)
        .query(originalRequest.query())
        .aggregations("docs_with_field", agg ->
          agg.filter(f -> f.exists(e -> e.field(fieldMapping)))
        )
    );
    // Execute the request
    final SearchResponse<Void> countResponse = elasticsearchService.search(
      countRequest,
      Void.class
    );
    final Aggregate docsWithFieldAgg = countResponse
      .aggregations()
      .get("docs_with_field");
    if (docsWithFieldAgg != null && docsWithFieldAgg.isFilter()) {
      final long docCount = docsWithFieldAgg.filter().docCount();
      return docCount;
    }
    return Long.valueOf(0);
  }

  /**
   * Gets the min and max values for the given project, search request, and property descriptor
   * @param project               the project to get the min/max for
   * @param request               the search request to use
   * @param propertyDescriptor    the {@link PropertyDescriptor} to get the min/max for
   * @return                      the {@link MinMax} object containing the min and max values
   */
  @PrefixedCacheable
  public MinMax getMinMax(
    final Project project,
    final PanteraSearchRequest request,
    final PropertyDescriptor propertyDescriptor
  ) {
    final SearchRequest searchRequest = searchRequestService
      .builder(project, request)
      .size(0)
      .trackTotalHits(b -> b.enabled(false))
      .trackScores(false)
      .aggregations("min", aggregationBuilder ->
        aggregationBuilder.min(minBuilder ->
          minBuilder.field(propertyDescriptor.getMapping())
        )
      )
      .aggregations("max", aggregationBuilder ->
        aggregationBuilder.max(maxBuilder ->
          maxBuilder.field(propertyDescriptor.getMapping())
        )
      )
      .build();

    final SearchResponse<JsonNode> response = elasticsearchService.search(
      searchRequest,
      JsonNode.class
    );
    return new MinMax()
      .setMin(response.aggregations().get("min").min().value())
      .setMax(response.aggregations().get("max").max().value());
  }

  /**
   * Gets a histogram (represented as a {@link TimelineResult} for the given request and date descriptor.  The {@link TimelineInterval} is used to specify the bucket side. The maxTimestamp
   * is used to specify a date in the range of the last bucket in the timeline. It need not exist in the data, but it is used to calculate the bucket extrema.
   * @param project           the project to get the timeline for
   * @param request           the search request to use
   * @param dateDescriptor    the {@link PropertyDescriptor} that represents the date field to use for the timeline
   * @param interval          the {@link TimelineInterval} to use for the timeline bucket widths
   * @param bucketCount       the number of buckets to return in the timeline
   * @param maxTimestamp      a timestamp that is present in the last bucket of the timeline
   * @return                  the {@link TimelineResult}. The {@link TimelineResult#moreTimestamp()} and {@link TimelineResult#lessTimestamp()} can be used as the maxTimestamp
   *                          value in subsequent requests to get more data to the right or left of the current window. If these values are null, there is no more data to fetch
   *                          in that direction.
   */
  @PrefixedCacheable
  public TimelineResult getTimeline(
    final Project project,
    final PanteraSearchRequest request,
    final PropertyDescriptor dateDescriptor,
    final TimelineInterval interval,
    final int bucketCount,
    final Long maxTimestamp
  ) {
    // Calculate the min/max timestamps for the number of buckets
    final Instant maxTimestampInstant = Instant.ofEpochMilli(maxTimestamp);
    final Instant maxBucketMin = DateUtilsWrapper.truncate(
      maxTimestampInstant,
      interval.getCalendarUnit()
    );
    final Instant maxBucketMax = DateUtilsWrapper.add(
      maxBucketMin,
      interval.getCalendarUnit(),
      1
    );
    final Instant minBucketMin = DateUtilsWrapper.minus(
      maxBucketMin,
      interval.getCalendarUnit(),
      bucketCount - 1
    );

    // Build the search request that fetches the min/max for the entire search, as well as the bucketed results
    // using the hard bounds defined by the bucket min/max
    final SearchRequest searchRequest = searchRequestService
      .builder(project, request)
      .trackTotalHits(b -> b.enabled(true))
      .size(0)
      .trackScores(false)
      .aggregations("min", aggregationBuilder ->
        aggregationBuilder.min(minBuilder ->
          minBuilder.field(dateDescriptor.getMapping())
        )
      )
      .aggregations("max", aggregationBuilder ->
        aggregationBuilder.max(maxBuilder ->
          maxBuilder.field(dateDescriptor.getMapping())
        )
      )
      .aggregations("buckets", aggregationBuilder ->
        aggregationBuilder.dateHistogram(dateHistogramBuilder ->
          dateHistogramBuilder
            .field(dateDescriptor.getMapping())
            .calendarInterval(interval.getCalendarInterval())
            .minDocCount(0)
            .hardBounds(hardBoundsBuilder ->
              hardBoundsBuilder
                .min(
                  FieldDateMath.of(fdmb ->
                    fdmb.value((double) minBucketMin.toEpochMilli())
                  )
                )
                .max(
                  FieldDateMath.of(fdmb ->
                    fdmb.value((double) maxBucketMax.toEpochMilli())
                  )
                )
            )
            .extendedBounds(extendedBoundsBuilder ->
              extendedBoundsBuilder
                .min(
                  FieldDateMath.of(fdmb ->
                    fdmb.value((double) minBucketMin.toEpochMilli())
                  )
                )
                .max(
                  FieldDateMath.of(fdmb ->
                    fdmb.value((double) maxBucketMin.toEpochMilli())
                  )
                )
            )
        )
      )
      .build();

    // Execute the search
    final SearchResponse<JsonNode> response = elasticsearchService.search(
      searchRequest,
      JsonNode.class
    );

    // Create the response
    return new TimelineResult()
      .setCount(response.hits().total().value())
      .setMinTimestampMs(
        (long) response.aggregations().get("min").min().value()
      )
      .setMaxTimestampMs(
        (long) response.aggregations().get("max").max().value()
      )
      .setBuckets(
        response
          .aggregations()
          .get("buckets")
          .dateHistogram()
          .buckets()
          .array()
          .stream()
          .map(bucket ->
            new TimelineResult.TimelineBucket()
              .setCount(bucket.docCount())
              .setMinTimestampMs(bucket.key())
              .setMaxTimestampMs(
                DateUtilsWrapper.add(
                  Instant.ofEpochMilli(bucket.key()),
                  interval.getCalendarUnit(),
                  1
                ).toEpochMilli()
              )
          )
          .collect(Collectors.toCollection(TreeSet::new))
      );
  }

  /**
   * Updates the document with the given id in the given project with the new property values
   * @param project                 the project to update the document in
   * @param docId                   the id of the document to update
   * @param propertyKeyToNewValues  a map of property key to new values for that property
   */
  @PrefixedCacheEvict
  public PanteraDocumentUpdateResponse update(
    final Project project,
    final String docId,
    final List<Provenance> provenances,
    final Map<String, List<String>> propertyKeyToNewValues
  ) {
    // Convert the property key values to a partial upset objectnode
    final ObjectNode objectNode = objectMapper.createObjectNode();
    propertyKeyToNewValues.forEach((key, value) -> {
      final String mapping = propertyDescriptorService
        .findByKey(project, key)
        .getMapping();
      JSONUtil.expand(mapping, value, objectNode);
    });

    for (Provenance provenance : provenances) {
      provenance.setTimestampMS(System.currentTimeMillis());
      provenance.setUserId(currentUserService.get().getId());
      provenance.setUserName(currentUserService.get().getName());
    }

    PanteraDocumentUpdateResponse provenanceResponse = addProvenancesToDocument(
      project,
      docId,
      provenances
    );

    if (!provenanceResponse.getErrors().isEmpty()) {
      log.error(
        "Error updating provenance: {}",
        provenanceResponse.getErrors()
      );
    }

    return updateProjectDocument(project, docId, List.of(objectNode));
  }

  /**
   * Updates the document with the given id in the given project with the new property values
   * @param project                 the project to update the document in
   * @param docId                   the id of the document to update
   * @param propertyKeyToNewValues  a map of property key to new values for that property
   */
  @PrefixedCacheEvict
  public PanteraDocumentUpdateResponse update(
    final Project project,
    final String docId,
    final Map<String, List<String>> propertyKeyToNewValues
  ) {
    final PanteraDocument document = getDocument(project, docId);
    List<Provenance> provenances = new ArrayList<>();

    for (String key : propertyKeyToNewValues.keySet()) {
      ProvenanceType provenanceType = ProvenanceType.fromPropertyKey(key);

      ObjectNode beforeNode = objectMapper.createObjectNode();
      beforeNode.put("preview", document.getByKey(key).getFirst().getValue());

      ObjectNode afterNode = objectMapper.createObjectNode();
      afterNode.put("preview", propertyKeyToNewValues.get(key).getFirst());

      Provenance prov = new Provenance()
        .setType(provenanceType)
        .setBefore(beforeNode)
        .setAfter(afterNode);
      provenances.add(prov);
    }

    return update(project, docId, provenances, propertyKeyToNewValues);
  }

  /**
   * Adds provenance information to a document and updates the document in the given project.
   *
   * @param project the project to which the document belongs
   * @param docId the identifier of the document to be updated
   * @param provenances a list of provenances containing details such as user information,
   *                    timestamp, type, and the changes made
   * @return a {@code PanteraDocumentUpdateResponse} indicating the result of the document update process
   */
  private PanteraDocumentUpdateResponse addProvenancesToDocument(
    final Project project,
    final String docId,
    final List<Provenance> provenances
  ) {
    List<ObjectNode> provenanceNode =
      provenanceService.generateUpdatedProvenanceNode(
        project.getIndexName(),
        docId,
        provenances
      );
    return updateProjectDocument(project, docId, provenanceNode);
  }

  /**
   * Updates the document with the given id in the given project with the new documents
   * @param project   the project to update the document in
   * @param docId     the id of the document to update
   * @param documents the documents to upsert
   */
  @PrefixedCacheEvict
  public PanteraDocumentUpdateResponse updateProjectDocument(
    final Project project,
    final String docId,
    final List<ObjectNode> documents
  ) {
    final BulkResponse response = elasticsearchService.upsert(
      project.getIndexName(),
      documents,
      List.of(docId)
    );
    elasticsearchService.refresh(project.getIndexName());
    final PanteraDocument updatedDocument = getDocument(project, docId);
    final List<PanteraError> errors = response
      .items()
      .stream()
      .filter(item -> item.error() != null)
      .map(item -> PanteraError.fromErrorCause(item.error()))
      .collect(Collectors.toList());
    final PanteraDocumentUpdateResponse updateResponse =
      new PanteraDocumentUpdateResponse();
    updateResponse.setDocument(updatedDocument);
    updateResponse.setErrors(errors);
    return updateResponse;
  }
}

