package software.uncharted.pantera.controller;

import co.elastic.clients.elasticsearch._types.SortOrder;
import java.util.Arrays;
import java.util.Comparator;
import java.util.HashMap;
import java.util.List;
import java.util.Map;
import java.util.Set;
import java.util.stream.Collectors;
import java.util.stream.Stream;
import lombok.RequiredArgsConstructor;
import org.springframework.data.domain.Page;
import org.springframework.http.ResponseEntity;
import org.springframework.web.bind.annotation.DeleteMapping;
import org.springframework.web.bind.annotation.GetMapping;
import org.springframework.web.bind.annotation.PathVariable;
import org.springframework.web.bind.annotation.PostMapping;
import org.springframework.web.bind.annotation.PutMapping;
import org.springframework.web.bind.annotation.RequestBody;
import org.springframework.web.bind.annotation.RequestMapping;
import org.springframework.web.bind.annotation.RequestParam;
import org.springframework.web.bind.annotation.RestController;
import software.uncharted.pantera.TimelineResult;
import software.uncharted.pantera.annotations.HasProjectAccess;
import software.uncharted.pantera.model.AggregateOperation;
import software.uncharted.pantera.model.MinMax;
import software.uncharted.pantera.model.TimelineInterval;
import software.uncharted.pantera.model.facets.CategoricalFacetDisplayState;
import software.uncharted.pantera.model.project.Project;
import software.uncharted.pantera.model.project.ProjectPermissionLevel;
import software.uncharted.pantera.model.provenance.ProjectDataUpdateRequest;
import software.uncharted.pantera.model.provenance.Provenance;
import software.uncharted.pantera.model.search.ElasticsearchPage;
import software.uncharted.pantera.model.search.FacetsResult;
import software.uncharted.pantera.model.search.GeoreferencingResponse;
import software.uncharted.pantera.model.search.LocationResponse;
import software.uncharted.pantera.model.search.PanteraDocument;
import software.uncharted.pantera.model.search.PanteraDocumentUpdateResponse;
import software.uncharted.pantera.model.search.PanteraSearchRequest;
import software.uncharted.pantera.model.search.PropertyDescriptor;
import software.uncharted.pantera.service.ProjectDataService;
import software.uncharted.pantera.service.ProjectService;
import software.uncharted.pantera.service.PropertyDescriptorService;
import software.uncharted.pipeline.common.features.Georeferencing;
import software.uncharted.pipeline.common.features.PropertyTag;

@RestController
@RequestMapping("/api/projects/{id}/data")
@HasProjectAccess
@RequiredArgsConstructor
public class ProjectsDataController {

  private final ProjectDataService projectDataService;
  private final ProjectService projectService;
  private final PropertyDescriptorService propertyDescriptorService;

  /**
   * Get the number of documents in the project
   * @param id  The project id
   * @return    The number of documents in the project
   */
  @GetMapping("/document-count")
  public ResponseEntity<Long> getDocumentCount(
    @PathVariable("id") String id,
    @RequestParam(required = false) String queryString,
    @RequestParam(required = false) String filter
  ) {
    final Project project = projectService.fetch(id);
    if (
      (queryString == null || queryString.isEmpty()) &&
      (filter == null || filter.isEmpty())
    ) {
      return ResponseEntity.ok(projectDataService.getDocumentCount(project));
    }
    final PanteraSearchRequest request = new PanteraSearchRequest()
      .setQuery(queryString == null ? "*" : queryString)
      .setFilter(filter);
    return ResponseEntity.ok(
      projectDataService.getDocumentCount(project, request)
    );
  }

  /**
   * Refresh the data for the project
   * @param id  The project id
   * @return    A response entity with no content
   */
  @GetMapping("/refresh")
  public ResponseEntity<Void> refresh(@PathVariable("id") String id) {
    final Project project = projectService.fetch(id);
    projectDataService.refresh(project);
    return ResponseEntity.ok().build();
  }

  /**
   * Get the property descriptors for the project that are configured. They may not all be used in the project
   * @param id  The project id
   * @return    The property descriptors for the project
   */
  @GetMapping("/properties")
  public ResponseEntity<List<PropertyDescriptor>> getPropertyDescriptors(
    @PathVariable("id") String id
  ) {
    final Project project = projectService.fetch(id);
    return ResponseEntity.ok(
      propertyDescriptorService
        .getConfiguredPropertyDescriptors(project)
        .stream()
        .sorted(Comparator.comparing(PropertyDescriptor::getKey))
        .collect(Collectors.toList())
    );
  }

  @GetMapping("/properties/page")
  public ResponseEntity<Page<PropertyDescriptor>> getPropertyDescriptorPage(
    @PathVariable("id") String id,
    @RequestParam(required = false, defaultValue = "0") Integer page,
    @RequestParam(required = false, defaultValue = "5") Integer rows,
    @RequestParam(required = false) String sortField,
    @RequestParam(required = false) Integer sortOrder,
    @RequestParam(required = false, defaultValue = "") String queryString
  ) {
    final Project project = projectService.fetch(id);
    return ResponseEntity.ok(
      propertyDescriptorService.getConfiguredPropertyDescriptors(
        project,
        page,
        rows,
        sortField,
        sortOrder,
        queryString
      )
    );
  }

  /**
   * Get the property descriptors for the project that exist within the data
   * @param id  The project id
   * @return    The property descriptors for the project
   */
  @GetMapping("/properties/existing")
  public ResponseEntity<
    List<PropertyDescriptor>
  > getExistingPropertyDescriptors(@PathVariable("id") String id) {
    final Project project = projectService.fetch(id);
    return ResponseEntity.ok(
      propertyDescriptorService.getExistingPropertyDescriptors(project)
    );
  }

  @PostMapping("/{docId}/update")
  @HasProjectAccess(level = ProjectPermissionLevel.WRITE)
  public ResponseEntity<PanteraDocumentUpdateResponse> updateProjectData(
    @PathVariable("id") String id,
    @PathVariable("docId") String docId,
    @RequestBody ProjectDataUpdateRequest request
  ) {
    final List<Provenance> provenances = request.getProvenances();
    final Map<String, List<String>> propertyKeyToNewValue =
      request.getPropertyKeyToNewValue();

    final Project project = projectService.fetch(id);
    PanteraDocumentUpdateResponse updatedDocument;

    if (provenances != null && !provenances.isEmpty()) {
      updatedDocument = projectDataService.update(
        project,
        docId,
        provenances,
        propertyKeyToNewValue
      );
    } else {
      updatedDocument = projectDataService.update(
        project,
        docId,
        propertyKeyToNewValue
      );
    }

    return ResponseEntity.ok(updatedDocument);
  }

  /**
   * Updates a property descriptor
   * @param id                  The project id
   * @param propertyDescriptor  The property descriptor to update
   * @return
   */
  @PutMapping("/properties")
  @HasProjectAccess(level = ProjectPermissionLevel.WRITE)
  public ResponseEntity<PropertyDescriptor> updatePropertyDescriptor(
    @PathVariable("id") String id,
    @RequestBody PropertyDescriptor propertyDescriptor
  ) {
    final Project project = projectService.fetch(id);
    return ResponseEntity.ok(
      propertyDescriptorService.update(project, propertyDescriptor)
    );
  }

  /**
   * Create a property descriptor
   * @param id                  The project id
   * @param propertyDescriptor  The property descriptor to create
   * @return                    The created property descriptor
   */
  @PostMapping("/properties")
  @HasProjectAccess(level = ProjectPermissionLevel.WRITE)
  public ResponseEntity<PropertyDescriptor> createPropertyDescriptors(
    @PathVariable("id") String id,
    @RequestBody PropertyDescriptor propertyDescriptor
  ) {
    final Project project = projectService.fetch(id);
    return ResponseEntity.ok(
      propertyDescriptorService.create(project, propertyDescriptor)
    );
  }

  /**
   * Remove a property descriptor
   * @param id  The project id
   * @param key The key of the property descriptor to remove
   * @return    A response entity with no content
   */
  @DeleteMapping("/properties/{key}")
  @HasProjectAccess(level = ProjectPermissionLevel.WRITE)
  public ResponseEntity<Void> removePropertyDescriptor(
    @PathVariable("id") String id,
    @PathVariable("key") String key
  ) {
    final Project project = projectService.fetch(id);
    propertyDescriptorService.remove(project, key);
    return ResponseEntity.ok().build();
  }

  @GetMapping("/unmapped-properties")
  public ResponseEntity<List<String>> getUnmappedProperties(
    @PathVariable("id") String id
  ) {
    final Project project = projectService.fetch(id);
    return ResponseEntity.ok(
      propertyDescriptorService.getUnmappedProperties(project)
    );
  }

  /**
   * Get the documents for the project
   * @param id          The project id
   * @param page        The page number
   * @param rows        The number of rows per page
   * @param sortField   The field to sort by
   * @param sortOrder   The sort order
   * @return            A Page of documents
   */
  @GetMapping
  public ResponseEntity<ElasticsearchPage<PanteraDocument>> search(
    @PathVariable("id") String id,
    @RequestParam(required = false, defaultValue = "*") String queryString,
    @RequestParam(required = false) String filter,
    @RequestParam(required = false, defaultValue = "0") Integer page,
    @RequestParam(required = false, defaultValue = "5") Integer rows,
    @RequestParam(required = false) String sortField,
    @RequestParam(required = false) Integer sortOrder
  ) {
    final Project project = projectService.fetch(id);
    final PanteraSearchRequest request = new PanteraSearchRequest()
      .setQuery(queryString)
      .setFilter(filter)
      .setPage(page)
      .setSize(rows > 10000 ? 10000 : rows)
      .setSort(sortField)
      .setOrder(
        sortOrder == null || sortOrder == 1 ? SortOrder.Asc : SortOrder.Desc
      )
      .setTrackTotalHits(true);
    return ResponseEntity.ok(projectDataService.search(project, request));
  }

  /**
   * Search for document IDs only (similar to search but returns only the list of document IDs)
   * @param id          The project id
   * @param queryString The query string
   * @param filter      The filter string
   * @param rows        The number of rows per page
   * @param sortField   The field to sort by
   * @param sortOrder   The sort order
   * @return            A list of document IDs
   */
  @GetMapping("/searchIds")
  public ResponseEntity<List<String>> searchIds(
    @PathVariable("id") String id,
    @RequestParam(required = false, defaultValue = "*") String queryString,
    @RequestParam(required = false) String filter,
    @RequestParam(required = false, defaultValue = "10000") Integer rows,
    @RequestParam(required = false) String sortField,
    @RequestParam(required = false) Integer sortOrder
  ) {
    final Project project = projectService.fetch(id);
    final PanteraSearchRequest request = new PanteraSearchRequest()
      .setQuery(queryString)
      .setFilter(filter)
      .setPage(0)
      .setSize(rows > 10000 ? 10000 : rows)
      .setSort(sortField)
      .setOrder(
        sortOrder == null || sortOrder == 1 ? SortOrder.Asc : SortOrder.Desc
      )
      .setTrackTotalHits(true);

    ElasticsearchPage<PanteraDocument> searchResult = projectDataService.search(
      project,
      request
    );
    List<String> documentIds = searchResult
      .getContent()
      .stream()
      .map(PanteraDocument::getId)
      .collect(Collectors.toList());

    return ResponseEntity.ok(documentIds);
  }

  /**
   * Gets a single document by its ID.
   * @param id          The project ID
   * @param documentId  The ID of the document to get
   * @return            A PanteraDocument
   */
  @GetMapping("/{documentId}")
  public ResponseEntity<PanteraDocument> getDocument(
    @PathVariable("id") String id,
    @PathVariable("documentId") String documentId
  ) {
    final Project project = projectService.fetch(id);
    return ResponseEntity.ok(
      projectDataService.getDocument(project, documentId)
    );
  }

  @DeleteMapping("/{documentId}")
  @HasProjectAccess(level = ProjectPermissionLevel.ADMIN)
  public ResponseEntity<Void> deleteDocument(
    @PathVariable("id") String id,
    @PathVariable("documentId") String documentId
  ) {
    final Project project = projectService.fetch(id);
    projectDataService.deleteDocument(project, documentId);
    return ResponseEntity.ok().build();
  }

  /**
   * Compute a timeline date histogram. The timeline is computed for the given property key and interval and based upon
   * the query. The bucket count is the number of buckets to return. The max timestamp a timestamp to use as the "rightmost"
   * bucket. If not provided, we calculate the max timestamp from the data and query. This is to show the most recent data
   * first.
   *
   * @param id                The project id
   * @param query             The query string
   * @param filter            The filter string
   * @param interval          The {@link TimelineInterval} to use
   * @param propertyKey       The property key to use. Will return 400 if this is not tagged as a {@link PropertyTag#DATE}
   * @param bucketCount       The number of buckets to return
   * @param maxTimestampMsIn  A timestamp contained in the max (rightmost) bucket
   * @return                  The {@link TimelineResult}
   */
  @GetMapping("/timeline")
  public ResponseEntity<TimelineResult> getTimeline(
    @PathVariable("id") String id,
    @RequestParam(required = false, defaultValue = "*") String query,
    @RequestParam(required = false, defaultValue = "") String filter,
    @RequestParam String interval,
    @RequestParam String propertyKey,
    @RequestParam int bucketCount,
    @RequestParam(
      value = "maxTimestamp",
      required = false
    ) Long maxTimestampMsIn
  ) {
    final Project project = projectService.fetch(id);
    final PanteraSearchRequest request = new PanteraSearchRequest()
      .setQuery(query)
      .setFilter(filter)
      .setTrackTotalHits(false);

    final PropertyDescriptor propertyDescriptor =
      propertyDescriptorService.findByKey(project, propertyKey);

    Long maxTimestampMs = maxTimestampMsIn;
    if (maxTimestampMsIn == null) {
      final MinMax minMax = projectDataService.getMinMax(
        project,
        request,
        propertyDescriptor
      );
      maxTimestampMs = minMax.getMax().longValue();
    }

    final TimelineResult result = projectDataService.getTimeline(
      project,
      request,
      propertyDescriptor,
      TimelineInterval.valueOf(interval.toUpperCase()),
      bucketCount,
      maxTimestampMs
    );

    return ResponseEntity.ok(result);
  }

  @GetMapping("/bar-chart")
  public ResponseEntity<
    Map<String, Map<AggregateOperation, Number>>
  > getBarChartData(
    @PathVariable("id") String id,
    @RequestParam(required = false, defaultValue = "*") String query,
    @RequestParam(required = false) String xField,
    @RequestParam(required = false) String yField,
    @RequestParam(required = false) String[] operation
  ) {
    final Project project = projectService.fetch(id);
    final PanteraSearchRequest request = new PanteraSearchRequest()
      .setQuery(query)
      .setTrackTotalHits(false);
    return ResponseEntity.ok(
      projectDataService.aggregate(
        project,
        request,
        xField,
        yField,
        Stream.of(operation)
          .map(AggregateOperation::valueOf)
          .collect(Collectors.toList())
      )
    );
  }

  @GetMapping("/locations/layers")
  public ResponseEntity<Set<String>> getLocationLayers(
    @PathVariable("id") String id
  ) {
    final Project project = projectService.fetch(id);
    return ResponseEntity.ok(projectDataService.getLocationLayers(project));
  }

  @GetMapping("/locations")
  public ResponseEntity<LocationResponse> getLocations(
    @PathVariable("id") String id,
    @RequestParam(
      value = "query",
      required = false,
      defaultValue = "*"
    ) String query,
    @RequestParam(value = "filter", required = false) String filter,
    @RequestParam("layers") List<String> layers
  ) {
    final Project project = projectService.fetch(id);
    final PanteraSearchRequest request = new PanteraSearchRequest()
      .setQuery(query)
      .setFilter(filter)
      .setTrackTotalHits(false)
      .setSize(0);
    return ResponseEntity.ok(
      projectDataService.getLocations(project, request, layers)
    );
  }

  @GetMapping("/georeferencings")
  public ResponseEntity<GeoreferencingResponse> getGeoreferencings(
    @PathVariable("id") String id,
    @RequestParam(
      value = "query",
      required = false,
      defaultValue = "*"
    ) String query,
    @RequestParam(value = "filter", required = false) String filter,
    @RequestParam("layers") List<String> layers
  ) {
    final Project project = projectService.fetch(id);
    final PanteraSearchRequest request = new PanteraSearchRequest()
      .setQuery(query)
      .setFilter(filter);
    return ResponseEntity.ok(
      projectDataService.getGeoreferencings(project, request)
    );
  }

  @PostMapping("/georeferencings/{docId}/update")
  @HasProjectAccess(level = ProjectPermissionLevel.WRITE)
  public ResponseEntity<PanteraDocumentUpdateResponse> updateGeoreferencings(
    @PathVariable("id") String id,
    @PathVariable("docId") String docId,
    @RequestBody Map<String, List<Georeferencing>> updatedGeoreferenceLayers
  ) {
    final Project project = projectService.fetch(id);
    final PanteraDocumentUpdateResponse updatedDocument =
      projectDataService.updateGeoreferences(
        project,
        docId,
        updatedGeoreferenceLayers
      );
    return ResponseEntity.ok(updatedDocument);
  }

  /**
   * Get the facets for the project
   * @param id      The project id
   * @param queryString   The query string.
   * @param filter  The lucene filter string to use for the search
   * @param keys    The {@link PropertyDescriptor} keys to facet on.
   * @return        The {@link FacetsResult} for the project
   */
  @PostMapping("/getFacets")
  public ResponseEntity<FacetsResult> getFacets(
    @PathVariable("id") String id,
    @RequestParam(required = false, defaultValue = "*") String queryString,
    @RequestParam(required = false) String filter,
    @RequestBody(required = false) Map<
      String,
      CategoricalFacetDisplayState
    > displayState,
    @RequestParam String[] keys
  ) {
    final Project project = projectService.fetch(id);
    final PanteraSearchRequest request = new PanteraSearchRequest()
      .setSize(0)
      .setQuery(queryString)
      .setFilter(filter)
      .setTrackTotalHits(true)
      .setTrackScores(false);

    final FacetsResult result = projectDataService.getFacets(
      project,
      request,
      Arrays.asList(keys),
      (displayState == null) ? new HashMap<>() : displayState
    );
    return ResponseEntity.ok(result);
  }

  /**
   * Get the facets for a specific document in the project
   * @param id            The project id
   * @param documentId    The document id
   * @param queryString   The query string.
   * @param filter        The filter string
   * @param displayState  The display state for the facets
   * @param keys          The {@link PropertyDescriptor} keys to facet on.
   * @return              The {@link FacetsResult} for the project and document
   */
  @PostMapping("/{documentId}/getFacets")
  public ResponseEntity<FacetsResult> getDocumentFacets(
    @PathVariable("id") String id,
    @PathVariable("documentId") String documentId,
    @RequestParam(required = false, defaultValue = "*") String queryString,
    @RequestParam(required = false) String filter,
    @RequestBody(required = false) Map<
      String,
      CategoricalFacetDisplayState
    > displayState,
    @RequestParam String[] keys
  ) {
    final Project project = projectService.fetch(id);

    // Create a document-specific filter that combines the existing filter with document ID
    String documentFilter = "id:\"" + documentId + "\"";
    String combinedFilter;

    if (filter != null && !filter.trim().isEmpty()) {
      combinedFilter = "(" + filter + ") AND " + documentFilter;
    } else {
      combinedFilter = documentFilter;
    }

    final PanteraSearchRequest request = new PanteraSearchRequest()
      .setSize(0)
      .setQuery(queryString)
      .setFilter(combinedFilter) // Use the combined filter that includes document ID
      .setTrackTotalHits(true)
      .setTrackScores(false);

    final FacetsResult result = projectDataService.getFacets(
      project,
      request,
      Arrays.asList(keys),
      (displayState == null) ? new HashMap() : displayState
    );
    return ResponseEntity.ok(result);
  }
}

