import { AxiosRequestConfig } from "axios";

export interface HttpClient {

    request<R>(requestConfig: { method: string; url: string; queryParams?: any; data?: any; copyFn?: (data: R) => R; options?: AxiosRequestConfig; }): RestResponse<R>;
}

export class RestApplicationClient {

    constructor(protected httpClient: HttpClient) {
    }

    /**
     * HTTP GET /api/admin/events
     * Java method: software.uncharted.pantera.controller.admin.AdminController.getEvents
     */
    getEvents(queryParams?: { page?: number; rows?: number; sortField?: string; sortOrder?: number; queryString?: string; }, options?: AxiosRequestConfig): RestResponse<Page<AuditEvent>> {
        return this.httpClient.request({ method: "GET", url: uriEncoding`api/admin/events`, queryParams: queryParams, options: options });
    }

    /**
     * HTTP POST /api/admin/group
     * Java method: software.uncharted.pantera.controller.admin.AdminController.createGroup
     */
    createGroup(model: GroupDTO, options?: AxiosRequestConfig): RestResponse<Group> {
        return this.httpClient.request({ method: "POST", url: uriEncoding`api/admin/group`, data: model, options: options });
    }

    /**
     * HTTP GET /api/admin/group/{id}
     * Java method: software.uncharted.pantera.controller.admin.AdminController.getGroup
     */
    getGroup(id: string, options?: AxiosRequestConfig): RestResponse<Group> {
        return this.httpClient.request({ method: "GET", url: uriEncoding`api/admin/group/${id}`, options: options });
