import API from '@/services/API';
import { LoadDataParams } from '@/composables/useGenericTableState';
import {
  CategoricalFacetDisplayState,
  ElasticsearchPage,
  FacetsResult,
  LocationResponse,
  PanteraDocument,
  PropertyDescriptor,
} from '@/types/Types';

// Normalize filters that target missing values ("N/A") so they work with Lucene syntax consistently
function normalizeMissingValueFilter(filter: string): string {
  let result = filter.replace('"N/A"', '*');
  if (result.startsWith('(') && result.endsWith(')')) {
    result = result.substring(1, result.length - 1);
  }
  return `-${result}`;
}

/**
 * Get the number of documents in a project
 * @param projectId The ID of the project
 */
async function getDocumentCount(projectId: string): Promise<number> {
  const response = await API.get(`/projects/${projectId}/data/document-count`);
  return response.data;
}

/**
 * Refresh the data in a project
 * @param projectId The ID of the project
 */
async function refresh(projectId: string): Promise<void> {
  await API.get(`/projects/${projectId}/data/refresh`);
}

/**
 * Get the rows of data from a project given the query parameters
 * @param projectId The ID of the project
 * @param params    The query parameters {@link LoadDataParams}
 */
async function getRows(
  projectId: string,
  params?: LoadDataParams,
): Promise<ElasticsearchPage<PanteraDocument>> {
  let apiParams = params ?? null;
  if (params && params.filter && params.filter.includes('"N/A"')) {
    apiParams = {
      ...params,
      filter: normalizeMissingValueFilter(params.filter),
    } as LoadDataParams;
  }

  const response = await API.get(`/projects/${projectId}/data`, {
    params: apiParams,
  });
  return response.data;
}

/**
 * Create property descriptors for a project
 * @param projectId           The ID of the project
 * @param propertyDescriptor The property descriptor to create
 */
async function createPropertyDescriptor(
  projectId: string,
  propertyDescriptor: PropertyDescriptor,
): Promise<PropertyDescriptor> {
  return API.post(`/projects/${projectId}/data/properties`, propertyDescriptor);
}

/**
 * Update property descriptors for a project
 * @param projectId             The ID of the project
 * @param propertyDescriptor    The property descriptor to update
 */
async function updatePropertyDescriptor(
  projectId: string,
  propertyDescriptor: PropertyDescriptor,
): Promise<PropertyDescriptor> {
  return API.put(`/projects/${projectId}/data/properties`, propertyDescriptor);
}

/**
 * Delete property descriptors for a project
 * @param projectId             The ID of the project
 * @param propertyDescriptor    The property descriptor to delete
 */
async function deletePropertyDescriptor(
  projectId: string,
  propertyDescriptor: PropertyDescriptor,
): Promise<void> {
  return API.delete(
    `/projects/${projectId}/data/properties/${propertyDescriptor.key}`,
  );
}

/**
 * Bulk delete properties from all documents in a project
 * @param projectId     The ID of the project
 * @param propertyKeys  Array of property keys to remove from all documents
 * @returns Promise containing task ID and status
 */
async function bulkDeleteProperties(
  projectId: string,
  propertyKeys: string[],
): Promise<{ taskId: string; status: string }> {
  const response = await API.delete(
    `/projects/${projectId}/data/properties/bulk`,
    {
      data: propertyKeys,
    },
  );
  return response.data;
}

/**
 * Gets a list of unmapped properties for a project. These are properties that exist in the elasticsearch but are not
 * mapped to a property descriptor
 * @param projectId The ID of the project
 */
async function getUnmappedProperties(projectId: string): Promise<string[]> {
  const response = await API.get(
    `/projects/${projectId}/data/unmapped-properties`,
  );
  return response.data;
}

/**
 * queryString: The free-text lucene query string that the user enters into the search bar.
 * filter: The lucene query string that is formulated based on the facet selection state.
 * displayState: The query, sort order, and visible row count of any categorical facets with non-default values.
 */
export interface GetFacetDataParams {
  queryString: string;
  filter: string;
  displayState: {
    [facetKey: string]: CategoricalFacetDisplayState;
  };
}

/**
 * Fetches and aggregates the data for each of the specified property descriptors so that they can
 * be rendered as facets without needing to fetch all data in the project and aggregate it on the
 * frontend.
 * @param projectId The ID of the project
 * @param propertyDescriptorKeys The unique identifiers for the property descriptors.
 * @param params Specifies which results and how many are returned for each facet, in what order.
 * @returns An object containing the pre-aggregated and filtered data required to render facets.
 */
async function getFacetData(
  projectId: string,
  propertyDescriptorKeys: string[],
  params: GetFacetDataParams,
): Promise<FacetsResult> {
  const filterParam = params.filter.includes('"N/A"')
    ? normalizeMissingValueFilter(params.filter)
    : params.filter;

  const response = await API.post(
    `/projects/${projectId}/data/getFacets`,
    params.displayState,
    {
      params: {
        filter: filterParam,
        queryString: params.queryString,
        keys: propertyDescriptorKeys,
      },
    },
  );
  return response.data;
}

/**
 * Fetches location data for a project with optional query, filter and layer parameters.
 * @param projectId The ID of the project
 * @param params Query parameters including query string, filter, and layers
 * @returns A promise containing the location response data
 */
async function getLocations(
  projectId: string,
  params: {
    query?: string;
    filter?: string;
    layers: string[];
  },
): Promise<LocationResponse> {
  const filterParam =
    params.filter && params.filter.includes('"N/A"')
      ? normalizeMissingValueFilter(params.filter)
      : params.filter;

  const response = await API.get(`/projects/${projectId}/data/locations`, {
    params: {
      query: params.query,
      filter: filterParam,
      layers: params.layers,
    },
  });
  return response.data;
}

/**
 * Fetches and aggregates the data for each of the specified property descriptors for a specific document so that they can
 * be rendered as facets without needing to fetch all data in the project and aggregate it on the
 * frontend.
 * @param projectId The ID of the project
 * @param documentId The ID of the document to get facets for
 * @param propertyDescriptorKeys The unique identifiers for the property descriptors.
 * @param params Specifies which results and how many are returned for each facet, in what order.
 * @returns An object containing the pre-aggregated and filtered data required to render facets.
 */
async function getDocumentFacetData(
  projectId: string,
  documentId: string,
  propertyDescriptorKeys: string[],
  params: GetFacetDataParams,
): Promise<FacetsResult> {
  const response = await API.post(
    `/projects/${projectId}/data/${documentId}/getFacets`,
    params.displayState,
    {
      params: {
        filter: params.filter,
        queryString: params.queryString,
        keys: propertyDescriptorKeys,
      },
    },
  );
  return response.data;
}

export default {
  getDocumentCount,
  refresh,
  getRows,
  createPropertyDescriptor,
  updatePropertyDescriptor,
  deletePropertyDescriptor,
  bulkDeleteProperties,
  getUnmappedProperties,
  getFacetData,
  getLocations,
  getDocumentFacetData,
};

