const onDelete = async (propertyDescriptor: PropertyDescriptor) => {
  Logger.info('Delete', propertyDescriptor);
  confirm.require({
    message: 'Are you sure you wish to delete this column?',
    header: 'Delete column',
    icon: 'pi pi-exclamation-triangle',
    rejectClass: 'p-button-secondary p-button-outlined',
    acceptClass: 'p-button-danger',
    accept: async () => {
      if (projectStore?.project) {
        await ProjectDataService.deletePropertyDescriptor(
          projectStore.project.id,
          propertyDescriptor,
        );
        await propertyTable.value?.loadTableData();
      }
    },
