const onBulkDelete = () => {
  if (selectedProperties.value.length === 0) return;

  const propertyNames = selectedProperties.value
    .map(p => p.name)
    .join(', ');

  confirm.require({
    message: `Are you sure you want to remove properties "${propertyNames}" from ALL documents? This action cannot be undone.`,
    header: 'Bulk Delete Properties',
    icon: 'pi pi-exclamation-triangle',
    rejectClass: 'p-button-secondary p-button-outlined',
    acceptClass: 'p-button-danger',
    accept: async () => {
      if (projectStore.project && selectedProperties.value.length > 0) {
        bulkDeleteLoading.value = true;
        try {
          const propertyKeys = selectedProperties.value.map(p => p.key);
          const result = await ProjectDataService.bulkDeleteProperties(
            projectStore.project.id,
            propertyKeys,
          );

          const logger = Logger();
          logger.info(
            `Bulk property deletion initiated with task ID: ${result.taskId}`,
          );

          // Clear selection and reload table
          selectedProperties.value = [];
          await propertyTable.value.loadTableData();
        } catch (error) {
          const logger = Logger();
          logger.error('Failed to execute bulk property deletion:', error);
        } finally {
          bulkDeleteLoading.value = false;
        }
      }
    },
    reject: () => {},
  });
};

