/**
 * Updates the document with the given id in the given project with the new property values
 * @param project                 the project to update the document in
 * @param docId                   the id of the document to update
 * @param propertyKeyToNewValues  a map of property key to new values for that property
 */
@PrefixedCacheEvict
public PanteraDocumentUpdateResponse update(
  final Project project,
  final String docId,
  final Map<String, List<String>> propertyKeyToNewValues
) {
  final PanteraDocument document = getDocument(project, docId);
  List<Provenance> provenances = new ArrayList<>();

  for (String key : propertyKeyToNewValues.keySet()) {
    ProvenanceType provenanceType = ProvenanceType.fromPropertyKey(key);

    ObjectNode beforeNode = objectMapper.createObjectNode();
    beforeNode.put("preview", document.getByKey(key).getFirst().getValue());

    ObjectNode afterNode = objectMapper.createObjectNode();
    afterNode.put("preview", propertyKeyToNewValues.get(key).getFirst());

    Provenance prov = new Provenance()
      .setType(provenanceType)
      .setBefore(beforeNode)
      .setAfter(afterNode);
    provenances.add(prov);
  }

  return update(project, docId, provenances, propertyKeyToNewValues);
}

/**
 * Adds provenance information to a document and updates the document in the given project.
 *
 * @param project the project to which the document belongs
 * @param docId the identifier of the document to be updated
 * @param provenances a list of provenances containing details such as user information,
 *                    timestamp, type, and the changes made
 * @return a {@code PanteraDocumentUpdateResponse} indicating the result of the document update process
 */
private PanteraDocumentUpdateResponse addProvenancesToDocument(
  final Project project,
  final String docId,
  final List<Provenance> provenances
) {
  List<ObjectNode> provenanceNode =
    provenanceService.generateUpdatedProvenanceNode(
      project.getIndexName(),
      docId,
      provenances
    );
  return updateProjectDocument(project, docId, provenanceNode);
}

/**
 * Updates the document with the given id in the given project with the new documents
 * @param project   the project to update the document in
 * @param docId     the id of the document to update
 * @param documents the documents to upsert
 */
@PrefixedCacheEvict
public PanteraDocumentUpdateResponse updateProjectDocument(
  final Project project,
  final String docId,
  final List<ObjectNode> documents
) {
  final BulkResponse response = elasticsearchService.upsert(
    project.getIndexName(),
    documents,
    List.of(docId)
  );
  elasticsearchService.refresh(project.getIndexName());
  final PanteraDocument updatedDocument = getDocument(project, docId);
  final List<PanteraError> errors = response
    .items()
    .stream()
    .filter(item -> item.error() != null)
    .map(item -> PanteraError.fromErrorCause(item.error()))
    .collect(Collectors.toList());
  final PanteraDocumentUpdateResponse updateResponse =
    new PanteraDocumentUpdateResponse();
  updateResponse.setDocument(updatedDocument);
  updateResponse.setErrors(errors);

