package software.uncharted.pantera.model.search;

import java.io.Serializable;
import java.util.List;
import java.util.Map;
import lombok.Data;
import lombok.NoArgsConstructor;
import lombok.experimental.Accessors;
import software.uncharted.pantera.model.provenance.Provenance;
import software.uncharted.pipeline.common.TSIgnore;
import software.uncharted.pipeline.common.TSModel;

@Accessors(chain = true)
@NoArgsConstructor
@Data
@TSModel
public class PanteraDocument implements Serializable {

  String id;
  Double score;
  List<Property> properties;
  Map<String, List<Location>> locations;

  @TSIgnore
  List<Provenance> provenances;

  /**
   * Get the first property with the given key
   *
   * @param key The key to search for
   * @return The first property with the given key, or null if no property with
   *         the given key is found
   */
  public Property getFirstByKey(String key) {
    return getByKey(key).stream().findFirst().orElse(null);
  }

  /**
   * Get all properties with the given key
   *
   * @param key The key to search for
   * @return A list of all properties with the given key
   */
  public List<Property> getByKey(String key) {
    return properties.stream().filter(p -> p.getKey().equals(key)).toList();
  }
}

