   562→   * Insert the given documents into the given index with the given ids
   563→   *
   564→   * @param index     The index to insert the documents into
   565→   * @param documents the documents to insert
   566→   * @param ids       The ids of the documents to insert, parallel to the documents
   567→   * @return The bulk response
   568→   * @throws IOException              If the bulk insert fails
   569→   * @throws IllegalArgumentException If the number of documents and ids are not the same
   570→   */
   571→  public BulkResponse insert(
   572→    final String index,
   573→    final List documents,
   574→    final List<String> ids
   575→  ) throws IOException {
   576→    if (ids != null && documents.size() != ids.size()) {
   577→      throw new IllegalArgumentException(
   578→        "The number of documents and ids must be the same"
   579→      );
   580→    }
   581→
   582→    final List<BulkOperation> bulkOperations = new ArrayList<>();
   583→    for (int i = 0; i < documents.size(); i++) {
   584→      final String id = ids != null ? ids.get(i) : UUID.randomUUID().toString();
   585→      final Object document = documents.get(i);
   586→      bulkOperations.add(
   587→        BulkOperation.of(b ->
   588→          b.index(op -> op.index(index).id(id).document(document))
   589→        )
   590→      );
   591→    }
   592→
   593→    log.info(
   594→      "Elasticsearch | Bulk | Inserting {} documents into index {}",
   595→      documents.size(),
   596→      index
   597→    );
   598→    return client.bulk(
   599→      BulkRequest.of(bulkRequest ->
   600→        bulkRequest
   601→          .index(index)
   602→          .operations(bulkOperations)
   603→          .pipeline("full_text_search")
   604→      )
   605→    );
   606→  }
