/**
 * Bulk remove specific property values from selected documents using update by query with Painless script
 *
 * @param index the index to update
 * @param propertyKey the property key to target
 * @param valueToRemove the specific value to remove from the property
 * @param documentIds list of document IDs to update
 * @return the task id of the update operation
 */
public String bulkRemovePropertyValues(
  final String index,
  final String propertyKey,
  final String valueToRemove,
  final List<String> documentIds
) {
  if (propertyKey == null || propertyKey.isEmpty()) {
    throw new IllegalArgumentException("Property key cannot be null or empty");
  }
  if (valueToRemove == null) {
    throw new IllegalArgumentException("Value to remove cannot be null");
  }
  if (documentIds == null || documentIds.isEmpty()) {
    throw new IllegalArgumentException("Document IDs list cannot be null or empty");
  }

  try {
    // Build the update by query request with Painless script
    final ObjectNode queryNode = mapper.createObjectNode();

    // Create terms query to target specific documents
    final ObjectNode termsQuery = queryNode.putObject("query").putObject("terms");
    final ArrayNode docIdsArray = termsQuery.putArray("_id");
    documentIds.forEach(docIdsArray::add);

    // Create script to remove specific property values
    final ObjectNode scriptNode = queryNode.putObject("script");
    final String painlessScript =
      "ctx._source.properties.removeIf(prop -> prop.key == params.propertyKey && prop.value == params.valueToRemove)";
    scriptNode.put("source", painlessScript);

    final ObjectNode paramsNode = scriptNode.putObject("params");
    paramsNode.put("propertyKey", propertyKey);
    paramsNode.put("valueToRemove", valueToRemove);

    final String queryJson = queryNode.toString();

    log.info(
      "Executing bulk property value removal for property '{}' with value '{}' in {} documents",
      propertyKey,
      valueToRemove,
      documentIds.size()
    );

    // Execute update by query directly with script (no pipeline needed)
    final String taskId = updateByQueryWithScript(index, queryJson);

    log.info(
      "Started bulk property value removal for index {} with task {}",
      index,
      taskId
    );

    return taskId;

  } catch (final Exception e) {
    log.error(
      "Error executing bulk property value removal for index {} property {} value {}",
      index,
      propertyKey,
      valueToRemove,
      e
    );
    throw new RuntimeException("Failed to execute bulk property value removal", e);
  }
}

