/**
 * Execute update by query with a custom script
 *
 * @param index the index to update
 * @param queryJson the update by query JSON with script
 * @return the task id of the update operation
 */
private String updateByQueryWithScript(final String index, final String queryJson) {
  try {
    final HttpHeaders headers = new HttpHeaders();
    headers.setContentType(MediaType.APPLICATION_JSON);
    final HttpEntity<String> entity = new HttpEntity<>(queryJson, headers);

    final String url = this.url + "/" + index + "/_update_by_query?wait_for_completion=false";
    final ResponseEntity<JsonNode> response = getRestTemplate()
      .exchange(new URI(url), HttpMethod.POST, entity, JsonNode.class);

    final JsonNode body = response.getBody();
    if (body != null && body.has("task")) {
      return body.get("task").asText();
    } else {
      throw new RuntimeException("No task ID returned from update by query");
    }
  } catch (final Exception e) {
    log.error("Error executing update by query with script for index {}", index, e);
    throw new RuntimeException("Failed to execute update by query with script", e);
  }
}

