/**
 * Bulk remove properties from all documents in an index using update by query with a remove processor pipeline
 *
 * @param index the index to update
 * @param propertyMappings list of property mappings to remove from all documents
 * @return the task id of the update operation
 */
public String bulkRemoveProperties(
  final String index,
  final List<String> propertyMappings
) {
  if (propertyMappings == null || propertyMappings.isEmpty()) {
    throw new IllegalArgumentException(
      "Property mappings list cannot be null or empty"
    );
  }

  try {
    // Create a unique pipeline name for this bulk removal operation
    final String pipelineName = "bulk_remove_" + System.currentTimeMillis();

    // Build the pipeline JSON with remove processors for each property
    final ObjectNode pipelineNode = mapper.createObjectNode();
    final ArrayNode processorsArray = pipelineNode.putArray("processors");

    for (final String propertyMapping : propertyMappings) {
      final ObjectNode removeProcessor = mapper.createObjectNode();
      final ObjectNode removeConfig = removeProcessor.putObject("remove");
      removeConfig.put("field", propertyMapping);
      removeConfig.put("ignore_missing", true);
      processorsArray.add(removeProcessor);
    }

    // Create the pipeline
    final String pipelineJson = pipelineNode.toString();
    final ByteArrayInputStream inputStream = new ByteArrayInputStream(
      pipelineJson.getBytes(StandardCharsets.UTF_8)
    );

    final boolean pipelineCreated = putPipeline(pipelineName, inputStream);
    if (!pipelineCreated) {
      throw new RuntimeException("Failed to create removal pipeline");
    }

    log.info(
      "Created bulk removal pipeline {} for properties: {}",
      pipelineName,
      String.join(", ", propertyMappings)
    );

    // Execute update by query with the pipeline
    final String taskId = updateByQuery(index, pipelineName);

    log.info(
      "Started bulk property removal for index {} with task {}",
      index,
      taskId
    );

    return taskId;
  } catch (final Exception e) {
    log.error("Error executing bulk property removal for index {}", index, e);
    throw new RuntimeException("Failed to execute bulk property removal", e);
  }
}

toolUseResult.newString:
/**
 * Bulk remove specific property values from selected documents using update by query with Painless script
 *
 * @param index the index to update
 * @param propertyKey the property key to target
 * @param valueToRemove the specific value to remove from the property
 * @param documentIds list of document IDs to update
 * @return the task id of the update operation
 */
public String bulkRemovePropertyValues(
  final String index,
  final String propertyKey,
  final String valueToRemove,
  final List<String> documentIds
) {
  if (propertyKey == null || propertyKey.isEmpty()) {
    throw new IllegalArgumentException("Property key cannot be null or empty");
  }
  if (valueToRemove == null) {
    throw new IllegalArgumentException("Value to remove cannot be null");
  }
  if (documentIds == null || documentIds.isEmpty()) {
    throw new IllegalArgumentException("Document IDs list cannot be null or empty");
  }

  try {
    // Build the update by query request with Painless script
    final ObjectNode queryNode = mapper.createObjectNode();

    // Create terms query to target specific documents
    final ObjectNode termsQuery = queryNode.putObject("query").putObject("terms");
    final ArrayNode docIdsArray = termsQuery.putArray("_id");
    documentIds.forEach(docIdsArray::add);

    // Create script to remove specific property values
    final ObjectNode scriptNode = queryNode.putObject("script");
    final String painlessScript =
      "ctx._source.properties.removeIf(prop -> prop.key == params.propertyKey && prop.value == params.valueToRemove)";
    scriptNode.put("source", painlessScript);

    final ObjectNode paramsNode = scriptNode.putObject("params");
    paramsNode.put("propertyKey", propertyKey);
    paramsNode.put("valueToRemove", valueToRemove);

    final String queryJson = queryNode.toString();

    log.info(
      "Executing bulk property value removal for property '{}' with value '{}' in {} documents",
      propertyKey,
      valueToRemove,
      documentIds.size()
    );

    // Execute update by query directly with script (no pipeline needed)
    final String taskId = updateByQueryWithScript(index, queryJson);

    log.info(
      "Started bulk property value removal for index {} with task {}",
      index,
      taskId
    );

    return taskId;
  } catch (final Exception e) {
    log.error(
      "Error executing bulk property value removal for index {} property {} value {}",
      index,
      propertyKey,
      valueToRemove,
      e
    );
    throw new RuntimeException("Failed to execute bulk property value removal", e);
  }
}

/**
 * Execute update by query with a custom script
 *
 * @param index the index to update
 * @param queryJson the update by query JSON with script
 * @return the task id of the update operation
 */
private String updateByQueryWithScript(final String index, final String queryJson) {
  try {
    final HttpHeaders headers = new HttpHeaders();
    headers.setContentType(MediaType.APPLICATION_JSON);
    final HttpEntity<String> entity = new HttpEntity<>(queryJson, headers);

    final String url = this.url + "/" + index + "/_update_by_query?wait_for_completion=false";
    final ResponseEntity<JsonNode> response = getRestTemplate()
      .exchange(new URI(url), HttpMethod.POST, entity, JsonNode.class);

    final JsonNode body = response.getBody();
    if (body != null && body.has("task")) {
      return body.get("task").asText();
    } else {
      throw new RuntimeException("No task ID returned from update by query");
    }
  } catch (final Exception e) {
    log.error("Error executing update by query with script for index {}", index, e);
    throw new RuntimeException("Failed to execute update by query with script", e);
  }
}

