import org.springframework.stereotype.Service;
import org.springframework.web.client.RestTemplate;
import software.uncharted.elasticsearch.model.Task;
import software.uncharted.elasticsearch.model.TaskType;

@Service
@Slf4j
@RequiredArgsConstructor
public class ElasticsearchService {

  private final RestTemplateBuilder restTemplateBuilder;
  private final ObjectMapper mapper;

  @Value("${elasticsearch.url}")
  private String url;

  @Value("${elasticsearch.username:}")
  private String username;

  @Value("${elasticsearch.password:}")
  private String password;

  private RestTemplate restTemplate;

  @Getter
  private ElasticsearchClient client = null;

  private static final String SCROLL_TIME = "5m";

  private static final Map<String, TaskType> TASK_TYPES = new HashMap<>();

  static {
    TASK_TYPES.put("indices:data/write/reindex", REINDEX);
    TASK_TYPES.put("indices:data/write/delete/byquery", DELETE_BY_QUERY);
    TASK_TYPES.put("indices:data/write/update/byquery", UPDATE_BY_QUERY);
  }

  @Getter
  public enum TEMPLATE_TYPE {
    INDEX("index_template"),
    COMPONENT("component_template");

    private final String name;

    TEMPLATE_TYPE(final String name) {
      this.name = name;
    }

    public String getName() {
      return name;
    }

    public String getEndpoint() {
      return "_" + name;
    }

    public String getJacksonAccessor() {
      return "/" + name + "s/0/" + name;
    }
  }

  @PostConstruct
  public void init() {
    final RestClientBuilder httpClientBuilder = RestClient.builder(
      HttpHost.create(url)
    );
    if (hasAuthentication()) {
      httpClientBuilder.setDefaultHeaders(
        new Header[] {
          new BasicHeader(
            "Authorization",
            "Basic " +
            Base64.getEncoder()
              .encodeToString((username + ":" + password).getBytes())
          ),
        }
      );
    }

    // Create the new Java Client with the same low level client
    final ElasticsearchTransport transport = new RestClientTransport(
      httpClientBuilder.build(),
      new JacksonJsonpMapper(mapper)
    );

    client = new ElasticsearchClient(transport);
    try {
      client.ping();
    } catch (final IOException e) {
      log.error("Error pinging Elasticsearch", e);
    }
  }

  protected RestTemplate getRestTemplate() {
    if (restTemplate == null) {
      initRestTemplate();
    }
    return restTemplate;
  }

  public ResponseEntity<JsonNode> executeWithRestClient(
    final String endpoint,
    final HttpMethod method,
    final HttpEntity<?> entity
  ) {
    final URI uri = URI.create(
      url + (endpoint.startsWith("/") ? endpoint : "/" + endpoint)
    );
    return getRestTemplate().exchange(uri, method, entity, JsonNode.class);
  }

  private void initRestTemplate() {
    if (hasAuthentication()) {
      restTemplate = restTemplateBuilder
        .basicAuthentication(username, password)
        .build();
    } else {
      restTemplate = restTemplateBuilder.build();
    }
  }

  private String hash(String input) {
    try {
      MessageDigest digest = MessageDigest.getInstance("SHA-256");
      byte[] hash = digest.digest(input.getBytes(StandardCharsets.UTF_8));
      StringBuilder hexString = new StringBuilder();
      for (byte b : hash) {
        String hex = Integer.toHexString(0xff & b);
        if (hex.length() == 1) hexString.append('0');
        hexString.append(hex);
      }
      return hexString.toString();
    } catch (NoSuchAlgorithmException e) {
      throw new RuntimeException("SHA-256 hashing algorithm not available", e);
    }
  }

  /**
   * Checks if we need to connect to Elasticsearch with authentication
   *
   * @return True if we need to connect with authentication, false otherwise
   */
  boolean hasAuthentication() {
    return (
      username != null &&
      password != null &&
      !username.isEmpty() &&
      !password.isEmpty()
    );
  }

  /**
   * Search for documents in the given index
   *
   * @param index The index to search
   * @param query The query to execute
   * @param clazz The class of the documents to return
   * @param <T>   The type of the documents to return
   * @return The search response
   */
  public <T> SearchResponse<T> search(
    final String index,
    final Query query,
    final Class<T> clazz
  ) {
    try {
      log.info("Elasticsearch | Query | {} | {}", index, query);
      return client.search(
        s ->
          s
            .index(index)
            .query(query)
            .trackTotalHits(b -> b.enabled(true))
            .ignoreUnavailable(true),
        clazz
      );
    } catch (final IOException e) {
      log.error("Error searching index {}", index, e);
    }
    return null;
  }

  /**
   * Search for documents using a {@link SearchRequest} instance
   *
   * @param request The search request
   * @param clazz   The class of the documents to return
   * @param <T>     The type of the documents to return
   * @return The search response
   */
  public <T> SearchResponse<T> search(
    final SearchRequest request,
    final Class<T> clazz
  ) {
    try {
      log.info("Elasticsearch | Query | {}", request);
      return client.search(request, clazz);
    } catch (final Exception e) {
      if (e instanceof ElasticsearchException) {
        final ErrorCause firstCause =
          ((ElasticsearchException) e).response()
            .error()
            .rootCause()
            .getFirst();
        if (firstCause != null) {
          log.error(
            "Error searching index {} | {}: {}",
            request.index(),
            firstCause.type(),
            firstCause.reason(),
            e
          );
        }
      } else {
        log.error("Error searching index {}", request.index(), e);
      }
    }
    return null;
  }

  /**
   * Get a document by its ID
   *
   * @param index      The ID of the index that the document is in
   * @param documentId The ID of the document
   * @param clazz      The class of the document to return
   * @param <T>        The type of the document to return
   * @return The Get response
   */
  public <T> GetResponse<T> getDocument(
    final String index,
    final String documentId,
    final Class<T> clazz
  ) {
    try {
      log.info("Elasticsearch | getDocument | {} | {}", index, documentId);
      return client.get(
        getRequest -> getRequest.index(index).id(documentId),
        clazz
      );
    } catch (final IOException e) {
      log.error(
        "Error getting document {} from index {}",
        documentId,
        index,
        e
      );
    }
    return null;
  }

  /**
   * Delete a document by its ID
   *
   * @param index      The ID of the index that the document is in
   * @param documentId The ID of the document to delete
   * @return The delete response
   */
  public DeleteResponse deleteDocument(
    final String index,
    final String documentId
  ) {
    try {
      log.info("Elasticsearch | deleteDocument | {} | {}", index, documentId);
      return client.delete(deleteRequest ->
        deleteRequest.index(index).id(documentId)
      );
    } catch (final IOException e) {
      log.error(
        "Error deleting document {} from index {}",
        documentId,
        index,
        e
      );
    }
    return null;
  }

  /**
   * Search for documents using a {@link MsearchRequest} instance. It is up to the caller to ensure that the
   * order of the initial queries and the order of the responses match
   *
   * @param request The msearch request
   * @param clazz   The class of the documents to return
   * @param <T>     The type of the documents to return
   * @return The msearch response
   */
  public <T> MsearchResponse<T> msearch(
    final MsearchRequest request,
    final Class<T> clazz
  ) {
    try {
      log.info("Elasticsearch | MQuery | {}", request);
      return client.msearch(request, clazz);
    } catch (final IOException e) {
      log.error("Error searching index {}", request.index(), e);
    }
    return null;
  }

  /**
   * Scroll through the given index and produce batches of documents.  Example:
   * <pre>
   *   final SearchRequest request = SearchRequest.of(b -> b
   *    .index("books")
   *    .query(Query.of(q -> q.matchAll(m -> m)))
   *    .sort(Sort.of(s -> s.field("title")))
   *    .size(100));
   *
   *  elasticsearchService.scroll(request, Book.class, batch -> {
   *     batch.forEach(book -> // do something with the book);
   *   });
   * </pre>
   * <p>
   * <p>
   * NOTE: The scroll API is not implemented in the Java client, so we have to use the rest client to implement this. The
   * recommended way of using search_after only works when you have a sort field that is unique across all documents.
   * Since we cannot assume this for all datasets, we have to use the scroll API.
   *
   * @param request  The {@link SearchRequest} to use for the scroll. The size field indicates the number of documents
   *                 to return in each batch
   * @param clazz    The class of the documents to return
   * @param producer The producer to call with each batch of documents and for error handling
   * @param <T>      The type of the documents to return
   */
  public <T> void scroll(
    final SearchRequest request,
    final Class<T> clazz,
    final BatchProducer<List<Hit<T>>> producer
  ) throws IOException {
    if (request.sort() == null || request.sort().isEmpty()) {
      throw new IllegalArgumentException("Scrolling requires a sort field");
    }

    // Scroll through the documents
    final AtomicReference<String> scrollIdRef = new AtomicReference<>();
    boolean done = false;
    do {
      final int size = request.size() != null ? request.size() : 100;
      final SearchRequest.Builder copy = new SearchRequest.Builder()
        .size(size)
        .sort(request.sort())
        .index(request.index())
        .scroll(t -> t.time(SCROLL_TIME))
        .query(
          request.query() != null
            ? request.query()
            : Query.of(q -> q.matchAll(m -> m))
        );

      if (request.source() != null) {
        copy.source(request.source());
      }

      final ResponseBody<T> response;
      if (scrollIdRef.get() == null) {
        response = getClient().search(copy.build(), clazz);
      } else {
        response = getClient()
          .scroll(
            ScrollRequest.of(b ->
              b.scrollId(scrollIdRef.get()).scroll(s -> s.time(SCROLL_TIME))
            ),
            clazz
          );
      }

      final List<Hit<T>> batch = response.hits().hits().stream().toList();
      producer.onBatch(batch);
      if (response.hits().hits().size() < size) {
        done = true;
        if (scrollIdRef.get() != null) {
          getClient().clearScroll(b -> b.scrollId(scrollIdRef.get()));
        }
      } else {
        scrollIdRef.set(response.scrollId());
      }
    } while (!done);
  }

  /**
   * Counts the number of documents in the given index
   *
   * @param index The index to count
   * @return The number of documents in the index
   */
  public Long count(final String index) {
    return count(index, Query.of(q -> q.matchAll(m -> m)));
  }

  /**
   * Count the number of documents in the given index that match the given query
   *
   * @param index The index to search
   * @param query The query to execute
   * @return The number of documents that match the query
   */
  public Long count(final String index, final Query query) {
    final SearchResponse<Object> response = search(index, query, Object.class);
    if (response != null) {
      final TotalHits totalHits = response.hits().total();
      if (totalHits != null) {
        return totalHits.value();
      }
    }
    return null;
  }

  /**
   * Delete the given index
   *
   * @param index The index to delete
   * @return The optional delete index response. If the delete index fails,
   */
  public DeleteIndexResponse deleteIndex(final String index) {
    try {
      return client.indices().delete(d -> d.index(index));
    } catch (final IOException e) {
      log.error("Error deleting index {}", index, e);
    }
    return null;
  }

  /**
   * Open the given index
   *
   * @param index The index to open
   * @return The acknowledged response
   */
  public AcknowledgedResponse openIndex(final String index) {
    try {
      return client.indices().open(d -> d.index(index));
    } catch (final IOException e) {
      log.error("Error opening index {}", index, e);
    }
    return null;
  }

  /**
   * Close the given index
   *
   * @param index The index to close
   * @return The acknowledged response
   */
  public AcknowledgedResponse closeIndex(final String index) {
    try {
      return client.indices().close(d -> d.index(index));
    } catch (final IOException e) {
      log.error("Error closing index {}", index, e);
    }
    return null;
  }

  /**
   * Refresh the given index
   *
   * @param index The index to refresh
   */
  public void refresh(final String index) {
    try {
      client.indices().refresh(r -> r.index(index).ignoreUnavailable(true));
    } catch (final IOException e) {
      log.error("Error refreshing index {}", index, e);
    }
  }

  /**
   * Insert the given documents into the given index with the given ids
   *
   * @param index     The index to insert the documents into
   * @param documents the documents to insert
   * @param ids       The ids of the documents to insert, parallel to the documents
   * @return The bulk response
   * @throws IOException              If the bulk insert fails
   * @throws IllegalArgumentException If the number of documents and ids are not the same
   */
  public BulkResponse insert(
    final String index,
    final List documents,
    final List<String> ids
  ) throws IOException {
    if (ids != null && documents.size() != ids.size()) {
      throw new IllegalArgumentException(
        "The number of documents and ids must be the same"
      );
    }

    final List<BulkOperation> bulkOperations = new ArrayList<>();
    for (int i = 0; i < documents.size(); i++) {
      final String id = ids != null ? ids.get(i) : UUID.randomUUID().toString();
      final Object document = documents.get(i);
      bulkOperations.add(
        BulkOperation.of(b ->
          b.index(op -> op.index(index).id(id).document(document))
        )
      );
    }

    log.info(
      "Elasticsearch | Bulk | Inserting {} documents into index {}",
      documents.size(),
      index
    );
    return client.bulk(
      BulkRequest.of(bulkRequest ->
        bulkRequest
          .index(index)
          .operations(bulkOperations)
          .pipeline("full_text_search")
      )
    );
  }

  /**
   * Insert the given documents into the given index
   *
   * @param index
   * @param documents
   * @return
   * @throws IOException
   */
  public BulkResponse insert(final String index, final List documents)
    throws IOException {
    return insert(index, documents, null);
  }

  /**
   * Upsert the given documents into the given index with the given ids
   *
   * @param index     The index to upsert the documents into
   * @param documents The documents to upsert
   * @param ids       The ids of the documents to upsert, parallel to the documents
   * @return The bulk response
   */
  public BulkResponse upsert(
    final String index,
    final List documents,
    final List<String> ids
  ) {
    if (ids != null && documents.size() != ids.size()) {
      throw new IllegalArgumentException(
        "The number of documents and ids must be the same"
      );
    }

    final List<BulkOperation> bulkOperations = new ArrayList<>();
    for (int i = 0; i < documents.size(); i++) {
      final String id = ids != null ? ids.get(i) : UUID.randomUUID().toString();
      final Object document = documents.get(i);
      bulkOperations.add(
        BulkOperation.of(b ->
          b.update(op ->
            op
              .index(index)
              .id(id)
              .action(ua -> ua.doc(document).docAsUpsert(true))
          )
        )
      );
    }

    log.info(
      "Elasticsearch | Bulk | Upserting {} documents into index {}",
      documents.size(),
      index
    );

    BulkResponse bulkResponse = null;
    try {
      // Perform the upsert operation
      bulkResponse = client.bulk(
        BulkRequest.of(bulkRequest ->
          bulkRequest.index(index).operations(bulkOperations)
        )
      );

      // Refresh to ensure documents are searchable immediately
      refresh(index);

      if (ids != null && !ids.isEmpty()) {
        refreshFullTextSearchForDocuments(index, ids);
      }
    } catch (final IOException e) {
      log.error("Error upserting documents", e);
    }
    return bulkResponse;
  }

  /**
   * Helper method to refresh full_text_search for specific documents
   */
  private void refreshFullTextSearchForDocuments(
    final String index,
    final List<String> documentIds
  ) {
    try {
      // Build query to match only the specified document IDs
      final String idsQuery = String.format(
        "{\"ids\": {\"values\": [%s]}}",
        documentIds
          .stream()
          .map(id -> "\"" + id + "\"")
          .collect(Collectors.joining(", "))
      );

      // Use updateByQuery with the full_text_search pipeline
      String taskId = updateByQuery(index, idsQuery, "full_text_search");

      log.info(
        "Started full_text_search refresh for {} documents, task: {}",
        documentIds.size(),
        taskId
      );
    } catch (Exception e) {
      log.error(
        "Failed to refresh full_text_search for upserted documents: {}",
        e.getMessage()
      );
    }
  }

