  /**
   * Find the structure of the given text
   *
   * @param text List of JsonNode objects to find the structure of
   * @return JsonNode with the response from the find_structure endpoint
   */
  public JsonNode findStructure(final List<JsonNode> text) {
    //use the field mapping api to get the structure of the index;
    try {
      //convert jsonnode list to one jsonlines string
      final String jsonLines = text
        .stream()
        .filter(node -> !node.isEmpty())
        .map(JsonNode::toString)
        .collect(Collectors.joining("\n"));

      final HttpHeaders headers = new HttpHeaders();
      headers.setContentType(MediaType.APPLICATION_JSON);
      final HttpEntity<String> entity = new HttpEntity<>(jsonLines, headers);
      final ResponseEntity<JsonNode> response = getRestTemplate()
        .exchange(
          new URI(url + "/_text_structure/find_structure"),
          HttpMethod.POST,
          entity,
          JsonNode.class
        );
      return response.getBody();
    } catch (final Exception e) {
      log.error("Error finding structure", e);
      return null;
    }
  }

