  /**
   * @param sourceIndex          the index to enrich
   * @param sourceField          the field to use for enriching
   * @param includedSourceFields the fields to include in the enrich policy
   * @return the name of the enrich policy
   */
  public String createEnrichPolicy(
    final String sourceIndex,
    final String sourceField,
    final List<String> includedSourceFields
  ) {
    final String enrichPolicyName = UUID.nameUUIDFromBytes(
      (sourceIndex +
        sourceField +
        includedSourceFields
          .stream()
          .sorted()
          .collect(Collectors.joining(" "))).getBytes()
    ).toString();
    if (enrichPolicyExists(enrichPolicyName)) {
      return enrichPolicyName;
    }

    final PutPolicyRequest putPolicyRequest = PutPolicyRequest.of(b ->
      b
        .name(enrichPolicyName)
        .match(mb ->
          mb
            .matchField(sourceField)
            .indices(List.of(sourceIndex))
            .enrichFields(includedSourceFields)
        )
    );
    try {
      client.enrich().putPolicy(putPolicyRequest);
    } catch (final IOException e) {
      log.error("Error creating enrich policy", e);
      return null;
    }
    return enrichPolicyName;
  }

