  /**
   * Execute the enrich policy with the given name. This needs to be done after the policy is created and anytime the data
   * in the source index changes. Use the rest template to execute the policy as the client does not return the task id
   *
   * @param enrichPolicyName the name of the enrich policy to execute
   * @return the task id of the enrich policy execution
   */
  public String executeEnrichPolicy(final String enrichPolicyName) {
    try {
      final HttpHeaders headers = new HttpHeaders();
      headers.setContentType(MediaType.APPLICATION_JSON);
      final HttpEntity<String> entity = new HttpEntity<>(null, headers);
      final ResponseEntity<JsonNode> response = getRestTemplate()
        .exchange(
          new URI(
            url +
            "/_enrich/policy/" +
            enrichPolicyName +
            "/_execute?wait_for_completion=false"
          ),
          HttpMethod.POST,
          entity,
          JsonNode.class
        );
      return response.getBody().at("/task").asText();
    } catch (final Exception e) {
      log.error("Error executing enrich policy", e);
    }
    return null;
  }

  /**
   * Create an enrich pipeline that uses the given enrich policy to enrich the data in the target field
   *
   * @param enrichPolicy    the name of the enrich policy to use
   * @param targetJoinField the field in the index to use for joining the enrichment data
   * @return the name of the enrich pipeline
   */
  public String createEnrichPipeline(
    final String enrichPolicy,
    final String targetJoinField
  ) {
    return createEnrichPipeline(enrichPolicy, targetJoinField, Map.of());
  }

  /**
   * Create an enrich pipeline that uses the given enrich policy to enrich the data in the target field
   *
   * @param enrichPolicy    the name of the enrich policy to use
   * @param targetJoinField the field in the index to use for joining the enrichment data
   * @param renameFields    a map from original to new field names that are written to enrich_temp
   * @return the name of the enrich pipeline
   */
  public String createEnrichPipeline(
    final String enrichPolicy,
    final String targetJoinField,
    final Map<String, String> renameFields
  ) {
    final String enrichPipelineName = UUID.nameUUIDFromBytes(
      (enrichPolicy + targetJoinField).getBytes()
    ).toString();
    if (pipelineExists(enrichPipelineName)) {
      return enrichPipelineName;
    }

    final GetPolicyResponse response = getEnrichPolicy(enrichPolicy);
    if (response == null) {
      log.error("Enrich policy {} does not exist", enrichPolicy);
      return null;
    }

    // Pull out the enrich policy and create the pipeline
    final EnrichSummary summary = response
      .policies()
      .stream()
      .findFirst()
      .orElseThrow();
    final EnrichPolicy policy = summary.config().value();

    final String description = String.format(
      "SELECT %s from <target_index> where %s.%s = <target_index>.%s",
      policy.enrichFields(),
      policy.indices().getFirst(),
      policy.matchField(),
      targetJoinField
    );

    final ObjectNode enrichPolicyNode = mapper.createObjectNode();
    enrichPolicyNode.put("policy_name", enrichPolicy);
    enrichPolicyNode.put("field", targetJoinField);
    enrichPolicyNode.put("target_field", "enrich_temp");
    enrichPolicyNode.put("description", description);

    final ObjectNode enrichPolicyObjectNode = mapper.createObjectNode();
    enrichPolicyObjectNode.set("enrich", enrichPolicyNode);

    final ObjectNode scriptNode = mapper.createObjectNode();
    final String src = Arrays.stream(
      """
      def deduplicate(def arr) {
        return arr.stream().distinct().collect(Collectors.toList());
      }

      def mergeIntoArray(def existing, def newValue) {
          if (existing == null) {
              return newValue;
          }
          if (existing instanceof List) {
              if (newValue instanceof List) {
                  existing.addAll(newValue);
              } else {
                  existing.add(newValue);
              }
          } else {
              if (newValue instanceof List) {
                  newValue.add(0, existing);
                  return newValue;
              } else {
                  return [existing, newValue];
              }
          }
          return existing;
      }
      void mergeObjects(def original, def enrich) {
          if (original == null || enrich == null) {
              return;
          }
          for (def entry : enrich.entrySet()) {
              def key = entry.getKey();
              def newValue = entry.getValue();
              if (original.containsKey(key)) {
                  def existingValue = original[key];
                  if (existingValue instanceof Map && newValue instanceof Map) {
                      mergeObjects(existingValue, newValue);
                  } else {
                      original[key] = deduplicate(mergeIntoArray(existingValue, newValue));
                  }
              } else {
                  original[key] = newValue;
              }
          }
          return;
      }

      // Iterate over the enriched data and merge into the document
      mergeObjects(ctx, ctx.enrich_temp);

      """.split("\n")
    )
      .map(String::trim)
      .filter(line -> !line.startsWith("//"))
      .collect(Collectors.joining(" "));

    scriptNode.put("source", src);

    final ObjectNode scriptObjectNode = mapper.createObjectNode();
    scriptObjectNode.set("script", scriptNode);

    final ObjectNode pipelineRequestNode = mapper.createObjectNode();
    final ArrayNode processorsNode = pipelineRequestNode.putArray("processors");
    processorsNode.add(enrichPolicyObjectNode);

    // Rename the temp fields to the final fields
    for (final Map.Entry<String, String> entry : renameFields.entrySet()) {
      final ObjectNode renameObjectNode = mapper.createObjectNode();
      final ObjectNode renameNode = mapper.createObjectNode();
      renameNode.put("field", "enrich_temp." + entry.getKey());
      renameNode.put("target_field", "enrich_temp." + entry.getValue());
      renameNode.put("ignore_missing", true);
      renameObjectNode.set("rename", renameNode);
      processorsNode.add(renameObjectNode);
    }

    processorsNode.add(scriptObjectNode);

    final String json = pipelineRequestNode.toString();
    final ByteArrayInputStream inputStream = new ByteArrayInputStream(
      json.getBytes(StandardCharsets.UTF_8)
    );

    putPipeline(enrichPipelineName, inputStream);
    return enrichPipelineName;
  }

  /**
   * Update the index with the given pipeline
   *
   * @param index    the index to update
   * @param pipeline the pipeline to use
   * @return the task id of the update
   */
  public String updateByQuery(final String index, final String pipeline) {
    return updateByQuery(index, "{\"match_all\": {}}", pipeline);
  }

  /**
   * Update the enrich pipeline with the given name
   *
   * @param index    the index to update
   * @param query    the query to match a subset of the documents in the index
   * @param pipeline the pipeline to use
   * @return the task id of the update
   */
  public String updateByQuery(
    final String index,
    final String query,
    final String pipeline
  ) {
    try {
      log.info(
        "Updating by query for index {} with pipeline {}",
        index,
        pipeline
      );

      final String jsonPayload = String.format("{\"query\": %s}", query);

      final HttpHeaders headers = new HttpHeaders();
      headers.setContentType(MediaType.APPLICATION_JSON);
      final HttpEntity<String> entity = new HttpEntity<>(jsonPayload, headers);

      // This is executed by the rest client because the Java client appears to have a bug
      // where the response cannot be deserialized
      final ResponseEntity<JsonNode> response = getRestTemplate()
        .exchange(
          new URI(
            url +
            "/" +
            index +
            "/_update_by_query?wait_for_completion=false&conflicts=proceed&pipeline=" +
            pipeline
          ),
          HttpMethod.POST,
          entity,
          JsonNode.class
        );

      // Get the taskId from the response
      if (response.getBody() != null) {
        return response.getBody().at("/task").asText();
      }
    } catch (final Exception e) {
      log.error("Error updating by query", e);
    }
    return null;
  }

  /**
   * Get the task status for the given task id. We search the .tasks index to get running and completed tasks
   *
   * @param taskId the task id to get the status for
   * @return the historical task JSON representation. If the task is not found, an empty JSON object is returned.
   * If the search fails, null is returned
   */
  public JsonNode getHistoricalTask(final String taskId) {
    final Query query = Query.of(b -> b.ids(idb -> idb.values(taskId)));
    final SearchRequest request = SearchRequest.of(b ->
      b.index(".tasks").query(query)
    );
    final SearchResponse<JsonNode> response = search(request, JsonNode.class);
    if (response == null) {
      return null;
    }
    if (
      response.hits() != null &&
      response.hits().hits() != null &&
      !response.hits().hits().isEmpty()
    ) {
      return response.hits().hits().getFirst().source();
    }
    return mapper.createObjectNode();
  }

  /**
   * Get the task status for all tasks in the cluster. Returns up to 1000 running and completed tasks
   *
   * @return the list of task statuses
   */
  public List<JsonNode> getHistoricalTasks() {
    final Query query = Query.of(b -> b.matchAll(m -> m));
    final SearchRequest request = SearchRequest.of(b ->
      b.index(".tasks").query(query).size(1000)
    );
    final SearchResponse<JsonNode> response;
    try {
      response = client.search(request, JsonNode.class);
    } catch (final ElasticsearchException e) {
      // The .tasks index may not exist, so we use the client directly to avoid exception log spamming
      return new ArrayList<>();
    } catch (final IOException e) {
      log.error("Error getting historical tasks", e);
      return new ArrayList<>();
    }
    if (
      response != null &&
      response.hits() != null &&
      response.hits().hits() != null &&
      !response.hits().hits().isEmpty()
    ) {
      return response.hits().hits().stream().map(Hit::source).toList();
    }
    return new ArrayList<>();
  }

  /**
   * Get the running task for the given task id
   *
   * @param id the task id to get the running task for
   * @return the running task JSON representation
   */
  public JsonNode getRunningTask(final String id) {
    // Use the rest client to call the task api
    try {
      final ResponseEntity<JsonNode> response = getRestTemplate()
        .exchange(
          new URI(url + "/_tasks/" + id),
