  /**
   * Get the running tasks in the cluster
   *
   * @return the list of running tasks
   */
  public List<JsonNode> getRunningTasks() {
    try {
      final ResponseEntity<JsonNode> response = getRestTemplate()
        .exchange(
          new URI(url + "/_tasks?detailed=true"),
          HttpMethod.GET,
          null,
          JsonNode.class
        );
      final List<JsonNode> tasks = new ArrayList<>();
      if (response.getBody() != null) {
        response
          .getBody()
          .at("/nodes")
          .forEach(node -> node.at("/tasks").forEach(tasks::add));
      }
      return tasks;
    } catch (final Exception e) {
      log.error("Error getting running tasks", e);
    }
    return null;
  }

  /**
   * Helper function to wait for task completion
   *
   * @param taskId         The task id to wait for
   * @param timeoutSeconds The number of seconds to wait before timing out
   * @return A completable future that completes when the task is done
   */
  public CompletableFuture<Void> waitForTaskCompletion(
    final String taskId,
    final long timeoutSeconds
  ) {
    final CompletableFuture<Void> future = new CompletableFuture<>();
    final long startTime = System.currentTimeMillis();
    new Thread(() -> {
      while (System.currentTimeMillis() - startTime < timeoutSeconds * 1000) {
        final JsonNode taskInfo = getHistoricalTask(taskId);
        // Task is done when it is null as getTaskInfo only reports on running tasks
        if (taskInfo != null && taskInfo.at("/completed").asBoolean()) {
          future.complete(null);
          break;
        }
        try {
          Thread.sleep(500);
        } catch (final InterruptedException e) {
          future.completeExceptionally(e);
        }
      }
    }).start();
    return future;
  }

  /**
   * Helper function to wait for enrich task completion
   *
   * @param taskId         The task id to wait for
   * @param timeoutSeconds The number of seconds to wait before timing out
   * @return A completable future that completes when the task is done
   */
  public CompletableFuture<Void> waitForEnrichTaskCompletion(
    final String taskId,
    final long timeoutSeconds
  ) {
    final CompletableFuture<Void> future = new CompletableFuture<>();
    final long startTime = System.currentTimeMillis();
    new Thread(() -> {
      while (System.currentTimeMillis() - startTime < timeoutSeconds * 1000) {
        final List<ExecutingPolicy> policies = getRunningEnrichStats();
        final Set<String> policyIds = policies
          .stream()
          .map(policy -> policy.task().node() + ":" + policy.task().id())
          .collect(Collectors.toSet());

        if (policyIds.contains(taskId)) {
          try {
            Thread.sleep(500);
          } catch (final InterruptedException e) {
            future.completeExceptionally(e);
          }
        } else {
          future.complete(null);
          break;
        }
      }
    }).start();
    return future;
  }

  /**
   * Get all running and historical tasks
   *
   * @return a list of {@link Task} objects
   */
  public List<Task> getTasks() {
    final List<Task> historicalTasks = getHistoricalTasks()
      .stream()
      .map(source -> {
        final Task task = new Task();
        task.setStartTimeMillis(
          source.at("/task/start_time_in_millis").asLong()
        );
        task.setRunningTimeNanos(
          source.at("/task/running_time_in_nanos").asLong()
        );
        task.setCancellable(source.at("/task/cancellable").asBoolean());
        task.setDescription(source.at("/task/description").asText());
        task.setCompleted(true);
        task.setNodeId(source.at("/task/node").asText());
        task.setTaskId(source.at("/task/id").asLong());

        final JsonNode errorNode = source.at("/error");
        if (errorNode != null && !errorNode.isEmpty()) {
          task.setError(errorNode);
        }

        task.setCancelled(!source.at("/response/canceled").isMissingNode());

        final long total = source.at("/task/status/total").asLong();
        final long created = source.at("/task/status/created").asLong();
        final long updated = source.at("/task/status/updated").asLong();
        final long deleted = source.at("/task/status/deleted").asLong();
        final long operatedOn = Math.max(created, Math.max(updated, deleted));

        final Double progress = (double) operatedOn / total;
        task.setProgress(progress);
        task.setTotal(total);
        task.setCreated(created);
        task.setUpdated(updated);
        task.setDeleted(deleted);

        final String typeStr = source.at("/task/action").asText();
        return switch (TASK_TYPES.get(typeStr)) {
          case REINDEX -> {
            task.setType(REINDEX);
            yield task;
          }
          case DELETE_BY_QUERY -> {
            task.setType(DELETE_BY_QUERY);
            yield task;
          }
          case UPDATE_BY_QUERY -> {
            task.setType(UPDATE_BY_QUERY);
            yield task;
          }
          case null -> null;
        };
      })
      .filter(Objects::nonNull)
      .toList();

    final List<Task> runningTasks = getRunningTasks()
      .stream()
      .map(source -> {
        final Task task = new Task();

        task.setNodeId(source.at("/node").asText());
        task.setTaskId(source.at("/id").asLong());
        task.setStartTimeMillis(source.at("/start_time_in_millis").asLong());
        task.setRunningTimeNanos(source.at("/running_time_in_nanos").asLong());
        task.setCancellable(source.at("/cancellable").asBoolean());
        task.setDescription(source.at("/description").asText());
        task.setCompleted(false);

        task.setTotal(source.at("/status/total").asLong());
        task.setCreated(source.at("/status/created").asLong());
        task.setUpdated(source.at("/status/updated").asLong());
        task.setDeleted(source.at("/status/deleted").asLong());

        final long total = source.at("/status/total").asLong();
        final long operatedOn = Math.max(
          task.getCreated(),
          Math.max(task.getUpdated(), task.getDeleted())
        );
        final Double progress = (double) operatedOn / total;
        task.setProgress(progress);

        final String typeStr = source.at("/action").asText();
        return switch (TASK_TYPES.get(typeStr)) {
          case REINDEX -> {
            task.setType(REINDEX);
            yield task;
          }
          case DELETE_BY_QUERY -> {
            task.setType(DELETE_BY_QUERY);
            yield task;
          }
          case UPDATE_BY_QUERY -> {
            task.setType(UPDATE_BY_QUERY);
            yield task;
          }
          case null -> null;
        };
      })
      .filter(Objects::nonNull)
      .toList();

    final List<Task> tasks = new ArrayList<>();
    tasks.addAll(historicalTasks);
    tasks.addAll(runningTasks);
    return tasks;
  }

  /**
   * Delete all historical tasks
   */
  public void deleteTasks() {
    deleteIndex(".tasks");
    refresh(".tasks");
  }

  /**
   * Delete the task with the given id
   *
   * @param nodeId the node id of the task
   * @param taskId the task id to delete
   */
  public void deleteTask(final String nodeId, final Long taskId) {
    final String id = nodeId + ":" + taskId;
    final DeleteRequest request = DeleteRequest.of(b -> b.id(id).index(".tasks")
    );
    try {
      client.delete(request);
      refresh(".tasks");
    } catch (final IOException e) {
      log.error("Error deleting task", e);
    }
  }

  /**
   * Cancel the task with the given node id and task id
   *
   * @param nodeId the node id of the task
   * @param taskId the task id to cancel
   */
  public void cancelTask(final String nodeId, final Long taskId) {
    final CancelRequest request = CancelRequest.of(b ->
      b.taskId(nodeId + ":" + taskId)
    );
    try {
      client.tasks().cancel(request);
      refresh(".tasks");
    } catch (final IOException e) {
      log.error("Error cancelling task", e);
    }
  }

  public GetMappingResponse getMapping(final String index) {
