  public GetIndexTemplateResponse getIndexTemplate(final String name) {
    try {
      return client
        .indices()
        .getIndexTemplate(GetIndexTemplateRequest.of(b -> b.name(name)));
    } catch (final IOException e) {
      log.error("Error getting index template", e);
