    }
  }

  /**
   * Validates that the given template matches the existing template in the cluster.
   * @param name          the name of the template (index or component)
   * @param templateJson  the template JSON to validate
   * @param type          the type of the template (component or index)
   */
  public void validateTemplate(
    String name,
    JsonNode templateJson,
    final TEMPLATE_TYPE type
  ) {
    final JsonNode existingTemplate = getTemplate(name, type);
    if (existingTemplate == null || existingTemplate.isEmpty()) {
      log.error("Failed to fetch existing {} {}", type, name);
      throw new IllegalStateException(
        "Failed to fetch existing " + type.getName() + " " + name
      );
    }
    final String existingTemplateHash = existingTemplate
      .at("/_meta/hash")
      .asText();
    final String templateJsonHash = hash(templateJson.toString());
    if (!existingTemplateHash.equals(templateJsonHash)) {
      log.error(
        "{}  {} already exists and does not match the expected template.",
        type.getName(),
        name
      );
      throw new IllegalStateException(
        type.getName() +
        " " +
        name +
        " already exists in the cluster and does not match the expected template."
      );
    } else {
      log.info("{} {} already exists and matches", type.getName(), name);
    }
  }

  /**
   * Validates that the given component template matches the existing component template in the cluster.
   * @param name          the name of the component template
   * @param templateJson  the component template JSON to validate
   * @throws IllegalStateException if there is a mismatch
   */
  public void validateComponentTemplate(
    final String name,
    final JsonNode templateJson
  ) {
    validateTemplate(name, templateJson, TEMPLATE_TYPE.COMPONENT);
  }

  /**
   * Validates that the given index template matches the existing index template in the cluster.
   * @param name          the name of the index template
   * @param templateJson  the index template JSON to validate
   * @throws IllegalStateException if there is a mismatch
   */
  public void validateIndexTemplate(
    final String name,
    final JsonNode templateJson
  ) {
    validateTemplate(name, templateJson, TEMPLATE_TYPE.INDEX);
  }

  /**
   * Bulk remove properties from all documents in an index using update by query with a remove processor pipeline
   *
   * @param index the index to update
   * @param propertyMappings list of property mappings to remove from all documents
   * @return the task id of the update operation
   */
  public String bulkRemoveProperties(
    final String index,
    final List<String> propertyMappings
  ) {
    if (propertyMappings == null || propertyMappings.isEmpty()) {
      throw new IllegalArgumentException(
        "Property mappings list cannot be null or empty"
      );
    }

    try {
      // Create a unique pipeline name for this bulk removal operation
      final String pipelineName = "bulk_remove_" + System.currentTimeMillis();

      // Build the pipeline JSON with remove processors for each property
      final ObjectNode pipelineNode = mapper.createObjectNode();
      final ArrayNode processorsArray = pipelineNode.putArray("processors");

      for (final String propertyMapping : propertyMappings) {
        final ObjectNode removeProcessor = mapper.createObjectNode();
        final ObjectNode removeConfig = removeProcessor.putObject("remove");
        removeConfig.put("field", propertyMapping);
        removeConfig.put("ignore_missing", true);
        processorsArray.add(removeProcessor);
      }

      // Create the pipeline
      final String pipelineJson = pipelineNode.toString();
      final ByteArrayInputStream inputStream = new ByteArrayInputStream(
        pipelineJson.getBytes(StandardCharsets.UTF_8)
      );

      final boolean pipelineCreated = putPipeline(pipelineName, inputStream);
      if (!pipelineCreated) {
        throw new RuntimeException("Failed to create removal pipeline");
      }

      log.info(
        "Created bulk removal pipeline {} for properties: {}",
        pipelineName,
        String.join(", ", propertyMappings)
      );

      // Execute update by query with the pipeline
      final String taskId = updateByQuery(index, pipelineName);

      log.info(
        "Started bulk property removal for index {} with task {}",
        index,
        taskId
      );

      return taskId;
    } catch (final Exception e) {
      log.error("Error executing bulk property removal for index {}", index, e);
      throw new RuntimeException("Failed to execute bulk property removal", e);
    }
  }
}

