/**
 * Bulk remove specific property values from selected documents in a project
 *
 * @param project the project containing the documents
 * @param propertyKey the property key to target
 * @param valueToRemove the specific value to remove from the property
 * @param documentIds the list of document IDs to update
 * @return the task ID of the bulk value removal operation
 */
@PrefixedCacheEvict
public String bulkRemovePropertyValuesFromDocuments(
  final Project project,
  final String propertyKey,
  final String valueToRemove,
  final List<String> documentIds
) {
  if (propertyKey == null || propertyKey.isEmpty()) {
    throw new IllegalArgumentException("Property key cannot be null or empty");
  }
  if (valueToRemove == null) {
    throw new IllegalArgumentException("Value to remove cannot be null");
  }
  if (documentIds == null || documentIds.isEmpty()) {
    throw new IllegalArgumentException("Document IDs list cannot be null or empty");
  }

  log.info(
    "Starting bulk property value removal for project {} property '{}' value '{}' in {} documents",
    project.getId(),
    propertyKey,
    valueToRemove,
    documentIds.size()
  );

  try {
    // Validate that the property descriptor exists
    final PropertyDescriptor descriptor = propertyDescriptorService.findByKey(project, propertyKey);
    if (descriptor == null) {
      throw new IllegalArgumentException("Property descriptor not found for key: " + propertyKey);
    }

    // Execute bulk value removal via Elasticsearch
    final String taskId = elasticsearchService.bulkRemovePropertyValues(
      project.getIndexName(),
      propertyKey,
      valueToRemove,
      documentIds
    );

    log.info(
      "Initiated bulk property value removal for project {} with task ID: {}",
      project.getId(),
      taskId
    );

    return taskId;
  } catch (final Exception e) {
    log.error(
      "Failed to execute bulk property value removal for project {} property '{}' value '{}': {}",
      project.getId(),
      propertyKey,
      valueToRemove,
      e.getMessage(),
      e
    );
    throw new RuntimeException("Failed to execute bulk property value removal", e);
  }
}

