  @PrefixedCacheable
  public Map<String, Map<AggregateOperation, Number>> aggregate(
    final Project project,
    final PanteraSearchRequest panteraSearchRequest,
    final String xField,
    final String yField,
    final List<AggregateOperation> operations
  ) {
    final PropertyDescriptor xFieldDescriptor =
      propertyDescriptorService.findByKey(project, xField);
    final PropertyDescriptor yFieldDescriptor =
      propertyDescriptorService.findByKey(project, yField);

    final AggregateTermsType xType = getAggregateTermsType(xFieldDescriptor);
    if (xType == null) {
      throw new IllegalArgumentException(
        "Field %s is not a valid field for aggregation".formatted(xField)
      );
    }

    final AggregateTermsType yType = getAggregateTermsType(yFieldDescriptor);
    if (yType == null) {
      throw new IllegalArgumentException(
        "Field %s is not a valid field for aggregation".formatted(yField)
      );
    }

    final SearchRequest.Builder searchRequestBuilder =
      searchRequestService.builder(project, panteraSearchRequest);
    searchRequestBuilder.aggregations("x", aggregationBuilder -> {
      final Aggregation.Builder.ContainerBuilder builder =
        aggregationBuilder.terms(termsBuilder ->
          termsBuilder.field(xFieldDescriptor.getMapping()).size(1000)
        );

      for (final AggregateOperation operation : operations) {
        if (operation == AggregateOperation.COUNT) {
          continue;
        }

        final Aggregation a = Aggregation.of(subBuilder ->
          switch (operation) {
            case UNIQUE -> subBuilder.cardinality(b ->
              b.field(yFieldDescriptor.getMapping())
            );
            case SUM -> subBuilder.sum(b ->
              b.field(yFieldDescriptor.getMapping())
            );
            case AVERAGE -> subBuilder.avg(b ->
              b.field(yFieldDescriptor.getMapping())
            );
            case MIN -> subBuilder.min(b ->
              b.field(yFieldDescriptor.getMapping())
            );
            case MAX -> subBuilder.max(b ->
              b.field(yFieldDescriptor.getMapping())
            );
            case UPPER_QUARTILE, LOWER_QUARTILE -> subBuilder.percentiles(b ->
              b.field(yFieldDescriptor.getMapping())
            );
            default -> throw new IllegalStateException(
              "Unexpected value: " + operation
            );
          }
        );
        builder.aggregations("y" + operation.toString(), a);
      }

      return builder;
    });

    final SearchResponse<JsonNode> response = elasticsearchService.search(
      searchRequestBuilder.build(),
      JsonNode.class
    );

    return switch (xType) {
      case STRING -> parse(
        response.aggregations().get("x").sterms().buckets().array(),
        operations
      );
      case LONG -> parse(
        response.aggregations().get("x").lterms().buckets().array(),
        operations
      );
      case DOUBLE -> parse(
        response.aggregations().get("x").dterms().buckets().array(),
        operations
      );
    };
  }

  private <T extends TermsBucketBase> Map<
    String,
    Map<AggregateOperation, Number>
  > parse(final List<T> buckets, final List<AggregateOperation> operations) {
    final Map<String, Map<AggregateOperation, Number>> result = new HashMap<>();
    for (final T bucket : buckets) {
      final Map<AggregateOperation, Number> values = new HashMap<>();
      if (operations.contains(AggregateOperation.COUNT)) {
        values.put(AggregateOperation.COUNT, bucket.docCount());
      }
      for (final AggregateOperation operation : operations) {
        values.put(
          operation,
          switch (operation) {
            case SUM -> bucket
              .aggregations()
              .get("y" + operation)
              .sum()
              .value();
            case AVERAGE -> bucket
              .aggregations()
              .get("y" + operation)
              .avg()
              .value();
            case COUNT -> bucket.docCount();
            case UNIQUE -> bucket
              .aggregations()
              .get("y" + operation)
              .cardinality()
              .value();
            case MIN -> bucket
              .aggregations()
              .get("y" + operation)
              .min()
              .value();
            case MAX -> bucket
              .aggregations()
              .get("y" + operation)
              .max()
              .value();
            case UPPER_QUARTILE -> Double.valueOf(
              bucket
                .aggregations()
                .get("y" + operation)
                .tdigestPercentiles()
                .values()
                .keyed()
                .get("75.0")
            );
            case LOWER_QUARTILE -> Double.valueOf(
              bucket
                .aggregations()
                .get("y" + operation)
                .tdigestPercentiles()
                .values()
                .keyed()
                .get("25.0")
            );
          }
        );
      }
      final String key =
        switch (bucket) {
          case final StringTermsBucket stringBucket -> stringBucket
            .key()
            .stringValue();
          case final LongTermsBucket longBucket -> "" + longBucket.key();
          case final DoubleTermsBucket doubleBucket -> "" + doubleBucket.key();
          default -> throw new IllegalArgumentException(
            "Unknown bucket type %s".formatted(bucket.getClass().getName())
          );
        };

      result.put(key, values);
    }
    return result;
  }

  private AggregateTermsType getAggregateTermsType(
    final PropertyDescriptor descriptor
  ) {
    if (descriptor.getMapping().startsWith("long")) {
      return AggregateTermsType.LONG;
    } else if (descriptor.getMapping().startsWith("double")) {
      return AggregateTermsType.DOUBLE;
    } else if (descriptor.getMapping().startsWith("keyword")) {
      return AggregateTermsType.STRING;
    } else {
      throw new IllegalArgumentException(
        "Unknown mapping type %s".formatted(descriptor.getMapping())
      );
    }
  }

  /**
   * Gets the locations for the given project
   * @param project   The project to get the locations for
   * @param request   The search request
   * @param layers    The layers to get the locations for
   * @return          The location response for the location counts in each layer
   */
  @PrefixedCacheable
  public LocationResponse getLocations(
    final Project project,
    final PanteraSearchRequest request,
    final List<String> layers
  ) {
    final String locationFeaturePrefix = "location_feature.";
    request.setSize(0).setTrackScores(false).setTrackTotalHits(true);

    final SearchRequest.Builder builder = searchRequestService.builder(
      project,
      request
    );

    // Add an aggregation for each layer that also includes the exemplar location so we can get the lat/lon for each location
    // and collect unique document IDs for each location
    layers.forEach(layer ->
      builder.aggregations(layer, b ->
        b
          .terms(tb ->
            tb
              .field(layer + ".label")
              .size(1000)
              .missing(AggregateProperty.NOT_AVAILABLE)
          )
          .aggregations("exemplar", eb ->
            eb.topHits(thb ->
              thb
                .size(1)
                .source(s ->
                  s.filter(fb ->
                    fb.includes(List.of(layer + ".location", layer + ".label"))
                  )
                )
            )
          )
          .aggregations(
            "unique_docs",
            ub -> ub.terms(tb -> tb.field("id").size(1000)) // Collect unique document IDs
          )
      )
    );

    final SearchResponse<JsonNode> response = elasticsearchService.search(
      builder.build(),
      JsonNode.class
    );

    // Parse the result
    final Map<String, List<LocationCount>> layerCounts = new HashMap<>();
    layers.forEach(layer -> {
      final List<LocationCount> layerResult = new ArrayList<>();
      final String layerPath =
        "/location_feature/" +
        layer.substring(locationFeaturePrefix.length(), layer.length());
      response
        .aggregations()
        .get(layer)
        .sterms()
        .buckets()
        .array()
        .forEach(bucket -> {
          final String locationKey = bucket.key().stringValue();
          final long count = bucket.docCount(); // Keep original location count

          // Extract unique document IDs from the unique_docs aggregation
          final List<String> docIds = bucket
            .aggregations()
            .get("unique_docs")
            .sterms()
            .buckets()
            .array()
            .stream()
            .map(docBucket -> docBucket.key().stringValue())
            .toList();

          bucket
            .aggregations()
            .get("exemplar")
            .topHits()
            .hits()
            .hits()
            .forEach(hit -> {
              final JsonData source = hit.source();

              if (source != null) {
                final JsonNode hitNode = source.to(JsonNode.class);
                final JsonNode arrayNode = hitNode.at(layerPath);
                if (arrayNode.isArray()) {
                  for (final JsonNode node : arrayNode) {
                    final JsonNode labelNode = node.get("label");
                    if (
                      labelNode != null &&
                      locationKey.equals(labelNode.asText())
                    ) {
                      final Double lon = node.at("/location/lon").asDouble();
                      final Double lat = node.at("/location/lat").asDouble();

                      final LocationCount locationCount =
                        (LocationCount) new LocationCount()
                          .setCount(count)
                          .setDocIds(docIds)
                          .setLocation(new LatLng().setLat(lat).setLng(lon))
                          .setLabel(bucket.key().stringValue());
                      layerResult.add(locationCount);
                    }
                  }
                } else {
                  final LocationCount locationCount =
                    (LocationCount) new LocationCount()
                      .setCount(count)
                      .setDocIds(docIds)
                      .setLocation(null)
                      .setLabel(bucket.key().stringValue());
                  layerResult.add(locationCount);
                }
              }
            });
        });
      layerCounts.put(layer, layerResult);
    });

    return new LocationResponse()
      .setLayers(layerCounts)
      .setTotal(response.hits().total().value());
  }

  /**
   * Gets the Georeferences for the given project
   * @param project   The project to get the locations for
   * @param request   The search request
   * @return          The location response for the location counts in each layer
   */
  @PrefixedCacheable
  public GeoreferencingResponse getGeoreferencings(
    final Project project,
    final PanteraSearchRequest request
  ) {
    final String georeferencingFeaturePrefix =
      LARAGeoreferencedResourceFeature.LARA_GEOREFERENCED_RESOURCE_FEATURE;
    request.setTrackScores(false).setTrackTotalHits(true);

    final SearchRequest.Builder builder = searchRequestService.builder(
      project,
      request
    );

    builder.source(
      SourceConfig.of(s ->
        s.filter(f -> f.includes(georeferencingFeaturePrefix))
      )
    );

    builder.size(1000);

    final SearchResponse<JsonNode> response = elasticsearchService.search(
      builder.build(),
      JsonNode.class
    );

    final List<Hit<JsonNode>> hits = response.hits().hits();
    final LARAGeoreferencedResourceFeatureValidator validator =
      new LARAGeoreferencedResourceFeatureValidator();
    final Map<String, List<Georeferencing>> layersOut = new HashMap<>();
    for (final Hit<JsonNode> hit : hits) {
      if (hit.source() == null) {
        continue;
      }
      final JsonNode source = hit.source().get(georeferencingFeaturePrefix);
      if (source == null) {
        continue;
      }
      for (
        final Iterator<Map.Entry<String, JsonNode>> it = source.fields();
        it.hasNext();
      ) {
        final Map.Entry<String, JsonNode> georefLayer = it.next();
        final ArrayList<JsonNode> georefLayerVals = StreamSupport.stream(
          Spliterators.spliteratorUnknownSize(
            georefLayer.getValue().elements(),
            Spliterator.ORDERED
          ),
          false
        ).collect(Collectors.toCollection(ArrayList::new));
        georefLayerVals.removeIf(val -> val == null || val.isNull());
        final LARAGeoreferencedResourceFeature georefFeature = validator.parse(
          georefLayerVals,
          georefLayer.getKey(),
          null
        );
        layersOut.putIfAbsent(georefLayer.getKey(), new ArrayList<>());
        final var layersOutList = layersOut.get(georefLayer.getKey());
        if (georefFeature.getValue() instanceof List) {
          final var georefs = (List<Georeferencing>) georefFeature.getValue();
          layersOutList.addAll(georefs);
        } else {
          final var georef = (Georeferencing) georefFeature.getValue();
          layersOutList.add(georef);
        }
      }
    }
    return new GeoreferencingResponse().setLayers(layersOut);
  }

  /**
   * Updates the document with the given id in the given project with the new georeferences
   * @param project                   the project to update the document in
   * @param docId                     the id of the document to update
   * @param updatedGeoreferenceLayers the map of the georeference layers with the values as the updated georeference points
   */
  @PrefixedCacheEvict
  public PanteraDocumentUpdateResponse updateGeoreferences(
    final Project project,
    final String docId,
    final Map<String, List<Georeferencing>> updatedGeoreferenceLayers
  ) {
    final ObjectNode objectNode = objectMapper.createObjectNode();

    objectNode.set(
      LARAGeoreferencedResourceFeature.LARA_GEOREFERENCED_RESOURCE_FEATURE,
      objectMapper.convertValue(updatedGeoreferenceLayers, JsonNode.class)
    );

    return updateProjectDocument(project, docId, List.of(objectNode));
  }

  /**
   * Replaces image with map in the markdown content of the document
   * @param project          the project to update the document in
   * @param docId            the id of the document to update
   * @param imageFileName    the image filename to match
   * @param mapId            the map ID to replace with
   * @return the updated document response
   */
  @PrefixedCacheEvict
  public PanteraDocumentUpdateResponse replaceImageWithMapInMarkdown(
    final Project project,
    final String docId,
    final String imageFileName,
    final String mapId
  ) {
    final GetResponse<JsonNode> doc = elasticsearchService.getDocument(
      project.getIndexName(),
      docId,
      JsonNode.class
    );
    final String markdownKey = DefaultProperty.MARKDOWN.getKey();
    final String markdownMapping = propertyDescriptorService
      .findByKey(project, markdownKey)
      .getMapping();
    final String markdownPath = "/" + markdownMapping.replace(".", "/");

    // Extract markdown content as List<String>
    List<String> markdownContent = new ArrayList<>();
    if (doc.found() && doc.source() != null) {
      JsonNode markdownNode = doc.source().at(markdownPath);
      if (markdownNode != null && markdownNode.isArray()) {
        // Convert JsonNode array to List<String>
        markdownContent = StreamSupport.stream(
          markdownNode.spliterator(),
          false
        )
          .map(JsonNode::asText)
          .map(content ->
            MarkdownUtil.replaceImageWithMap(content, imageFileName, mapId)
          )
          .collect(Collectors.toCollection(ArrayList::new));
      }
    }

    final Map<String, List<String>> payload = new HashMap<>();
    payload.put(markdownKey, markdownContent);

    return this.update(project, docId, payload);
  }

  /**
   * Get the available location layers for the given project
   * @param project
   * @return
   */
  @PrefixedCacheable
  public Set<String> getLocationLayers(final Project project) {
    final List<String> locationFieldMappings =
      elasticsearchService.getAllPropertyNames(
        project.getIndexName(),
        "location_feature*"
      );
    return locationFieldMappings
      .stream()
      .map(mapping -> mapping.split("\\.")[1])
      .collect(Collectors.toSet());
  }

